INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (501, '716238', '拉卜楞寺', NULL, '甘肃 > 夏河 > 夏河景点 > 拉卜楞寺', '甘南', '夏河', 'https://img1.qunarzz.com/travel/d7/1701/94/9946aafe0184cb5.jpg;https://img1.qunarzz.com/travel/d2/1608/92/9ea5c69e51af22b5.jpg;https://img1.qunarzz.com/travel/d7/1704/70/8e84fb0194e8b3b5.jpg;https://img1.qunarzz.com/travel/d2/1701/a8/70e31c1751ad61b5.jpg;https://img1.qunarzz.com/travel/d9/1706/c4/d72e21f202c3bb5.jpg;https://img1.qunarzz.com/travel/d1/1706/e8/ecd11cb306c32ab5.jpg', '4.6', '夏河景点排名第1', '1小时 - 4小时', '208', '35.199743', '102.512144', '49', '拉卜楞寺位于夏河县城西，背依风山，面对龙山，地处“金盆养鱼”之地。寺院由第一世嘉木样活佛创建于1710年，经历世嘉木样修建，现已成为甘、青、川地区最大的藏族宗教和文化中心。拉卜楞寺占地1234亩，建筑面积82.3万平方米，拥有经堂6座，佛殿84座，藏式楼31座，佛宫30院，经轮房500余间，僧舍10,000余间，为藏传佛教黄教（格鲁派）六大宗主寺之一。寺院汇集了藏、汉、蒙各族人民的智慧，以精湛的建筑艺术和辉煌的宗教文化而著称。拉卜楞寺内藏有各类经卷6万余册，分全集、哲学、密宗、医药、声明、缀韵、历史、宗教、传记、工巧、数学、诗词12类，成为藏书最多的寺院。拉卜楞寺是甘南旅游的名片，是格鲁派六大寺院之一，也是安多藏区的宗教中心，在藏民心中享有极高的声誉和地位。这里有着全世界最长的7公里转经道，因为《天下无贼》名闻全国。', '甘南藏族自治州夏河县', '0941-7121128', NULL, '寺院区全天开放，闻思大殿区域、贡唐宝塔开放时间约为8:00-18:00，其他佛殿开放时间约为8:00-16:00。', 11435, '每年6月-8月，这时甘南的气温一般保持在15～30℃之间，凉爽宜人，景色也是最美的。', NULL, '1、转经廊请务必顺时针拨动；\n2、拍照前最好征求喇嘛同意；\n3、夏季寺院内设辩经场。', NULL, 'http://travel.qunar.com/p-oi716238-labulengsi', '2', '2', '', '2', '1', '4', 0, 20726, 81, 26641, 41746, NULL, 46698, 5427, 86514, 42664, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (502, '708936', '南少林寺遗址', NULL, '福建 > 莆田 > 莆田景点 > 南少林寺遗址', '莆田', '莆田', 'https://img1.qunarzz.com/travel/d3/1703/a2/6e1e7a5f20cc5eb5.jpg;https://img1.qunarzz.com/travel/d6/1410/28/f749d617c271a9fffffffffc8d65eac.jpg;https://img1.qunarzz.com/travel/d7/1410/28/c1aaf6ecd1ab2bfffffffffc8d65eac.jpg;https://img1.qunarzz.com/travel/d9/1703/e8/c27e093512aa52b5.jpg;https://img1.qunarzz.com/travel/d9/1703/a9/b78c99af0b64c0b5.jpg;https://img1.qunarzz.com/travel/poi/1803/b0/95fd02923c24d937.jpg', '3.6', '莆田景点排名第8', '1小时 - 2小时', '12', '25.5461523701', '119.0466693564', '3', '相传河南嵩山少林寺13武僧帮助唐太宗统一中国后，唐太宗赐于“僧兵”，并准许在全国各地建立十座分寺。莆田县九莲山的林泉院，就是少林寺十座分寺中较早创建的分寺之一。\n唐太宗贞观年间，嵩山少林寺僧南下，不远千里来到九莲山，住锡在林泉院。此后，林泉院易名林山少林寺。北宋时，寺庙更加兴旺，僧人500多名，武风极盛， 因为规模宏大（占地约三万平方米），武风鼎盛，影响南中国，故称之为南少林寺，形成南少林拳，与北少林的腿功一起誉称“北腿南拳”。南宋时，寺院仍保留兴旺趋势。刘克庄也盛赞“邑之嵩山少林”。\n林泉院毁于清初。清初，南少林寺因在反清复明斗争中与天地会有着千丝万缕的联系，清王朝深为忌恨，遣兵焚毁，南少林寺从此湮没于历史烟尘之中。现在南少林寺遗址一带仅存一些附属寺院，如资福寺、红花亭等。1986年至1988年，莆田县在文物普查中，发现了这座湮灭了几百年的寺院遗址，发现了5个刻有“僧兵”、“诸罗汉浴煎茶散”等文字的北宋石槽。\n1998年，南少林寺重建落成。2006年12月，南少林寺正式接纳广化寺方丈管理，成为两寺院共同统一体制。由中国佛教会会长赵朴初先生题写“南少林寺”寺名。', '莆田县西天尾镇九莲山林山村', NULL, 'http://ptnsl.cn/', '8:00-18:00', 7466, NULL, NULL, NULL, '2小时', 'http://travel.qunar.com/p-oi708936-nanshaolinsiyizhi', '2', '4', '', '5', '5', '4', 4, 32000, 165, 32044, 179810, NULL, 335700, 94911, 81968, 41451, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
