INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (508, '7527013', '中越友谊大桥', NULL, '广西 > 东兴 > 东兴景点 > 中越友谊大桥', '防城港', '东兴', 'https://img1.qunarzz.com/travel/poi/1803/92/f04cd3c4d97f7d37.jpg;https://img1.qunarzz.com/travel/d4/1609/e1/b4932b9f454e3b5.jpg;https://img1.qunarzz.com/travel/d8/1511/14/792ad5fb040917f7.jpg;https://img1.qunarzz.com/travel/d8/1511/af/73a4258f4e579cf7.jpg;https://img1.qunarzz.com/travel/d2/1511/f9/e161f2b4c0163f7.jpg;https://img1.qunarzz.com/travel/d7/1511/46/4b317faf648b2f7.jpg', '3.3', '东兴景点排名第5', '0.5小时', '8', '21.5457126033', '107.9766497903', '3', '横跨北仑河的中越友谊大桥架接两国民众的友谊，是两国关系发展的象征和缩影。老一辈东兴人曾留下名言：桥通则兴。在二十世纪三、四十年代，东兴成为中国与东南亚及美、日、英、法诸国的主要通商口岸，中外商贾云集，商贸、金融、黄金贸易十分活跃，素有“小香港”之称。现如今，东兴口岸已成为国家一级口岸；中越友谊大桥也成为一条边境贸易、国际贸易、跨国旅游的大通道，是中国第三大陆路口岸，去年进出境人数逾三百万人次。', '东兴市东兴口岸中越友谊大桥', NULL, NULL, '全天', 16969, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi7527013-zhongyueyouyidaqiao', '1', '4', '2', '1', '1', '5', 0, 32393, 138, 13935, 156515, 10599, 5795, 8652, 103593, 101566, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
