INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (511, '10001683', '桑株岩画', NULL, '新疆 > 皮山 > 皮山景点 > 桑株岩画', '和田', '皮山', 'https://img1.qunarzz.com/travel/poi/1712/5c/c0470df11ed6ab37.jpg', '--', '皮山景点排名第3', '2小时', '0', '37.070892334', '78.2733145479', '0', '岩画长3米多，高1.3米。画面上的形象有各类人像：有人作弯弓射箭状、有人跟随于马后作行走状、有人骑马而行状；有兽像，多为头上有双角的动物，类似大角羊等。画面左上方还有象文字一样的符号，利用硬物在岩石上敲击勾刻而成。岩画附近和河谷中没有找到什么古代遗物，在民间也无传说，一般人认为是原始氏族社会时期的作品，可能是反映远古时代畜牧和狩猎的生活。1962年被定为新疆自治区重点文物保护单位。', '位于皮山县城东南100公里桑株乡乌尔其村的一块岩石上', '0903-6426018（皮山县旅游局）', NULL, NULL, 16503, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi10001683-sangzhuyanhua', '', '5', '5', '', '5', '3', 9, 25303, 82, NULL, 290960, 101680, NULL, 185449, 37801, 21806, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (512, '716093', '卓奥友峰', NULL, '西藏 > 定日 > 定日景点 > 卓奥友峰', '日喀则', '定日', 'https://img1.qunarzz.com/travel/poi/201211/15/a1c56758e29d48caddb12cfb.jpg;https://img1.qunarzz.com/travel/poi/1803/ca/aa896dc527e55637.jpg;https://img1.qunarzz.com/travel/d3/1705/a3/ea0d60f056f60db5.jpg;https://img1.qunarzz.com/travel/d3/1705/2e/678ad9af78e863b5.jpg;https://img1.qunarzz.com/travel/d9/1705/9d/7e998d819bcd4fb5.jpg;https://img1.qunarzz.com/travel/d1/1705/67/3f824c3634ad41b5.jpg', '4.7', '定日景点排名第4', '4小时 - 1天', '10', '28.106378448', '87.1560641734', '12', '位于中尼边界上，山峰以东北山脊和西南山脊为界，北侧在西藏自治区定日县境内，南侧属尼泊尔王国。卓奥友峰还有一个名字，叫“乔乌雅”。其中， “乔”是神的意思，而“乌雅”则是秃头的意思。所以合起来便是 “秃头之神”。为什么这么叫它呢？藏族同胞便说，很久以前，乔乌雅和其他附近的六座高峰本来都是相亲相爱的兄弟姐妹。后来乔马雅偷偷喜欢上了隔岸相对的 “第三女神”――珠穆朗玛。珠穆朗玛也很喜欢他。然而天有不测风云，后来不知道怎么回事，珠穆朗玛却就近嫁给了马鲁卡山。乔乌雅受此打击，一蹶不振，便自己剃光了头发，发誓永不再娶，并把头背对珠穆朗玛向西扭去。于是我们今天看这一群山众，大家都像从星捧月般仰视着珠穆朗玛峰，惟独卓奥友峰切独自把头转向了西边。凄美的爱情故事，表达了当地居民对卓奥友峰的崇敬和喜爱。卓奥友峰每年都会迎来不少的信徒在它的脚下顶礼膜拜。', '西藏自治区日喀则地区定日县', '0891-6835472', NULL, '全天', 2119, '4月底到5月末或9～10月，这段时间是风季过渡到雨季的时节，是进行登山活动的最好时机。', NULL, NULL, NULL, 'http://travel.qunar.com/p-oi716093-zhuoaoyoufeng', '', '5', '', '3', '5', '1', 5, 29057, 190, NULL, 372543, NULL, 91146, 242511, 13241, 5497, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
