INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (516, '702561', '黄帝陵', NULL, '陕西 > 黄陵 > 黄陵景点 > 黄帝陵', '延安', '黄陵', 'https://img1.qunarzz.com/travel/d7/1605/80/3f7bd9d5140acaf7.jpg;https://img1.qunarzz.com/travel/d3/1605/97/ca457187565815f7.jpg;https://img1.qunarzz.com/travel/d9/1605/3e/155ca9982b864af7.jpg;https://img1.qunarzz.com/travel/d2/1612/8c/eedea2aed4d372b5.jpg;https://img1.qunarzz.com/travel/d5/1612/51/dc53243c64dfefb5.jpg;https://img1.qunarzz.com/travel/d4/1612/64/f17fb4d2f4a26fb5.jpg', '4', '黄陵景点排名第1', '1.5小时 - 2小时', '61', '35.5881968268', '109.2798957954', '4', '中华民族的始祖轩辕黄帝的陵墓，被称为中华第一陵，是民族圣地。千百年来，每逢清明，来此拜谒祭陵的人络绎不绝。一共划分为东湾、西湾、马山、印台山、黄花沟、桥山镇6个景区。桥山现有古柏8万多株，其中千年以上古柏3多万株，为我国面积最大的古柏林。自唐大历五年（770年）建庙祀典以来，这里一直是历代王朝举行国家大祭的场所。', '延安市黄陵县城东关', '0911-5216510', 'http://www.hdlinfo.com/', '8:00-17:00', 2085, '春秋两季。', NULL, NULL, '1.5小时', 'http://travel.qunar.com/p-oi702561-huangdiling', '', '5', '', '2', '', '1', 8, 22706, 74, NULL, 269838, NULL, 18065, NULL, 15814, 5757, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (517, '707170', '当惹雍错', NULL, '西藏 > 尼玛 > 尼玛景点 > 当惹雍错', '那曲', '尼玛', 'https://img1.qunarzz.com/travel/poi/1803/f7/217e8b739851d237.jpg;https://img1.qunarzz.com/travel/d5/1704/e7/9484192c8714ebb5.jpg;https://img1.qunarzz.com/travel/d3/1704/80/a87649b1438f28b5.jpg;https://img1.qunarzz.com/travel/d1/1704/46/6b6e1f60176bc8b5.jpg;https://img1.qunarzz.com/travel/d7/1704/da/1a0c83448fb8c3b5.jpg;https://img1.qunarzz.com/travel/d5/1704/92/99119a02ab6329b5.jpg', '5', '尼玛景点排名第1', '1小时', '28', '30.913915', '86.536673', '9', '当惹雍错是苯教徒心目中的神湖。\n从尼玛县先向西南，大约8千米后转入向左的岔口，再向南约80千米，翻过一个5000多米的山口，便可以进入当惹雍错湖盆。\n当惹雍错位于一个深陷的湖盆底部，它与当惹雍错相毗邻。实际上当穷错与当惹雍错是在同一个湖盆之内，湖水未枯以前本是同一湖泊。这正是当穷错名字的来历，藏语里当穷错即小的当惹雍错之意。据说此湖在一天之中能变换三种颜色。湖东岸是连绵不断、屏风般矗立的褚红色山壁，它们清晰地记录了当惹雍错湖水一次次下降的历史。被湖水冲刷而成的阶梯从湖畔山顶一圈又一圈地一直环绕到湖滨。\n从当穷错继续前行5千米左右就可以看到藏北最著名的神湖：当惹雍错。\n当惹雍错为南北走向，形如鞋底的大湖泊，三面环山。唯南岸达尔果山东侧有一缺口。达尔果山一列七峰，山体黝黑，顶覆白雪，形状酷似7座整齐排列的金字塔。它和当惹雍错一起被苯教徒奉为神的圣地。湖边的玉本寺是一座建于悬崖山洞的寺庙，据说为苯教最古老的寺庙。\n当惹雍错湖滨有一近百余户人家的小村落－文部乡。村里人多数半农半牧，在湖滨开了一些田地，种植青稞、土豆、油菜和小白菜等。在海拔4500余米的地方，居然还能种植这些作物，着实令人惊奇。这要归功于当地的湖区气候。', '藏北文部二村、象雄王国遗址旁边', NULL, NULL, '全天', 15416, NULL, NULL, NULL, '半天', 'http://travel.qunar.com/p-oi707170-dangreyongcuo', '4', '2', '3', '4', '4', '2', 4, 32454, 93, 172857, 49735, 27450, 154966, 197385, 16248, 11385, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
