INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (529, '714741', '沂蒙山旅游区沂山景区', NULL, '山东 > 临朐 > 临朐景点 > 沂蒙山旅游区沂山景区', '潍坊', '临朐', 'https://img1.qunarzz.com/travel/d9/1605/cd/ad367b61bfac47f7.jpg;https://img1.qunarzz.com/travel/d9/1605/49/2cc0476cff69dbf7.jpg;https://img1.qunarzz.com/travel/d7/1605/25/5cf7556830b6baf7.jpg;https://img1.qunarzz.com/travel/d8/1605/d0/0ba08ab12e7298f7.jpg;https://img1.qunarzz.com/travel/d2/1610/5d/d6f1e40f785ad7b5.jpg;https://img1.qunarzz.com/travel/d2/1610/8e/eff3c57477931fb5.jpg', '4.8', '临朐景点排名第2', NULL, '15', '36.20906', '118.717122', '2', '沂山古称“海岱”“海岳”，又有“东泰山”“小泰山”之称。居中国五大镇山之首，在山东省临朐县境内，属国家5A景区、国家级森林公园、省级风景名胜区。沂山山体蜿蜒，气势磅礴，奥谷深幽，钟灵毓秀，具有南险、北奇、东秀、西幽之特点，为汶、弥、沂、沭四水发源地。古齐长城绵延山巅。其峰峭、石奇、谷翠、水秀、古刹、“松涛”“云霭”相映成趣。总面积65平方公里，最高海拔1032米，被誉为“鲁中仙山”。\n沂山是一座历史悠久的文化名山《史记》载，皇帝曾登封东泰山。隋、唐、宋、元、明、清历代皇帝屡有增封，祀典不废。自汉武帝始，历代立御碑16幢。李白、郦道元、欧阳修、苏轼以及明状元赵秉忠，清朝体仁阁大学士刘墉等均至此揽胜。\n沂山森林覆盖率高达98.6%，中药材214科，815种，其中植物药144科，700余种；动物药70科，104种；矿物药11种。主要景观有：玉皇顶、歪头崮、百丈崖瀑布等。主要人文景观有：法云寺、东镇庙、黑风口（《水浒传》中李逵力杀四虎即措此处）等景观、景点86余处。', '山东省潍坊市潍坊市临朐县沂山镇镇址南300米沂山国家森林公园', '4000536059', 'http://www.ymslyq.cn', '全天开放', 6263, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi714741-yimengshanlu:youquyishan', '2', '', '1', '1', '2', '1', 4, 29116, 95, 45360, NULL, 23725, 21283, 18123, 22454, 6464, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
