INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (532, '718987', '八泉峡', NULL, '山西 > 壶关 > 壶关景点 > 八泉峡', '长治', '壶关', NULL, '--', '壶关景点排名第4', NULL, '0', '35.9262320862', '113.5845676237', NULL, '八泉峡也是龙泉峡的一条支峡，八泉峡呈南北走向，全长13公里，宽处20米，窄处3米，落差300余米，其中点缀着三溶洞、一线天、九栈道、八道水等4个景点。八泉峡北口悬崖绝壁周围有八个泉眼，常年流水不息，喷泉、瀑布直跌谷底，汇成八道水流出，故得名八泉峡。 游客走进八泉峡，踩着八道水中的石头逆流而上，映入眼帘的是积满山谷的河水、高耸入云的悬崖绝壁。往里走数百米，峡谷两侧悬崖绝壁合拢成两个孤形，围出圆形天井，（天井口小腹大嵌在东侧悬崖绝壁上）1，它是5亿年前至4亿年前形成的天然石灰岩溶洞。龙洞口呈椭圆形，好似龙口，平均直径4米，距谷底高度30米，从洞中流出的水旱季恰如雨滴，雨季喷薄如柱，故称龙洞。攀上悬崖绝壁，进入狭小的龙洞口，发现里面洞窟宽大，窟顶泉水淅沥，地上石笋林立，洞深莫测。 从龙洞出来，前行数百米，便是三溶洞中的第二洞――朱砂洞。朱砂洞也位于悬崖峭壁上，是5亿年前至4亿年前形成的天然石灰岩溶洞。朱砂洞口为椭圆形，平均直径3米，距谷底高度50米，潺潺清流，倾泻到洞外。沿小径攀援而上，拨开洞口前生长的灌木杂草，进入洞内，只见洞窟高低不平，宽窄不一，深浅不测。据当地民间传说：溶洞里原来蕴藏着丰富的朱砂矿藏，把流出来的水都染红了，因此人们称其为朱砂洞。206年（汉建安十一年）冬，曹操率大军进攻壶关，高干粮尽援绝，只好单人匹马逃出关城。守关高军士兵树倒猢狲散，各自逃命，其中一个士兵跑到八泉峡朱砂洞中，发现了洞里的朱砂矿藏。他欣喜若狂，抽出宝剑，拼命挖掘朱砂，打算运出去卖掉，发一大笔横财。不料想竟然从朱砂里挖出石碑，上面刻着“贪财者必自毙”六个大字。高军士兵吓坏了，慌忙扔下宝剑，逃离朱砂洞。经他胡挖乱掘，洞内朱砂矿产资源遭到破坏，流出的水越来越清，最终成为今天的样子。 继续前行，抬头仰望，只见两侧悬崖峭壁间距离越来越近，把辽阔的天际拥挤成一缕线条，这就是一线天。一线天总长度达10公里，随峰回路转，弯曲绵延，宽窄突变，使游客备感新奇刺激。一线天地段峡谷间距急速收窄，水流受到约束，越涨越高，越流越急，已漫过水中的石头。游客只能卷起裤腿，淌着深没膝盖的河水前行，不久河水已浸至游客腰部，寒气彻骨。正当苦无桥梁、舟楫时，建在悬崖峭壁上的栈道呈现在游客面前。栈道有的地段是在岩石上凿出蹬脚、把手，供人攀援；有的地段是用藤蔓当荡索，供人摆渡；还有的地段是用岩石堆成阶梯，供人上下，总共有九个不同的地段，故名九栈道。登上九栈道，需要格外小心谨慎，务必要踩牢脚蹬，抓紧把手，荡好藤索，登稳阶梯，否则定会落入水中，狼狈不堪。在栈道上行进途中，偶尔停止行动，俯视脚下急流，发现栈道距水面不高，河中岩石半浸水中，被水冲刷成各种形状，河边悬崖峭壁底部岩石被水冲刷成凹凸不平的旋涡形状，大旋涡共有3个，每个高9米，直径4米。 沿栈道到达八道水发源处，峡谷变得宽阔平坦，东侧悬崖绝壁上，镶嵌着5亿年前至4亿年前形成的天然石灰岩溶洞，这就是三溶洞中的第三洞――黑龙洞。黑龙洞又名黑风洞，洞口呈椭圆形，平均直径4米，距谷底高度50米。从洞中流出的水飞泄在谷底，常年不停。进入狭小的洞口，发现里面洞窟广大，不知深浅，时有阴风迎面扑来。据当地民间传说：黑龙洞里面栖息着一条黑龙，神通广大，法力无边，动则生风，静则喷水。它与黑龙潭中的黑龙本是亲兄弟，都是东海龙王的儿子，共同保佑地方风调雨顺。 八道水源泉其实不止8个，数目很多，有的源泉是悬崖峭壁上的天然石灰岩溶洞，高20余米，瀑布飞流直下，声响如雷；有的源泉是谷底平地上的天然石灰岩溶洞，山泉喷涌而出。还有的源泉是埋藏在草丛中的天然石灰岩溶洞，泉水缓缓渗出，潺潺流淌。这些源泉交叉喷射，最后汇成八道水，八道水又名淅水，沿八泉峡向南流去，直到八泉峡南口与郊沟河汇合。1938年，抗日民主政府利用八道水发源处有利地形，设立了八路军兵工厂，造出大量枪弹支援前线。现兵工厂已改为提水站，成为县级文物保护单位。', NULL, NULL, NULL, NULL, 4222, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi718987-baquanxia', '', '', '', '', '1', '4', 5, 27387, 82, NULL, NULL, NULL, NULL, 24301, 108523, 50482, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (533, '716184', '尼雅遗址', NULL, '新疆 > 民丰 > 民丰景点 > 尼雅遗址', '和田', '民丰', 'https://img1.qunarzz.com/travel/d9/1702/47/58cc025fdb8960b5.jpg;https://img1.qunarzz.com/travel/poi/1803/d7/71997e289d91f037.jpg;https://img1.qunarzz.com/travel/d5/1708/ce/6b01b9f83c341eb5.jpg;https://img1.qunarzz.com/travel/d9/1702/b9/5d22cbf264ebcdb5.jpg;https://img1.qunarzz.com/travel/d7/1702/68/09917496854b3fb5.jpg;https://img1.qunarzz.com/travel/d8/1702/31/3dd00f1503c78fb5.jpg', '5', '民丰景点排名第1', '3天', '2', '37.069591', '82.69943', '0', '尼雅遗址是汉晋时期精绝国故址，以佛塔为中心，散布于南北长25公里，东西宽5-7公里的区域内。在这片狭长的古城遗址中，残存的佛寺、房舍、墓地散落其间，当年的农田、林带、渠系和冶炼遗址依稀可见。可以看出，当年精绝国的居民们以穿城而过的尼雅河为界，分居南北两区，南为豪门，北住平民。但是，家家皆有果园、畜圈和池塘。遗址中心有一土筑佛塔，虽然破败，但古风犹存。\n精绝国处于塔克拉玛干大沙漠腹地，居弱水一方，是个小国。据《汉书·西域传》记载：这里仅“户四百八十、口三千三百六十、胜兵五百人”，并在东汉年间“被鄯善并吞，国遂绝”，但是，丝路南道“往来者莫不由此城焉”。奔走穿梭于尼雅河桥头的丝路商人给小国寡民带来了西亚的玻璃器皿、希腊风格的艺术品、印度的棉织物、黄河流域的锦绢、漆器、铜镜和纸这些当世珍宝。\n汉晋夫妻合葬墓中出土的蓝底蜡染棉布残片和棉布裤，是我国迄今所见的最早棉织物，尤其是“五星出东方利中国”文字织锦堪称锦中绝品、丝路遗珍。佉卢文书是尼雅考古中最重要的发现，800件出土于尼雅遗址的佉卢文书木简，有文告、信函、指示，内容几乎涉及当时精绝王国生活的每一个侧面。\n尼雅的废弃是个谜。古城遗址周围不见河流改道或战乱兵燹的迹象，房屋院落之中没有人迹；大量公文简牍刚刚整理好，还来不及运走；甚至连忠实的狗儿都忘了带走，还拴在家门口的柱子上。尼雅人就这样匆匆忙忙地消失了，只留下沙埋废墟，更留下瀚海荒漠中难解之谜。', '和田地区民丰县正北约150公里处的沙漠中', '0903--2515806', NULL, '全天开放', 13138, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi716184-niyayizhi', '', '', '', '1', '4', '4', 0, 20259, 208, NULL, NULL, NULL, 13151, 84310, 191438, 46166, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (534, '7469603', '张掖平山湖大峡谷', NULL, '甘肃 > 张掖 > 张掖景点 > 张掖平山湖大峡谷', '张掖', '张掖', 'https://img1.qunarzz.com/travel/d8/1709/45/8cb699c3dc950bb5.jpg;https://img1.qunarzz.com/travel/d9/1701/43/8ab5376602f422b5.jpg;https://img1.qunarzz.com/travel/d3/1701/2f/3e98ab17349bceb5.jpg;https://img1.qunarzz.com/travel/d5/1701/e5/b58c2372fb7ea2b5.jpg;https://img1.qunarzz.com/travel/d2/1701/6d/1d9a8557a1c18ab5.jpg;https://img1.qunarzz.com/travel/d6/1701/ce/6254606093613cb5.jpg', '4.8', '张掖景点排名第6', NULL, '49', '39.080997', '100.814073', '24', '亿万年风雨沧桑，大自然神奇造化。这里峡谷幽深、峰林奇特，大自然用鬼斧神工的创造力将五彩斑斓的山体镌刻成一幅幅无与伦比、摄人心魄的山水画卷。\n这里，就是被《中国地理杂志》及中外知名地质专家和游客誉为“比肩张家界”、“媲美克罗拉多大峡谷”、丝路之路新发现，具有世界垄断性旅游资源——位于中国西北甘肃省张掖平山湖大峡谷！\n平山湖大峡谷距市区仅56公里，是中国张掖地貌景观大观园中最美的景观之一！峡谷内，山倚壁蜿蜒，峰随地势峥嵘，石林犬崖交错，仿佛海市蜃楼，又如仙山琼阁。步入迷宫一样的峡谷，置身于纵横交错的沟壑，成林的群峰，犹如世界建筑大师巧夺天工之作，让人目不暇接：林立的楼群，雄奇的宫殿，人鸟兽塔，惟妙惟肖，面积之大，观赏性之强，世界罕见。景区以大峡谷、石林奇观、戈壁雪山、原始深林、自驾越野、运动攀岩、民族风情为一体，集科学价值和美学价值于一身，现已成为科普、生态、探险旅游的新亮点，必将吸引世界的目光。\n走进平山湖，探秘大峡谷——张掖平山湖大峡谷，期待勇者征服！\n张掖平山湖大峡谷——中国丝绸之路新地标！', '甘肃省张掖市甘州区城北56公里处', '0936-8888982;0936-8888983', NULL, '\n淡季（11月1日-4月30日）：7:00-19:00；旺季（5月1日-10月31日）：5:00-22:00', 12523, '\n{5月,6月,7月,8月,9月,10月}较为适宜。', NULL, NULL, NULL, 'http://travel.qunar.com/p-oi7469603-zhangyepingshanhudaxia', '', '1', '', '', '5', '3', 4, 29132, 134, NULL, 20084, NULL, NULL, 153507, 81148, 22123, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (535, '7469278', '虹梯关通天峡景区', NULL, '山西 > 长治 > 长治景点 > 虹梯关通天峡景区', '长治', '长治', 'https://img1.qunarzz.com/travel/d1/1605/e8/2742a150470e00f7.jpg;https://img1.qunarzz.com/travel/d1/1605/42/b8027990a47031f7.jpg;https://img1.qunarzz.com/travel/d1/1605/a2/071f4d92d35685f7.jpg;https://img1.qunarzz.com/travel/d5/1605/fc/ae691bf13a6350f7.jpg;https://img1.qunarzz.com/travel/d2/1605/f5/c3ef65c503e5e0f7.jpg;https://img1.qunarzz.com/travel/d2/1605/b2/bb61403c4569e3f7.jpg', '5', '长治景点排名第1', '2小时 - 4小时', '1', '36.2239204228', '113.6200691379', '0', '山西通天峡景区位于太行山南端以东，山西省平顺县城东30公里处，横跨山西、河南两省交界，主景区位于平顺县虹梯关乡境内，规划区涉及虹梯关乡、东寺头乡所属的18个行政村。东至库峧村，西 至虹梯关，北至西井山，南至东寺头，总占地面积约65平方公里，由山东烟台塔山企业集团股份有限公司斥资5.2亿元、历时四年精心打造而成。通天峡景区以贯通虹梯关与东寺头的大峡谷为轴线，集雄、奇、险、秀于一身，融历史文化与自然风光为一体，拥有“大峡谷、仙人峰、虹霓瀑布及虹梯古关”四大特色区域。主峡谷长约26公里，山 高海拔1600多米，堪称北方小九寨。这里奇峰林立、巍峨厚重；太极石阵、造化天成；峡谷万丈、壁立千仞；飞瀑流霞、如银似练；林秀潭幽、鸟语花香；红叶漫山、药材遍地，山水胜景美到极致，被誉为“三山五岳汇太行，北雄南秀 看平顺”。通天大峡谷堪称北方小九寨，在刀削斧劈的悬崖谷底，千姿百态的山石，如练似银的瀑布，碧波荡漾的深潭 ，四季分明的景色，无不让游人流连忘返。春天这里嫩芽点绿、山花烂漫，悬崖上尚未消融的冰挂映衬着童话般的世界；夏天这里绿荫围湖、莺飞燕舞，流水梳理着翠绿的树枝与碎石；秋天红叶铺山、彩林满目，如练似银的飞瀑弹奏着沁人心 脾的乐章；冬天雪裹山峦、冰瀑如玉，蓝色湖面的冰层充满了诗情画意。', '山西长治平顺县城东30公里处', '0355-8882999', 'http://www.ttxly.com/index.php', '8:30-17:00', 2701, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi7469278-hongtiguantongtianxia', '1', '3', '', '5', '3', '2', 0, 34886, 139, 24790, 87503, NULL, 104803, 82251, 17168, 13809, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
