INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (540, '10010849', '泗水亭公园', 'si shui ting gong yuan', '江苏 > 沛县 > 沛县景点 > 泗水亭公园', '徐州', '沛县', 'https://img1.qunarzz.com/travel/poi/1712/fd/c7523f8c34153c37.jpg', '--', '沛县景点排名第3', NULL, '0', '34.7403030396', '116.9301037949', '0', '位于江苏省沛县境内，公园因有泗水亭和碑而得名。泗水亭为汉高祖刘邦“试为吏”时的遗迹。乾隆版《沛县志》谓“汉高祖为泗水亭长，即此亭也”。沛人为纪念刘邦任过泗水亭长，故建亭作纪念。《水经注》载：“县治南垞上，东岸有泗水亭”。《地道记》称“沛县泗水亭，亭有高祖碑，班固为文。”可见亭为汉代所建，亭中立碑，仍用班固原文。明代诗人王玙《宿沛县泗亭驿》诗有句“千古龙飞地，令人感废兴”。园内还有沛县的另一著名古迹琉璃井，又称作八宝琉璃井。井石栏内有数不清的勒痕，传说为秦始皇所凿。《史记》载：“秦始皇二十八年东游，为压天子气，凿井浚沟，以断王脉。”', '徐州市沛县东风西路', '137****8189', NULL, '8:00-18:00', 2097, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi10010849-sishuitinggongyuan', '5', '', '1', '', '3', '1', 7, 44721, 106, 104267, NULL, 16076, NULL, 87812, 17124, 5187, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
