INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (544, '715790', '月亮山', NULL, '宁夏 > 海原 > 海原景点 > 月亮山', '中卫', '海原', NULL, '0', '海原景点排名第1', NULL, '1', '36.2013675491', '105.6304949144', NULL, '月亮山是西吉县境内最高的山脉，位于县城西北部西吉、海原两县交界处，白城乡东北。属屈吴山余脉，因其形状近似弧月而得名。主峰海拔2633米，上下山路程约七华里，西麓有宝河沟，故又名七里宝山。月亮山东南接六盘山余脉--西峰岭，西北与西华山接界，呈西北--东南走势，长约40公里，宽约20公里，山体呈窄条鱼脊状，两侧坡度较缓，一般在40-60度之间。其植被多为草本科，有无芒雀麦草、垂穗披硷草、鹅冠草、硬质早熟禾、黄花野苜蓿等世界稀有或濒临绝种的优质牧草，为西吉、海原两县主要的牧场。南麓胡家垴为葫芦河发源地。中卫--静宁公路盘山而过。月亮山西南麓有三滴水新石器时代文化遗址。遗址暴露范围东西宽500米，南北长2000米，分布在北庄东西两面山坡及三滴水小学操场内。文化层1-2米，墨、黄、红三种土层交替覆盖。大量陶片散布于地表和文化层中。陶片为夹砂、泥质，纹饰为绳纹、印纹等，出土较完整的红陶罐、瓶各一个。1984年7月在学校基建打墙时出土石斧、灰陶罐各一件，并发现长13米、厚30厘米的灰土层，夹杂大量陶片。此地已被列为县级文物保护单位。', '宁夏固原西吉县', NULL, NULL, NULL, 18592, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi715790-yueliangshan', '', '', '5', '', '', '3', 1, 39652, 156, NULL, NULL, 335078, NULL, NULL, 87484, 25543, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (545, '10032609', '永不沉没的泰坦尼克', NULL, '四川 > 大英 > 大英景点 > 永不沉没的泰坦尼克', '遂宁', '大英', 'https://img1.qunarzz.com/travel/poi/1712/a7/d157600c46c11237.jpg', '--', '大英景点排名第2', NULL, '0', '30.611246', '105.250393', '0', '永不沉没的泰坦尼克以还原历史为唯一立足，完全按照100年前泰坦尼克号游轮的实际尺寸1:1原型建造，这里将让您享受超五星级的游轮服务！这里将是一座超大的梦幻舞台，繁华与显赫交织的世界名流场。由好莱坞团队全程打造，这将TITANIC打造成为一座24小时狂欢不夜城。荣登泰坦尼克，能体验什么？\n完美复原，真实再现电影场景\n挽着心爱的TA，走下金色台阶：最奢华的头等舱大楼梯，配有橡木镶板以及镀金栏杆。顶部是玻璃穹顶，使自然光洒满大楼梯。完美还原电影场景，放佛置身卡梅隆的镜头中。\n经典船头桥段，留下爱的纪念：这一幕爱情留影，是多少人魂牵梦绕的画面。将在船头设置留影区，你可以像JACK和ROSE一样，面朝爱情，春暖花开。\nROSE房间重现，完成爱的勾勒：ROSE躺过的沙发，JACK作画的软椅。\n优雅的贵族聚会，精致从舌尖绽放：还原的TITANIC上，阳光充裕的巴黎咖啡馆成为名流聚会、圈层交流的私享之地。\n特色还原，经典重现，多彩体验：\n泰坦尼克角色扮演、晚宴、梦幻演出、撞冰山体验、经典影院、记忆绘画馆、万国小吃汇、浪漫假面舞会、Open air泳池派对、拉斯维加斯竞技、甲板音乐会还有特色健身、沐浴中心，趣味丰富，数不胜数。\n在这里，还能亲密接触近代文明，感受100年前的科技。\n登上永不沉没的泰坦尼克，体验一百年前游轮生活，品味触动人心的奢华盛宴，感受永不沉没的人性光辉！', '遂宁市大英县浪漫地中海七星国际文化旅游度假区内', NULL, NULL, '16:00-次日10:00', 1873, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi10032609-yongbuchenmeidetaitan', '', '3', '2', '3', '4', '2', 2, 46899, 104, NULL, 75327, 43229, 25233, 131944, 25495, 13110, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
