INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (552, '10015115', '太白山', NULL, '甘肃 > 渭源 > 渭源景点 > 太白山', '定西', '渭源', 'https://img1.qunarzz.com/travel/poi/1712/b6/80a444c5ab7b4a37.jpg', '--', '渭源景点排名第4', NULL, '0', '35.009007', '104.04163', '0', '位于渭源县城西南25公里的杨庄乡，因传说是太白金星修道的仙山而得名。山形险绝，酷似华山，俗称“小华山”。太白山一峰独耸，系云摩天，众山环拱，与露骨山遥相呼应。山势“海拔三千三，离天三尺三，不知路多远，八十一转湾”。 游人来至山前，往往先到接引殿，以请接引菩萨“通报”玉皇大帝及各路神仙，再沿右侧的大干沟或左侧的小干沟拾级登山。干沟并不干，沿沟到处溪流淙淙。从山下仰望，殿宇呈瑞，茂林修竹，悬崖峭壁上飞濂挂瀑。太白山在渭源县城西南25公里的会川镇，海拔3300米。因山势险峻酷似华山，有人称它为小华山。传说是太白金星修道的仙山。顶峰穿云摩天，郁郁葱葱。从山顶向下俯瞰，只见云海翻腾，众山环拱，使人大有“会当凌绝顶，一览众山小”的感觉。山上唐代已有庙宇，清道光年间又重建。原建筑毁于60年代，现在乡民又建有太白殿、三清殿、三霄殿、雪山太子殿、玉皇阁、雷祖殿、山神庙等。每年农历六月初六，山上举行庙会，热闹非凡。', '定西市渭源县会川镇半阴坡', NULL, NULL, '9:00-17:00', 20204, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi10015115-taibaishan', '4', '', '5', '4', '3', '2', 8, 43885, 193, 66922, NULL, 85059, 149052, 28168, 39893, 10820, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (553, '702172', '菜溪岩', NULL, '福建 > 仙游 > 仙游景点 > 菜溪岩', '莆田', '仙游', 'https://img1.qunarzz.com/travel/poi/1803/a5/858a124d659b8e37.jpg;https://img1.qunarzz.com/travel/d6/1504/d4/5c8ae6ec15c982.jpg', '3', '仙游景点排名第1', '1小时', '4', '25.584431683', '118.6694337101', '1', '菜溪岩传说有一百多景。山中有一座古老寺院，寺始建于唐，几经兴废，现存清代重建的大雄宝殿、九仙宫、观音殿和宁代舍利塔。\n寺院附近有两块高达百米、宽近三百米的巨石对峙，中开一门，即为石门。石门的北面峭壁如屏，每逢降雨后，山上流水从这里倾泻，形成壮观的瀑布，直泻龙潭。\n龙潭东侧，一块高数十米的巨石巍然矗立，俗称“飞来石”。据说是明万历年间从山巅滚下来的。飞来石下，另有一平卧巨石，上刻“眠云石”三字。站在石上西眺，只见狮子峰峻峭逼人。峰上的石笋、石凉伞形象逼真，亭亭玉立。\n眠云石下是“幻游洞”，洞前小湖边，古松垂荫，藤蔓翳然。菜溪岩的右侧还有石鼓、石钟、石狮、石象、罗汉洞诸景。罗汉洞幽深宽敞，传说昔时九鲤湖何氏九仙常到此游玩。', '莆田市仙游县城北53公里象溪乡境内', '0594-8661633', NULL, '8:00-17:00', 8243, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi702172-caixiyan', '', '4', '4', '', '2', '2', 3, 29131, 166, NULL, 185906, 94310, NULL, 11796, 25640, 12808, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (554, '10016690', '凤凰城引龙池温泉体验馆', NULL, '黑龙江 > 绥化 > 绥化景点 > 凤凰城引龙池温泉体验馆', '绥化', '绥化', 'https://img1.qunarzz.com/travel/poi/1712/64/301ccef4ef8a9437.jpg', '--', '绥化景点排名第7', NULL, '0', '46.6136741638', '126.9856311421', '0', '凤凰城引龙池温泉是滨北地区规模最大、设备先进、装饰一流，集温泉理疗、餐饮、购物、娱乐、养生、住宿于一体的大型温泉养生文化旅游基地和居民小区，达到24小时地下温泉直达入户，一年四季享受温泉浴的乐趣，在银装素裹的冬天也可尽兴享乐室外温泉的激情，同时得到温泉的理疗养颜效果，成为绥化市民以及周边地区人们度假、养生、休闲、会议、宴请宾朋的首选之地。经国家相关部门温泉检测报告表明，每升温泉水含矿物质2147毫克，含氟4.8毫克、含钾3.89毫克、含碘化物3.85毫克、含硼酸17.62毫克、含偏硅酸43.49毫克等。主要对各种类风湿、关节炎、皮肤病、湿疹、荨麻疹、牛皮癣、心脑血管、痛风、糖尿病综合症等有明显辅助治疗及保健作用。对女子养颜美容、嫩肌护肤更有奇效。馆内设有木桶理疗专区、木桶足疗专区、温泉泡浴、淋浴区、豪华休息大厅、贵宾包房，室外温泉泡浴区等不同专区、并配有专业的养生理疗专家讲解、指导。经过一年多的温泉体验，受到了广大市民的青睐，来体验者均感受到了怡心养性、养生保健、美容的惬意。', '绥化市北林区康庄路一中北500米路东', NULL, NULL, '7:00-19:00', 20101, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi10016690-fenghuangchengyinlong', '4', '5', '4', '2', '4', '4', 3, 24004, 190, 40946, 86739, 183287, 24351, 46789, 93434, 46178, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (555, '7468744', '百瀑峡', 'bai pu xia', '辽宁 > 丹东 > 丹东景点 > 百瀑峡', '丹东', '丹东', 'https://img1.qunarzz.com/travel/poi/1408/25/f3bae3ea0c14262dffffffffc8d65eac.jpg;https://img1.qunarzz.com/travel/d8/1702/66/764b4120f45fa6b5.jpg;https://img1.qunarzz.com/travel/d4/1702/cb/8d615febb10505b5.jpg;https://img1.qunarzz.com/travel/d7/1702/15/a795b62307e91cb5.jpg;https://img1.qunarzz.com/travel/d2/1702/40/d4c16f1eeb5cd2b5.jpg;https://img1.qunarzz.com/travel/d6/1702/26/2e3710437a176db5.jpg', '4.3', '丹东景点排名第87', NULL, '6', '40.978467', '125.016021', '4', '百瀑峡瀑布多、水清澈。只见那泉水从峰巅上涌出，千曲百折，漫谷穿峡，形成大小瀑布近百个，组成了一个庞大的梯式瀑布群，百瀑峡因此而得名。百瀑峡的瀑布在东北都属罕见，令人目不暇接，心扉震撼。\n夏日来到“百瀑峡”您就仿佛置身于一个清凉的世界，清冽的溪水，寒气袭人．碧绿的山，碧绿的水，碧绿的树，碧绿的石清碧诱人．这里大大小小的几十个瀑布形态各异，层层叠叠，翠潭迭瀑，首尾相衔．有如珠帘，有如银花，有如泻布，有如飞纱为慰壮观，在深山峡谷中奏响了一首漫妙的山水交响曲．让您大饱眼福，大饱耳福．大自然的鬼斧神功，将泉潭溪瀑聚敛为一体，组成一个和谐的世界，令人心扉震撼。\n境内原始古朴，森林茂密，森林垂直分布明显，动植物资源种类繁多。森林植被保持完整，自然环境清纯，氧气充足，负氧离子含量高，温湿度适宜，水质极佳，含有丰富的对人体有益的微量元素，是世界上第六大无污染区之一的核心部位，被称为“清爽、清幽、清秀、清纯”的大清世界，“辽东净土”、天然氧吧！', '辽宁省丹东市宽甸县牛毛坞镇泉山林场', NULL, NULL, '8:00-17:00。', 15279, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi7468744-baipuxia', '', '3', '3', '4', '5', '4', 5, 42625, 94, NULL, 29934, 62730, 138634, 334034, 119718, 43716, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (556, '8900988', '半拉山', NULL, '吉林 > 辽源 > 辽源景点 > 半拉山', '辽源', '辽源', 'https://img1.qunarzz.com/travel/d8/1701/29/b17e3b5e5530e1b5.jpg;https://img1.qunarzz.com/travel/d8/1701/61/f68c3a93b4cb22b5.jpg;https://img1.qunarzz.com/travel/d9/1701/b5/181d8d3db48b4cb5.jpg;https://img1.qunarzz.com/travel/d3/1701/e9/a3020018eb866b5.jpg;https://img1.qunarzz.com/travel/d2/1701/7b/9c69327b1b9418b5.jpg;https://img1.qunarzz.com/travel/d7/1701/6e/089a7fedd25b8b5.jpg', '2.5', '辽源景点排名第1', NULL, '3', '44.5290064648', '126.4092644853', '1', '昔日半拉山上没有耕地，山上草木葱笼，野花绚烂，是一个放牧的好地方。解放初，政府在那里建立了采石场。如今半拉山一带工厂、学校、居民密集，在山上开辟出了很多的树林和农田，已然形成了一片欣欣向荣的生活景象。形如霓虹的财富大桥坐落在辽河岸边的半拉山脚下，为半拉山一带增添了一道靓丽的风景。', NULL, NULL, NULL, NULL, 15592, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi8900988-banlashan', '', '4', '2', '1', '1', '2', 9, 31115, 138, NULL, 178084, 13520, 22941, 18406, 38522, 13584, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
