INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (558, '10011715', '太史坊', NULL, '海南 > 定安 > 定安景点 > 太史坊', '定安', '定安', 'https://img1.qunarzz.com/travel/poi/1712/a6/f37a6b3fb2c5d337.jpg', '--', '定安景点排名第3', '0.2小时 - 0.3小时', '0', '19.6139087677', '110.3498115405', '0', '太史坊坐落于定安县雷鸣镇龙梅村，建于万历二年（1574年）孟春，系右副都御史殷正茂，巡抚广东监察御史张守约为国史官王弘诲所立纪念物，故称太史坊。\n坊通高5.1米，面宽三间，明间（除柱）宽2.84米，次间（除柱）宽1.3米。明间太史坊额石面宽80厘米，长3.1米，顶盖及专额石之间竖一石，上刻“恩荣”二字。太史坊额外负担石正面中间阴刻横写正楷字“太史坊”，右侧阴刻直写小楷“总督闽广粮饷巡抚广东兵部尚书兼右副都御史殷正茂，巡抚广东监察御史张守约为”，左侧阴刻直写小楷“嘉靖乙丑科会试第二十名进士第翰林院编修文林郎同修国史王弘诲立，万历二年甲戌孟冬吉日”。太史坊额背面阴刻横定正楷大字“解无坊”，左侧阴刻楷体小字“嘉靖辛西科广东乡试第一名王弘诲”。明间大柱对面刻有楷字对联“石柱擎天秀毓南溟开五指，瑶台贯斗光摇北极应三台”，传为明代著名书法家董其昌所书（董其昌系王弘诲之门生）。', '定安县雷鸣镇龙梅村', NULL, NULL, '全天开放', 10842, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi10011715-taishifang', '', '', '2', '1', '5', '2', 4, 45160, 83, NULL, NULL, 13677, 10252, 233438, 25691, 11493, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (559, '713746', '互助土族故土园', NULL, '青海 > 互助 > 互助景点 > 互助土族故土园', '海东', '互助', 'https://img1.qunarzz.com/travel/poi/201303/14/55144915861d1d7eddb12cfb.jpg;https://img1.qunarzz.com/travel/poi/201303/14/72d7c45acca388b9ddb12cfb.jpg;https://img1.qunarzz.com/travel/poi/201303/14/ae1b5dff7e6f7e8bddb12cfb.jpg;https://img1.qunarzz.com/travel/poi/1708/1b/90034a4f65d1e137.jpg;https://img1.qunarzz.com/travel/d5/1712/8d/a541602737da8eb5.jpg;https://img1.qunarzz.com/travel/d2/1712/4a/d6bf2ef76eb19bb5.jpg', '4.7', '互助景点排名第2', '2小时 - 4小时', '14', '36.84493', '101.954596', '0', '这里是全国唯一的土族自治县，土族，这个古老的民族世代在这里繁衍生息，使这里形成了独特的人文景观。这些独特的自然景观和人文景观吸引了众多中外游客前来观光旅游。土族信仰喇嘛教，能歌善舞，服装富有民族特色。每年农历五月初五、六月初六、九月初九等民间节日，互助旅游区内多举行“花儿演唱会”。土族人在漫长的历史进程中形成了自己独特的文化。土族妇女的袖子用红、黄、黑、绿、白等不同颜色拼成，恰似天上彩虹，因此，这里被誉为“彩虹的故乡”。', '西宁市互助威远镇', NULL, NULL, '景点不同开放时间均不相同。', 18838, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi713746-huzhutuzugutuyuan', '5', '4', '5', '1', '1', '4', 8, 32698, 199, 251868, 132703, 300061, 17438, 14262, 118290, 40047, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (560, '10070450', '黎侯古城', NULL, '山西 > 黎城 > 黎城景点 > 黎侯古城', '长治', '黎城', 'https://img1.qunarzz.com/travel/poi/1712/82/fffc0025409b1337.jpg', '--', '黎城景点排名第4', NULL, '0', '36.5320663452', '113.3968716164', '0', '黎侯古城位于山西省黎城县靳家街村（即黎城新城区），古城核心区占地750亩，以一条760米长的人文大道为轴心，道路两旁建有配套商住。两公里的循环水系贯穿其中，特色酒水吧、小吃街依水而建环绕周围，亭廊桥阁点缀其间。尧帝广场、奎聚双塔、黎侯王宫、民俗博物馆、文庙、城门楼广场等主景点布局有序，恢弘大气，构成一道人文旅游景观。\n古戏台可以让游客欣赏到上党落子、晋剧、八音会等传统曲艺和民族歌舞表演。过街楼、庙会街则集中展示了市井文化。回龙潭、洗耳池、燕王泉、三皇桥、古黎亭、黎侯阁等水体景观贯穿其中，小桥流水、杨柳依依。特色酒水吧、小吃街依水而建、风情无限。\n古城还建有古玩字画街、玉石一条街、奇石根雕街等商业街区和黎侯大酒店、温泉服务中心等休闲服务区。整个古城气势恢宏，融汇古今，既能满足吃、住、行、游、购、娱旅游要素，又是宜居养生的好地方。', '长治市黎城县靳家街村（即黎城新城区）', NULL, NULL, '全天开放', 1165, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi10070450-lihougucheng', '5', '', '5', '', '1', '4', 2, 38011, 89, 344539, NULL, 205131, NULL, 11092, 192297, 45087, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
