INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (562, '720378', '火焰山', NULL, '新疆 > 吐鲁番 > 吐鲁番景点 > 火焰山', '吐鲁番', '吐鲁番', 'https://img1.qunarzz.com/travel/d6/1611/23/01949cb0a49857b5.jpg;https://img1.qunarzz.com/travel/d4/1612/71/321294927f0468b5.jpg;https://img1.qunarzz.com/travel/d8/1701/90/fdcea860c3c4c5b5.jpg;https://img1.qunarzz.com/travel/d5/1701/8a/583376a50b3cdfb5.jpg;https://img1.qunarzz.com/travel/d8/1704/13/be44170f055e9cb5.jpg;https://img1.qunarzz.com/travel/d2/1707/d9/da0e2c3030224fb5.jpg', '4', '吐鲁番景点排名第2', '1小时 - 2小时', '156', '42.9314902441', '89.5230043735', '148', '火焰山位于吐鲁番盆地的中部，东西长98公里，南北宽9公里，最高处海拔851米，主峰位于鄯善县连木沁镇西面12公里处。每当盛夏，山体在烈日照射下，炽热气流滚滚上升，赭红色的山体看似烈火在燃烧。火焰山是吐鲁番古代文明和绿洲农业的摇篮，吐鲁番80%的自然和人文景观都集中在这里。如著名的葡萄沟、神奇的吐峪沟和柏孜克里克石窟、吐峪沟石窟以及国家级重点文物保护单位高昌故城、台藏塔和阿斯塔那古墓群等。由于火焰山本身具有独特的地貌，再加上《西游记》里有孙悟空三借芭蕉扇扑灭火焰山烈火的故事，使得火焰山闻名天下。', '吐鲁番盆地的北缘，古丝绸之路北道', '0995-8693109', NULL, '旺季（4月21日-10月20日）8：00—21：00 ；淡季（10月21日-4月20日）10：00-18：30', 20065, NULL, NULL, NULL, '1-2小时', 'http://travel.qunar.com/p-oi720378-huoyanshan', '', '4', '', '3', '', '1', 2, 45763, 108, NULL, 89263, NULL, 96939, NULL, 21518, 5346, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (563, '10001012', '茶卡盐湖', NULL, '青海 > 海西 > 海西景点 > 茶卡盐湖', '海西', '海西', 'https://img1.qunarzz.com/travel/d8/1805/f5/ec191f65624438b5.jpg;https://img1.qunarzz.com/travel/d8/1805/48/6fd370b35fc930b5.jpg;https://img1.qunarzz.com/travel/d5/1805/d5/5359919fa559ab5.jpg;https://img1.qunarzz.com/travel/d1/1805/6a/4d8a4e2b645d52b5.jpg;https://img1.qunarzz.com/travel/d6/1805/6a/7ee6bb58178aacb5.jpg;https://img1.qunarzz.com/travel/d7/1805/d6/6da4db563cf171b5.jpg', '4.6', '海西景点排名第2', '2小时 - 3小时', '25', '36.713182', '99.075235', '173', '茶卡盐湖位于青海湖西侧的乌兰县茶卡镇境内，距离青海湖区域的黑马河乡约80公里，很多游玩青海湖的游客都会到这里一游。茶卡盐湖是一座3000多年的盐场，湖水含盐量很大，会自然结晶成为一片白色的湖面，将天空、云朵和对岸的山都倒映在湖里非常漂亮。游客还可以赤脚走到湖面上观看和拍摄自己的倒影，好像天空之境。景区里还有众多的盐雕可以观赏，还可以乘坐小火车，到盐湖的深处观光。\n茶卡盐湖景区不大，一般游玩2-3小时即可。最适合晴天时前来游玩，而且最好是连续几天都是晴天之后前来，此时湖水结晶度很高，会看到洁白的盐湖上覆盖一层薄薄的卤水，景色和倒影最为清晰纯净。若是连续几天阴雨，湖中的盐层可能会被稀释溶解掉，则会露出底下的泥地，不能下湖去玩，景色也很一般。\n游玩茶卡时会从茶卡镇过来，到达位于景区北侧的大门，在这里购票进入。大门距离游玩区域还有一段距离，不过可以开车进入，到达雕塑区之后再下车开始游玩。\n刚下车的区域会有一座盐湖守护女神的石质雕塑，除此之外，其他的雕塑便全是盐雕了，沿着通道大约有十几座巨大的盐雕，有弥勒佛、成吉思汗等造型，壮观逼真，可以沿路走过去时顺便看看。这里还有很多售卖盐雕工艺品的摊铺，可以根据喜好购买，盐雕有大有小，价格5元到几百元都有，一般推荐购买小型的给小朋友玩玩即可，大的比较贵，且带回后容易融化和破损。在盐雕区域旁边还有买酸奶的小店，一碗约10元左右，可以尝尝。\n沿路走到湖边，便看到了洁白纯净的茶卡盐湖。可以看到天空、云朵、对岸的山，还有排列整齐的电线杆全都映在湖中，非常漂亮。在这里还可以脱了鞋子赤脚走进湖里，去拍自己的倒影，尽情游玩。不过游玩之后上岸时要注意马上用淡水洗洗脚，不然皮肤会被盐水灼伤。岸边有专门的人卖淡水洗脚，每次约需5元左右。\n湖中间有一条铁路延伸至湖内，起点处便是乘坐小火车的地方（另收费），可以乘车前往湖内深处去游玩，那里的景色更为纯净，可以根据自己兴趣前去游玩。如果不坐小火车，沿着铁轨向湖深处散散步拍拍照片也是非常惬意的。\n在盐湖景区北侧约5公里处即为茶卡镇，镇上有几家餐馆，价格还算实惠，游玩之后可以来到这里用餐。茶卡镇住宿的选择很少，建议在游玩之后返回到青海湖区域住宿。', '海西州乌兰县茶卡镇盐湖路9号', '0977-8240129', NULL, '茶卡盐湖闭园改造，暂定2018年4月26日开放，具体信息以景区公告为准', 13390, NULL, NULL, NULL, '2-3小时', 'http://travel.qunar.com/p-oi10001012-chakayanhu', '3', '', '2', '3', '2', '3', 9, 22937, 72, 41019, NULL, 17824, 40359, 33341, 31865, 26218, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (564, '3864499', '沙湾温泉旅游区', NULL, '新疆 > 沙湾 > 沙湾景点 > 沙湾温泉旅游区', '塔城', '沙湾', 'https://img1.qunarzz.com/travel/poi/1803/81/28f60319bf976437.jpg', '4', '沙湾景点排名第2', '1小时', '1', '43.894873', '85.4094', '0', '沙湾温泉位于沙湾县西南78公里处，有旅游公路通过，交通条件较好。\n沙湾温泉居于金沟河河谷之中，东、南、西三面环山，金沟河向北流淌。相传200多年前，沙湾温泉已被人发现利用，清同治年间即在此修老君庙。此处共有大小温泉十几处，其中以老君庙下的三处热泉为主，被称为1号泉、2号泉和3号泉，泉汇水成流，流量分别为每秒10升、每秒2升和每秒3升。另有一\"眼睛泉\"流量每秒0.5升，可治眼疾。\n沙湾温泉景区，气候温凉宜人，夏无酷暑，冬无严寒，金沟河两岸景色优美，风光旖旎，是观光旅游的好地方。1952年沙湾就在此地建疗养院，并于1967年建浴池和病房1500平方米，现有3个浴池，58间病房，250个床位，还配备了生活车、救护车、商店、食堂等生活设施。', '新疆-伊犁沙湾县西南78公里处', '0901-6964006', NULL, '全天开放', 6257, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi3864499-shawanwenquanlu:youqu', '', '1', '', '', '1', '1', 1, 47400, 123, NULL, 18101, NULL, NULL, 7675, 12055, 5522, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (565, '7645622', '鼓山', NULL, '安徽 > 巢湖 > 巢湖景点 > 鼓山', '巢湖', '巢湖', 'https://img1.qunarzz.com/travel/poi/1803/a2/b31e34d7cda8f537.jpg;https://img1.qunarzz.com/travel/poi/1504/62/9b05904699f3e1.jpg', '--', '巢湖景点排名第30', NULL, '0', '31.608726', '117.926622', '0', '鼓山又名亚父山，是历史著名谋略家范增故里，亦因状似一面巨鼓而得名。山中林禅并重，遍山苍翠，山腰有一尊范增汉白玉雕像及范增亭。山顶建有一座明七层暗八层鼓山塔。沿亚父街往山上大约300米，就到了鼓山大门。鼓山大门是一座气势恢宏，层次错落的仿古建筑。上面有上下两层的琉璃瓦飞檐门楼，下面是四根红色廊柱。门楼上书“鼓山寺”三个金字，是赵朴初先生亲笔题写。下面廊柱上刻有张凯帆题写、方克逸撰写的对联：“绝顶摩天，慈怀纳地”。这八个字概括了鼓山佛教文化的内涵。', '合肥巢湖市居巢区巢湖景区', NULL, NULL, '7:00-19:00', 10615, NULL, NULL, '优惠政策：\n身高1.2米—1.5米购优惠票；\n60岁—69岁老年凭证购买优惠票。', NULL, 'http://travel.qunar.com/p-oi7645622-gushan', '1', '', '', '', '4', '2', 8, 28188, 192, 15659, NULL, NULL, NULL, 118114, 42887, 10717, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (566, '710248', '东坡书院', NULL, '海南 > 儋州 > 儋州景点 > 东坡书院', '儋州', '儋州', 'https://img1.qunarzz.com/travel/d9/1801/40/ee2637668c385fb5.jpg;https://img1.qunarzz.com/travel/d7/1601/19/5117dbae785938f7.jpg;https://img1.qunarzz.com/travel/d9/1603/d6/bfe3716ce15e2ff7.jpg;https://img1.qunarzz.com/travel/d6/1601/8e/89197191b08b1af7.jpg;https://img1.qunarzz.com/travel/d4/1504/15/9cf3751bc5563f.jpg;https://img1.qunarzz.com/travel/d3/1601/14/1aed2f68c92af5f7.jpg', '4.6', '儋州景点排名第1', NULL, '19', '19.7475161556', '109.3632860667', '11', '位于海口市西线180公里的儋州市中和镇，离现儋州市政府所在地那大镇40多公里，是为纪念北宋大文豪、谪臣苏东坡而建于北宋（1098年），后经重修，明代（1549年）更为现名。890年前，苏东坡由幼子苏过陪伴，在这里度过了三年的流放生活。后人把他讲学、会友、寓居过的地方命名为东坡村、东坡田、东坡井、东坡路、东坡坐石，并建东坡居士塑像以示纪念。书院大门轩昂宏阔，院内古木参天，由载酒亭、载酒堂、大殿、奥堂龛等古建筑组成，亭阁、莲池、廊舍，颇为精致讲究，显然当年东坡被贬之时无福受用，只是表明海南人对文化的尊重和对苏居士为海南做出伟大贡献的认可。院藏碑文、碑刻、雕刻、器具、井泉、书画等甚多，如著名的《坡仙笠屐图》以及郭沫若、邓拓、田汉来此题咏的艺术作品。载酒堂里存有15块碑刻，元、明、清等。', '儋州市中和镇', NULL, NULL, '8：00－18：00', 13806, NULL, NULL, NULL, '2小时', 'http://travel.qunar.com/p-oi710248-dongposhuyuan', '', '', '', '2', '2', '4', 0, 38738, 79, NULL, NULL, NULL, 18439, 41319, 86615, 49296, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
