INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (577, '714142', '恰西风景区', NULL, '新疆 > 伊犁 > 伊犁景点 > 恰西风景区', '伊犁', '伊犁', 'https://img1.qunarzz.com/travel/poi/201408/04/e9c2105942790e1bc8d65eac.jpg;https://img1.qunarzz.com/travel/d8/1701/51/dc3226613e5ca3b5.jpg;https://img1.qunarzz.com/travel/d7/1701/33/132a593f789262b5.jpg;https://img1.qunarzz.com/travel/d7/1701/30/69fdf69c5850d8b5.jpg;https://img1.qunarzz.com/travel/d4/1701/68/9ca9cc42f2b555b5.jpg;https://img1.qunarzz.com/travel/d4/1701/97/da5de705ea1f41b5.jpg', '4.6', '伊犁景点排名第28', '2小时', '9', '43.0931333201', '82.6414352298', '10', '塞外江南在伊犁，伊犁最秀是恰西。恰西位于巩留东南部山区，距县城72公里。由县城东上，途经特克斯河山口水利枢纽工程，穿过喀普其海峡谷和吉尔尕郎谷地，出吉尔尕郎乡向东，沿山路盘旋东行，到达被称为沙尕的恰西谷口。这里东西两条路在此交汇，西路通往著名景区塔里木森林公园。过恰西沙尕大桥，前行7公里，钻出天工斧削的峡谷，豁然的是一条宽敞清秀的谷。东西两侧低山如垣，东侧山峦芳草绿茵，西侧山峦苍松凝翠，正南是势如屏障的商山，山顶角峰林立，苍岩露红，冰山隐约，齐齐的林线以下，青杉如良，蜿蜒东西。这里就是颇具盛名的恰西。\n恰西阔谷呈南北走向，长约13公里，宽500-1000米，海拨1500米，冬暖夏凉，气候宜人背靠雪山，群山环抱，古树参天， 绿水环绕。其景观雄奇中透着秀丽，素有恰西山水美如画之称，有不少文人墨客为其咏和泼墨作画。主要景观有林海松涛鹿苑，石门垂柳，小溪飞瀑，燕子桥等。恰西是著名林区， 国家农业展览馆陈列的\"云杉王\"截样就出自该区。恰西河水素有消食健胃，抗病祛邪之说，堪称优质矿泉水。\n据说以恰西河水洗面，可除皱，祛斑，养颜。恰西河盛产黄鱼(学名银色臀鳞鱼,国家二类保护动物)，肉嫩刺少，鲜美无比。恰西云杉林中还盛产羊肚菌，是世界著名的食用菌，每公斤高达上千元，每年5-6月间是采摘的旺季。恰西西峡盛产马林，俯拾皆是，在采伐的迹地中，更是攀坡漫生，犹如一片片马林园。', '新疆伊犁州巩留东南部山区', NULL, NULL, '全天', 20498, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi714142-qiaxifengjingqu', '1', '5', '', '3', '5', '1', 8, 41955, 81, 12335, 293547, NULL, 88097, 285125, 12444, 5293, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
