INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (581, '704999', '青州博物馆', NULL, '山东 > 青州 > 青州景点 > 青州博物馆', '潍坊', '青州', 'https://img1.qunarzz.com/travel/poi/1803/1c/c79914895fb29b37.jpg;https://img1.qunarzz.com/travel/d9/1606/f9/22547807fd64799a.jpg;https://img1.qunarzz.com/travel/d7/1606/69/adf5907d513ee09a.jpg;https://img1.qunarzz.com/travel/d6/1606/e5/76a05f1ad892ba9a.jpg;https://img1.qunarzz.com/travel/d9/1606/52/b38bdc93e15e769a.jpg;https://img1.qunarzz.com/travel/d3/1606/bd/df7232e153b019a.jpg', '4.6', '青州景点排名第6', '1小时 - 2小时', '11', '36.6931941653', '118.4708960845', '4', '青州博物馆位于风光秀丽的古青州城西北角，毗邻范公亭公园。虽然博物馆不大，却珍藏有明代赵秉忠状元卷、汉代宜子孙玉璧、东汉大型跪姿石俑等稀世文物，还可见到独具特色的龙兴寺佛教造像群，藏品丰富而珍奇，因此成为国家一级博物馆中唯一一家县级博物馆。\n青州是古代“九州”之一，这里积淀了千年文化与历史，所以馆藏的文物大多是于当地出土的。建议在博物馆内租借导览器，可了解具体的文物介绍，费用为每台10元，需提供有效证件及押金200元。若是请讲解员讲解更为生动，中文讲解100元，讲解时间约45分钟。注意馆内禁止拍照。\n青州博物馆是座合围式的仿古建筑，外形古典雅致。展区共有两层，分为青州简史陈列厅、龙兴寺佛教造像精品厅，以及陶瓷、古代书画、石刻雕塑、石刻碑碣、玉器、青铜塑像等陈列展厅。其中最具特色的是在龙兴寺遗址出土的数百尊石刻佛教造像。\n龙兴寺始建于北魏时期，这里出土的佛像造型优美、雕刻精湛，有北魏至北宋各个时期的风格，最有意思的是佛像都面带微笑，十分罕见。由于这批佛像是在战火中被宗教人士保护性地埋藏起来的，而且出土很晚，所以有的袈裟仍是红色、有的镏金仍在，甚至有些五官棱角分明，保存很好。\n古代书画厅中陈列着的赵秉忠的状元殿试卷，是全国唯一殿试卷真迹，同样是镇馆之宝。这份保存完整的状元试卷，以小楷书写，字迹工整，通篇2460字没有任何错别字。卷上有朱批“第一甲第一名”，是当时的万历皇帝朱诩钧御书，还有数位考官的姓名职位。\n玉器展厅中的东汉古墓中出土的汉代“宜子孙玉璧”也不容错过，整块玉为和田墨玉质地，上面有透雕篆书\"宜子孙\"三字，意味“子子孙孙宜室宜家”，不仅雕技精湛，更是目前仅见的有字汉璧。\n此外，馆内还有北齐傅家画像石、仇英的清明上河图，以及金黑釉剔花罐、唐三彩钵等艺术珍品，而明代衡王府中的遗物、蹲狮、铜鹤等物品也十分经典，值得一观。', '潍坊市青州市范公亭路1号', '0536-3261736;0536-3266200', 'http://www.qzbowuguan.com', '冬季（10月-次年4月）：9:00-16:30（16:00停止发票）；夏季（5月-9月）:9:00-17:00（16:30停止发票）', 10399, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi704999-qingzhoubowuguan', '1', '1', '5', '4', '5', '3', 7, 33765, 152, 9698, 13357, 381177, 132758, 356433, 36785, 26056, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
