INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (583, '7469156', '文笔峰', NULL, '海南 > 定安 > 定安景点 > 文笔峰', '定安', '定安', 'https://img1.qunarzz.com/travel/d7/1602/50/f3e407959eb67df7.jpg;https://img1.qunarzz.com/travel/poi/1408/26/65bd85a1fd728b8dffffffffc8d65eac.jpg;https://img1.qunarzz.com/travel/d1/1602/19/288a4617b0170ff7.jpg;https://img1.qunarzz.com/travel/d7/1602/3f/98f1a910314862f7.jpg;https://img1.qunarzz.com/travel/d7/1602/50/f3e407959eb67df7.jpg;https://img1.qunarzz.com/travel/d3/1602/91/f08040c4e1c074f7.jpg', '4.3', '定安景点排名第1', '3小时 - 4小时', '6', '19.558987', '110.372117', '6', '文笔峰，又名文豪岭。“文笔”、“文豪”的意思就是：文采、有文采的人。古代这里出了不少的文人才子，被明朝明神宗誉为“三朝硕士，一代伟人”的礼部尚书王弘诲就出生在文笔峰脚下雷鸣镇龙梅村。王弘诲一生著作很多，文学上颇有成就，其中著有《南溟奇句录》、《尚友堂稿》等，至今，龙梅村还有一座为纪念王弘诲 所建的富有海南建筑特色的明代牌坊，建筑设计造型古朴雄伟、坚固大方，叫做“太史坊”。\n关于文笔峰的由来有一个神奇的传说：相传很久以前，有位仙人挑着一担仙土准备送到王母娘娘的蟠桃园，路过定安的时候，看到这里人杰地灵、美景如画，便停下来歇歇脚、饮酒赏景，一筐土放在自己的脚下，另一筐土放在旧州，不知不觉仙人就喝醉睡着了，这一睡就睡了很久很久，仙人便化成了仙人石，两担土变成了两座山，定安这座山就是现在的文笔峰。\n文笔峰上留下的仙迹有：仙人洞、仙人大脚印、仙人手掌、仙人酒杯、仙人石、娘子房等。', '海南省定安县龙湖镇丁湖路(即东线高速路41公里龙湖出口西行3公里处)', '0898-63733252', 'http://www.wenbifeng.com/', '8:00-18:00', 4957, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi7469156-wenbifeng', '1', '', '', '1', '5', '4', 3, 39318, 92, 6485, NULL, NULL, 19997, 351083, 174320, 47120, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (584, '10010920', '百丈峰', NULL, '江西 > 新余 > 新余景点 > 百丈峰', '新余', '新余', 'https://img1.qunarzz.com/travel/poi/1712/e5/dc031f4b8655f237.jpg', '--', '新余景点排名第13', NULL, '0', '27.764166', '115.236477', '0', '百丈峰地处渝水区、新干县、峡江县三县交汇处，属罗宵山脉，是武功山最东的余脉，是赣江、袁河的分水岭。主要山脉为东西走向，地势高峻，群峰耸立，蜿蜒30余里，宛如一条横空而卧的青龙。百丈峰主峰海拔424.2米，高逾百丈，因处于沃野千里的赣抚平原，尤显其高，故名曰百丈峰。\n百丈峰是平民百姓休闲避暑、踏青赏花的最佳去处。这里气候温和，阳光充足，雨量充沛，四季分明，年平均气温17.7℃，年平均降雨量1573.6毫米。景区内森林面积达3079公顷，总蓄积量21.6万立方米，森林覆盖率达90.8%，属省级森林公园。经过数十年的精心培育和有效保护，已形成阔叶林、针叶林、竹林和灌木林等植被群落，并拥有近百公顷的原始森林植被，山峻林茂、绿潮叠涌，四季景色千姿百态；壑深潭幽，泉涌溪流，探幽访胜其乐融融；空气清新，怡人心脾，休闲养生延年益寿。天清景明，伫立峰顶，赣江如长练，农田似棋盘，烦忧因此而消隐，心胸为之而开阔。景区内野生动物资源也极为丰富，是野生动物繁衍栖息的天堂。\n百丈峰是文人学者揽胜探幽、风云际会的极佳场所。这里濒临赣江，形似青龙，是一方颇具灵性的风水宝地。百丈峰以灵秀而著称，云雾飘渺，卓然物外，是佛道仙山。史载：“百丈峰右之葛峰，山势峻拔，高出东境，上有葛仙坛，有仙人迹”。这里人文璀璨，群峰之间留下自唐以来众多的寺庙遗迹，有南仙姑寺、圣井寺、石井寺、蓝塘寺、宝历寺、黄塘寺、保安寺、仙居寺、东角坛寺以及正在恢复重建的云峰寺等，道观遗迹有葛仙坛、雷祠、仙井等。百丈峰因仙而名，因佛而盛，清幽淡雅，享誉四方，炼丹始祖、道教名家、东晋名人葛洪、南宋著名理学家朱熹、元代文坛名家范德机、傅若金等历代名人纷至沓来，或修道参禅，或探幽访胜，留下无数胜迹名篇。\n百丈峰也是广大市民接受教育、陶冶情操的丰富课堂。这是一座雄山，地势雄险奇伟，曾留下许多英雄豪杰的战绩武功。在历史上，百丈峰为吴越分野之地，地处北达豫章、南下吉赣、襟带岭粤、西去潇湘的咽喉之地，故多次成为兵家必争之地，成为或严守、或死攻的要塞所在。从古至今，百丈峰发生过多次战役，著名的有：石达开百丈峰火烧湘勇、李秀成伏击团练、毛泽东大败国民党军突围南下等。传说中的皇帝洞、岳飞点将台等遗迹更是让人感叹！', '新余市渝水区306乡道', NULL, NULL, NULL, 14294, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi10010920-baizhangfeng', '', '2', '2', '', '5', '3', 4, 26225, 186, NULL, 38973, 46979, NULL, 286114, 99583, 20710, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
