INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (588, '9637613', '乌兰哈达火山地质公园', NULL, '内蒙古 > 乌兰察布 > 乌兰察布景点 > 乌兰哈达火山地质公园', '乌兰察布', '乌兰察布', 'https://img1.qunarzz.com/travel/poi/1705/1b/4e386a5db0c07a37.jpg;https://img1.qunarzz.com/travel/d7/1712/53/25b7a5d8c592a8b5.jpg;https://img1.qunarzz.com/travel/d8/1712/74/809452b5b92331b5.jpg;https://img1.qunarzz.com/travel/d2/1707/93/9bcf52d64045d8b5.jpg;https://img1.qunarzz.com/travel/d0/1707/6d/fde2e3c7fb2be5b5.jpg;https://img1.qunarzz.com/travel/d7/1707/44/7e5690509b3e95b5.jpg', '4.8', '乌兰察布景点排名第2', NULL, '17', '41.620425', '113.168869', '7', '数万年前，这里发生了惊天动地的火山喷发；沸腾的熔浆，像河流一样奔涌；数千年前，远古先民在这里生息繁衍，创造了丰富多彩的古代文化；数百年前，蒙古民族游牧文明的精髓，在这里得以保存；令人荡气回肠的历史史诗，在这片草原上流传；这就是乌兰哈达火山地质公园，一座草原深处的天然火山“博物馆”。\n乌兰哈达火山地质公园面积65.9平方公里，有着丰富的第四纪火山地质遗迹，形成完整的火山口，和火山链，犹如散落在草原上的一串珍珠。紧邻正在修建的大门的是黑脑包（四号火山）。海拔1581米。东部已经被人工开挖，火山锥体由红色、灰黑色玄武质火山渣、熔结集块岩、碎成熔岩和玄武岩等构成。高耸挺拔的火山犹如太上老君的炼丹炉，当地村民按照火山的相对方向给火山取名为“北炼丹炉”、“中炼丹炉”、“南炼丹炉”。', '内蒙古自治区乌兰察布市察哈尔右翼后旗', NULL, NULL, NULL, 12186, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi9637613-wulanhadahuoshandizhi', '', '5', '', '1', '5', '4', 9, 30231, 135, NULL, 135681, NULL, 12043, 297321, 128694, 42857, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
