INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (592, '708470', '华山森林公园', NULL, '山东 > 莱芜 > 莱芜景点 > 华山森林公园', '莱芜', '莱芜', NULL, '--', '莱芜景点排名第8', NULL, '0', '36.2157126532', '117.685858974', NULL, '华山森林公园位于莱芜市西北部山区，属于泰山支脉，距市区30多公里。公园总面积46平方公里。境内山峦起伏，沟壑纵横，有大小山头96座，其中海拔700米以上的25座。西部最高点为香山，海拔918.7米，是全市海拔最高的山峰，东部最高点为大山，海拔823米。该园于1998年12月被省林业厅批复为省级森林公园，2003年12月被国家林业局批复为国家森林公园。公园地处暖温带大陆性季风气候区，四季分明，雨热同期，干湿明显。公园历经多年封育，物种资源丰富。有乔木50科200多种，形成以常绿林为主的森林近4000公顷，森林覆盖率为87%，茂密的森林资源为各种野生动物栖息繁衍创造了良好的生态环境。区内有分属11目、29科的鸟类170种， 7目、11科的兽类19种。林区内部小气候特征明显，冬暖夏凉，空气清新，气候宜人。夏季气温比市区低5―8℃，是避暑休闲的理想之地。永宁崮景区位于园区东南部，地势较平缓，境内有山岳、缓地、水库、林木、果树。永宁崮（又名小泰山）为其中心景点。园内以其优越的地理条件，幽雅的自然环境，吸引着文人墨客、宗教信徒、达官贵人来此游览定居，吟诗题字，留下了较多的遗迹遗址和神话故事。大舟院、志公殿、白龙庙、黑龙庙、永宁崮等古迹遗址距今有数百年甚至上千年历史。', '山东莱芜市西北部山区', NULL, NULL, '8：00--19：00', 11491, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi708470-huashansenlingongyuan', '', '5', '5', '', '3', '3', 4, 25195, 80, NULL, 357767, 215642, NULL, 24593, 47562, 21382, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
