INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (594, '722803', '长白山天池', NULL, '吉林 > 长白山 > 长白山景点 > 长白山天池', '长白山', '长白山', 'https://img1.qunarzz.com/travel/d6/1602/dc/a2554d31c5297cf7.jpg;https://img1.qunarzz.com/travel/d5/1510/2a/984d239c461f1c.jpg;https://img1.qunarzz.com/travel/d6/1605/31/54d75e36ca8b2cf7.jpg;https://img1.qunarzz.com/travel/d7/1605/15/04695f155bf99af7.jpg;https://img1.qunarzz.com/travel/d1/1605/c8/3c76a42a1a2c5f7.jpg;https://img1.qunarzz.com/travel/d8/1506/7f/903e3d13ddf8d4.jpg', '4.7', '长白山景点排名第1', '2小时', '193', '42.026217', '128.077815', '23', '长白山天池，是松花、图们、鸭绿三江之源，是中朝两国的界湖。它像一块瑰丽的碧玉镶嵌在雄伟壮丽的长白山群峰之中。\n据《长白山江冈志略》记载：“天池在长白山巅的中心点，群峰环抱，离地高约20余里，故名为天池。”天池实际湖面高度为2194米，是我国最高的火口湖，不愧“天池”之称。天池的湖水面积为98平方公里，湖水平均深度204米，最深处达373米，是我国最深的湖泊。\n长白山天池由于高度较高，气候多变，风狂、雨暴、雪多是它的特点。它有长达10个月的冬季，湖水冻结的时间达6个月之久。当风力达5级时，池中浪高可达1米以上。如同任性的少女发怒，平静的湖面霎时狂风呼啸，砂石飞腾，甚至暴雨倾盆，冰雪骤落。绰约多姿的奇峰危崖统统罩上了一层朦胧的面纱。这雾霭风雨，瞬息万变，虚无缥缈的白山风云，既绘出了“水光潋滟晴方好，山色空蒙雨亦奇”的绝妙美景，又为长白山天池增添了无限的神秘感，它塑造了长白山天池的独特个性。', '吉林省东南部长白山主峰火山椎体的顶部', '4008483777', 'http://www.ichangbaishan.com/', '1月1日~4月30日、10月16日~12月31日：08:30~15:30\n5月1日~7月15日：07:30~16:30\n7月16日~8月15日：06:30 ~17:00（周末售票时间提前30分钟）\n8月16日~10月15日：07:30~16:00（周末售票时间提前30分钟）\nTips：\n1、节假日期间的门区开放时间和停止售票时间根据游客接待量情况另行提前通知。\n2、一般到冬季南坡及西坡会关闭，北坡正常开放。特大雪时，长白山会封山。', 17476, '7、8、9月最佳。\n长白山冬季漫长寒冷，夏季短暂凉爽且变化无常。春季多风，秋季多雾。\n7、8、9月的长白山是避暑胜地，风光秀丽，景色迷人。受山地地形垂直变化影响，长白山从山脚到山顶形成了由温带到寒带四个景观带。“一山有四季，十里不同天”，这时可度过一个不一样的夏天。\n冬季是滑雪运动的最佳季节，最容易看到天池全貌。', NULL, '长白山旅游贴士：\n1、夏季去长白山不要使用雨伞，因为在高山地区容易引雷（有过雨伞引雷身亡案例），而且山上有风，雨伞不易把握。建议使用一次性雨衣，在二道白河镇购买只需5元，景区里10元，要是冒雨买雨衣，常常卖给你20元/件。\n2、携带衣物尽量带外套和长裤，尽管是暑假时期，二道白河镇上穿半袖衣服也热，但进入景区还是需要外套，一来山上气温地，外套可以御寒挡风，若是大晴天也可以阻挡高山强烈的紫外线照射。\n3、景区封闭管理，不准游客自己开车进入景区，必须在里面乘坐环保巴士来往于各景点，所以最好轻装进入景区，带点午餐方便食品和水。', NULL, 'http://travel.qunar.com/p-oi722803-changbaishantianchi', '3', '5', '', '1', '1', '4', 4, 30001, 80, 24910, 341793, NULL, 8993, 22755, 114663, 43287, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (595, '10015843', '尤家花园', NULL, '安徽 > 颍上 > 颍上景点 > 尤家花园', '阜阳', '颍上', 'https://img1.qunarzz.com/travel/poi/1712/e7/2c96680568733237.jpg', '--', '颍上景点排名第5', '1小时 - 2小时', '0', '32.657742', '116.232761', '0', '尤家花园位于颍上县县城西边的五里湖畔，是清末兖台府镇守使尤成章所建的私家园林，1923年，其子尤荫轩扩建老园，园林整体布局小巧玲珑，在皖北有这么一座苏式园林颇为稀奇，得到了“皖北豫园”的美誉。\n尤家花园一共有四个园区。第一园区内的盆景园最吸引游客，有梅花、海棠、石榴等盆景，盆景的种类和数量都非常多。第二园区是看假山奇石、亭台楼阁的。第三园区为型松园，可以看到被修剪成各种造型的松柏，有的像伞，有的像塔，有的像鼓……造型生动。第四园区是新建的花园和植物园区。在这里，尤家花园的“花园”二字是名副其实的，园内的植被四季常青。\n此外，园内还有风波亭、读书堂、松桂亭、白玉塔、向桂楼、静坐堂、展望亭、将军碑亭等建筑，尤家花园不大，随意参观即可。', '阜阳市颍上县十八里铺乡花园村102省道', NULL, NULL, '8:00-17:30', 3410, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi10015843-youjiahuayuan', '4', '', '5', '', '4', '2', 0, 48655, 83, 113030, NULL, 174677, NULL, 70724, 28992, 13538, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
