INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (597, '711889', '寺口子旅游区', NULL, '宁夏 > 中卫 > 中卫景点 > 寺口子旅游区', '中卫', '中卫', 'https://img1.qunarzz.com/travel/poi/1707/50/d49937fdc173da37.jpg;https://img1.qunarzz.com/travel/poi/201408/13/954ab3ea65869a7bc8d65eac.jpg;https://img1.qunarzz.com/travel/d2/201404/17/8b7dcd8bfebbb756ddb12cfb.jpg;https://img1.qunarzz.com/travel/poi/1707/ea/2d45d7674d57737.jpg;https://img1.qunarzz.com/travel/d8/201404/17/3808386ce93a7c23ddb12cfb.jpg', '0', '中卫景点排名第10', '3小时', '1', '37.2989516982', '105.464466565', '2', '寺口子旅游区位于中卫县境内黄河南岸，距中卫县城50公里，所处地带古称北海，是丝绸之路上宁夏境内最著名的关口之一。寺口子历史悠久，风光旖旎，传说众多，文化底蕴厚重，是西北地区不可多得的旅游资源。这里随处可见“石压树斜出、崖悬花倒生”的景致，把生命理念中的艰难与顽强，深刻与伟大淋漓尽致地揭示在六和之中。\n景区以山体浑圆，状如圆球的绣球山为界，天然地分为东西两线。以奇险美幽自然风光为主的东线大峡谷不乏神奇优美的神话传说。有“一线天”、“天井山”、“通（灵）仙谷”、“神仙脚印”等景观；以历史文化沉淀深厚的西线景区自然景观特色鲜明，实属罕见。有“苏武牧羊遗址”、“ 苏武石窟”、“苦节堂”、“怀汉亭”、“睡佛寺石窟”、“米钵生金”、“马刨泉”等景观。两线风光迥异，特色分明，却又自然和谐地融为一体，形成了文化底蕴和山林风光的互补，真正是造化神秀，妙趣天成。\n该景区还开发了攀岩旅游项目，据国内专业人士介绍，寺口攀岩是目前中国攀岩较为理想的地区之一，大有开发潜力。相信到寺口子旅游区观光旅游，一定会给您意外的惊喜。', '宁夏中卫市宣和镇南20公里寺口风景区', '0955-7639268', NULL, '08:00-18:00', 9893, '沙坡头气候属于干旱气候区，平均气温9.6度，最高气温38.1度，最低气温度。冬春两季多沙暴，以夏、秋季为最佳。', NULL, NULL, NULL, 'http://travel.qunar.com/p-oi711889-sikouzilu:youqu', '', '', '1', '3', '4', '4', 1, 20399, 115, NULL, NULL, 13072, 54714, 72206, 110378, 49984, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
