INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (599, '10067890', '迈赫机器人大世界', NULL, '山东 > 诸城 > 诸城景点 > 迈赫机器人大世界', '潍坊', '诸城', 'https://img1.qunarzz.com/travel/poi/1712/7c/271d8c83d54a4737.jpg', '--', '诸城景点排名第5', NULL, '0', '36.1035949224', '119.4032556929', '0', '迈赫机器人大世界项目占地面积210亩，包括工业机器人、服务类机器人、教育类机器人、娱乐机器人、物联网、云计算及大数据等展示种类及体验设施，还有带给您视觉震撼的4D智能化动感影院，是国内外公众了解世界机器人应用和感受德国工业4.0内涵的一个高级平台，是目前全球规模最大、种类最多、科技水平最高的旅游体验中心。该体验中心以“人工智能”为线索，通过通讯技术、虚拟实现技术和现实工厂、机器设备相结合，将工业机器人焊接、喷涂、装配、搬运、打磨、分拣及码垛等功能运用到智慧生产的全过程，也将涵盖智能输送装备、集中控制系统、信息处理系统等制造业的智能化转变等要素；同时还展示机器恐龙、智能引导机器人、酒吧服务机器人、拳击对战机器人等人机互动系统。该中心将运用声、光、电等多项现代技术与娱乐手段，使体验现场既高度智能化又妙趣横生，使各展示单元既自成一体又彼此关联，是集科研、科普、生产、游览于一体的令人震撼、回味无穷的现代科技体验中心。', '潍坊市诸城市舜王街道工业新区纵一路', NULL, NULL, '周六周日及法定节假日:上午9:00-11:00，下午14:00-17:00，工作日不开放。', 12776, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi10067890-maihejiqirendashijie', '', '1', '', '4', '3', '4', 4, 39692, 156, NULL, 16829, NULL, 157562, 25291, 65220, 40119, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (600, '10004532', '德州希森欢乐岛游乐园', NULL, '山东 > 德州 > 德州景点 > 德州希森欢乐岛游乐园', '德州', '德州', 'https://img1.qunarzz.com/travel/poi/1712/67/396ce9cd5c97ce37.jpg', '5', '德州景点排名第22', '1小时', '1', '37.45442', '116.307476', '0', '德州希森欢乐岛游乐园，建于2002年7月，地处风景秀丽的德州新湖风景区西侧，与德州新湖风景区、德州中心广场交相辉映，溶为一体，是鲁北地区最大的旅游景区。德州游乐园占地70余亩，三面环湖，园内繁花似锦，绿草如茵，湖面波光遴遴，风景如画。游乐园建有十二项大型现代化娱乐设备，有鲁北“迪斯尼乐园”之称。\n德州游乐园2004年引进了鲁北地区第一套大型滑行类游艺设施，号称“刺激游戏之王”的“过山车”。此次引进的过山车轨道全长490米，时速可达75公里，落差达23米。“心脏停跳，天旋地转，骨骼喀嚓喀嚓地扭动，更高，更快，更像失控！”，“抓紧，否则就没命了！”这就是过山车带给你的体验。过山车倍受欢迎，自开放以来每天都有游人排队乘坐这种令人情不自禁发出尖叫的机器，等待体验那灵魂出壳的惊悸感。乘坐过山车的感觉就像普通人终于驾驶了喷气式战斗机。当过山车在轨道上达到每小时75公里的速度时，人们可以真实地体验到战斗机驾驶员的感觉。“坐过过山车，你才算没白活”。让我们做好准备进入过山车创造的“疯狂世界”吧！\n德州游乐园还建有模仿美国“阿波罗”号太空飞船在太空作三维立体飞行制作而成的“阿波罗飞船”。它造型新颖、气势磅礴、惊险刺激，两个回转臂好比是展开的太阳能极板，塔架好比是阿波罗飞船的船体，游客在运动中体验到相对水平旋转、相对垂直旋转、上下方向的升降，在舒适中体验刺激，得到开心的享受。', '德州市德城区柴市街', NULL, NULL, NULL, 13610, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi10004532-dezhouxisenhuanledaoyou', '', '3', '1', '4', '4', '3', 8, 21929, 205, NULL, 32136, 23879, 142906, 108860, 79061, 24783, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (601, '10067343', '百鸟乐园', NULL, '广西 > 上思 > 上思景点 > 百鸟乐园', '防城港', '上思', 'https://img1.qunarzz.com/travel/poi/1712/74/e093a2592bd3f737.jpg', '--', '上思景点排名第3', NULL, '0', '21.916149', '107.9208', '0', '“百鸟乐园”是利用十万大山优越的自然生态环境，建立以各种珍稀鸟类（国家二类保护的野生动物）观赏为中心主题，以休闲度假、科普教育、康体培训、绿色环保为主要内容，并集“吃、住、玩、乐”为一体，具有参与性、趣味性、娱乐性特色的大型生态旅游度假景区。\n鸟类观赏主题旅游生态景区为1、2、3区，公园各种珍稀鸟类数量将达到五千多只、一百五十余个品种，百鸟乐园将成为国内、乃至亚洲最大的、以鸟类文化为主题的旅游生态景区。目前园区利用十万大山优越的自然生态环境，宣传和推广鸟类文化，其中包括珍稀鸟类的驯养、繁殖、展示、救护、科普教育等，已初步完成六十个品种、约八百多只鸟类的园区建设。目的是培养人们识鸟、爱鸟、护鸟的环保意识，在山秀林茂水清的十万大山更加富有灵气和活力，营造一个“人与自然”亲密相处、高度和谐的美好环境，让百鸟乐园旅游风景区成为名符其实的“小鸟天堂、人类乐园”。', '防城港市上思县红旗林场十万大山森林公园', '0770-2220908;0770-2060119;139****6183;156****0907;139****2828', 'http://www.bainiaoleyuan.net/', '（夏季）8:00-18:00；（冬季）8:30-17:30', 9220, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi10067343-bainiaoleyuan', '4', '3', '4', '2', '3', '5', 8, 30568, 198, 114196, 74760, 137678, 21361, 32677, 236190, 88193, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
