INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (613, '705863', '明显陵', NULL, '湖北 > 钟祥 > 钟祥景点 > 明显陵', '荆门', '钟祥', 'https://img1.qunarzz.com/travel/d5/1611/36/6df64faa227920b5.jpg;https://img1.qunarzz.com/travel/d3/1611/cf/f84caa77b296ecb5.jpg;https://img1.qunarzz.com/travel/d0/1611/df/0cb97bfa0e3adfb5.jpg;https://img1.qunarzz.com/travel/d4/1712/95/b8592418e77b73b5.jpg;https://img1.qunarzz.com/travel/d2/1611/b9/8039eeceafc27cb5.jpg;https://img1.qunarzz.com/travel/d1/1611/16/90615a9ed155a0b5.jpg', '4.1', '钟祥景点排名第1', '4小时 - 5小时', '27', '31.2085019033', '112.6467278925', '1', '位于钟祥市的明显陵，始建于明正德十四年（1519年），迄于明嘉靖三十八年（1559年），历时40年建成，是明世宗嘉靖皇帝的父亲恭睿献皇帝朱祐杬、母亲章圣皇太后的合葬墓。\n陵园由30余处规模宏大的建筑群组成，依山间台地渐次布列有纯德山碑、敕谕碑、外明塘、新旧红门、御碑楼、石像生、棂星门、九曲御河、方城、前后茔城等，众多的建筑掩映于山环水抱之中。游客进入景区后，沿着中轴线向前步行游览即可。\n显陵的修建，是明朝嘉靖初期重大历史事件“大礼议”的产物，规划布局和建筑手法独特，在明代帝陵规制中具有承上启下的作用。其陵寝建筑中金瓶形的外罗城、九曲御河、龙鳞神道、琼花双龙琉璃影壁和内外明塘等都是明陵中仅见的孤例，尤其是“一陵两冢”的陵寝结构为历代帝王陵墓中绝无仅有，由瑶台相连而成哑铃状的两座隐密的地下玄宫神密莫测，一直为世人称奇。\n显陵中有前后两个茔城，前茔城建于正德十五年（1520年），是朱祐杬死后按藩王规制建兴献王坟时建造的，后茔城为嘉靖18年（1539年）嘉靖皇帝亲自选址、并合葬其父母而建。两宝城之间用很长的平台连接起来称为“瑶台”，且每个茔城都建有一套月牙城、哑巴院和琉璃影壁，并有独立的排水系统，这是显陵的独特之处。\n棱恩门两侧精美的双龙琉璃影壁，是显陵中的另一个看点，花心正面为琼花图案，背面为双龙图案，其做工非常精美。是明代各帝陵中所没有的。显陵中还有一道独特的景观，就是九曲河。九曲河是陵区的主要排水设施，此外它的形态还体现风水理论的完美，与明朝前七个帝陵形成了显著的区别。\n在显陵游玩时，也可以聘请讲解人员，参考费用：50-80元/次。', '湖北-荆门', '0724-4217387', NULL, '8:00-17:30', 12518, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi705863-mingxianling', '1', '3', '4', '3', '4', '5', 2, 49385, 109, 7199, 82690, 147921, 78522, 145218, 217534, 109369, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
