INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (621, '715598', '阜阳生态园', NULL, '安徽 > 阜阳 > 阜阳景点 > 阜阳生态园', '阜阳', '阜阳', 'https://img1.qunarzz.com/travel/d7/1612/a3/a30b492aeb4296b5.jpg;https://img1.qunarzz.com/travel/d7/1612/f7/0d671b5a6e277eb5.jpg;https://img1.qunarzz.com/travel/poi/1803/d7/42459e0fbdb17337.jpg;https://img1.qunarzz.com/travel/poi/1706/13/f6b0d84abda2ad37.jpg', '4.4', '阜阳景点排名第2', '12小时 - 3天', '14', '32.938181', '115.796704', '0', '阜阳生态园座落在阜城西北城乡结合部，古颍州西湖遗址，欧阳修“会老堂”北侧，系农业结构调整，改造治理泉河洼地而建。景区占地1107亩，一期工程2001年7月动工，2002年5月1日开园；二期工程2003年3月开始扩建，2004年5月1日对外开放。\n生态园由十四部分组成。东南部有果树种植示范区主要有葡萄园、梨园、桃园、石榴园、樱桃园、柿园、苹果园、枣园、斑竹园和桂花树、香樟、琵琶树、银杏等稀有树种；中部为水上乐园和垂钓中心，养有日本锦鲤、斑点叉尾鲢、银鲫等珍稀鱼类；北部为动物园有斑马、天鹅、海鸥、孔雀、火烈鸟、鸳鸯、弥猴、骆驼、牦牛、梅花鹿、鸵鸟、黑熊、棕熊、金刚鹦鹉等80余种名贵动物；中北部为文化园、高尔夫球练习场、农业科技示范园；西北部为五牛园和盆景园；西部为热带植物园区，引进20多个国家和地区珍贵树木，其中佛肚树、加拿利海枣、盘根榕树等最为名贵，美丽异木棉为阿根廷国花；西南部为恐龙园，主要展示霸王龙、暴龙、长颈龙等；南部为休闲中心，设有棋牌室、茶楼、餐饮等与水上音乐茶座隔湖而望；中南部为大型游乐园，是游客活动的好天地。', '安徽省阜阳市欧阳修路666号', '0558-2616191', 'http://www.fyssty.com/', '8:00-17:00', 10974, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi715598-fuyangshengtaiyuan', '2', '', '2', '2', '', '4', 9, 33179, 169, 47095, NULL, 48937, 17043, NULL, 165083, 52511, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
