INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (626, '7469068', '一寨两国', NULL, '云南 > 瑞丽 > 瑞丽景点 > 一寨两国', '德宏', '瑞丽', 'https://img1.qunarzz.com/travel/d3/1711/62/cdcbec974f2ed8b5.jpg;https://img1.qunarzz.com/travel/d8/1702/4b/5d280cf179cd77b5.jpg;https://img1.qunarzz.com/travel/d8/1711/35/49272d1b78b0b8b5.jpg;https://img1.qunarzz.com/travel/d3/1705/35/95a49b2ed6e744b5.jpg;https://img1.qunarzz.com/travel/d5/1506/cb/a183ca07ba6dcb.jpg;https://img1.qunarzz.com/travel/d7/1705/de/31e3c80f5b6281b5.jpg', '4.3', '瑞丽景点排名第1', '1小时 - 3小时', '37', '23.961167', '97.80207', '11', '瑞丽市西南约11公里，有一个世界少有的边境人文地理景观小寨--银井。有名的中缅边境71号界碑矗立寨中，国界线从该寨中央蜿蜒划过，是个典型的“一寨两国”边境地区。从瑞丽至弄岛的公路将同一傣族村寨一分为二，中方一侧的称为银井，缅方一侧的称为芒秀。寨中的国境线以竹棚、村道、水沟、土埂为界，因此，中国的瓜藤爬到缅甸的竹篱上去结瓜，缅甸的母鸡跑到中国居民家里生蛋便成了常有的事，边民们每月数十次地穿越神圣的国境线而浑然不觉。寨子里的老百姓语言相通、习俗相同，他们同走一条路，共饮一井水，同赶一场集，和睦相处。榕树在德宏是一道独特的风景线。无论山坝间、村寨里、道路旁都有种植，要么独树成林，要么榕树相连成片。人们视榕树为神树，只种不砍。在德宏榕树在所有树种中占的比重最大。这颗迎客榕并不算大，在德宏州内排行老五，但它树型奇特，左右前后姿势多变，于是为众多影视专家所青睐，如如《边寨烽火》、《孔雀公主》、《带手铐的旅客》、《西游记》等十多部影视片都在这里拍摄过外景，著名影星王晓棠在这树上打过秋千，刘晓庆在树林中流连忘返。 它的奇妙、秀丽一定会让你惊叹不已。', '德宏傣族景颇族自治州瑞丽市中缅边境南段71号界桩侧', '0692-4930777', NULL, '8:00-19:00', 10287, NULL, NULL, NULL, '半天', 'http://travel.qunar.com/p-oi7469068-yizhailiangguo', '2', '3', '4', '3', '5', '4', 1, 43180, 177, 17030, 67585, 116398, 84494, 130202, 96948, 42903, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
