INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (630, '8666955', '湟鱼家园', NULL, '青海 > 刚察 > 刚察景点 > 湟鱼家园', '海北', '刚察', 'https://img1.qunarzz.com/travel/d1/1703/ae/cc7dc503e3baaeb5.jpg;https://img1.qunarzz.com/travel/d9/1703/71/75ff8c2378a5a4b5.jpg;https://img1.qunarzz.com/travel/d1/1703/7b/b0f9aed63f96b9b5.jpg;https://img1.qunarzz.com/travel/d6/1703/80/20d4db596c9cfb5.jpg;https://img1.qunarzz.com/travel/d2/1703/52/70164987576fa8b5.jpg;https://img1.qunarzz.com/travel/d2/1703/8d/a55466b4a13ec3b5.jpg', '4', '刚察景点排名第8', NULL, '12', '37.325112', '100.140085', '1', '刚察沙柳河畔，山花烂漫、鸟翔鱼跃、景色迷人，它是中国唯一观赏裸鲤洄游奇观的好去处。这里有祥和塔生态区、有情人崖、有观鱼放生区、有祈福和感恩雕塑、有青海湖精灵--青海裸鲤（俗称湟鱼）。每年的六、七月沙柳河畔的湟鱼家园在夕阳的斜照下更多了一份迷人景象、湟鱼成群洄游到沙柳河延续生命恰似千帆竞发在沙柳河里形成“半河清水半河鱼”的奇特景观和“鱼丁兴旺”的景象让人叹为观止。', NULL, NULL, NULL, NULL, 2443, '每年的6月中旬到8月中旬（农历的端午节到立秋）是湟鱼的最佳观赏时期。', NULL, NULL, NULL, 'http://travel.qunar.com/p-oi8666955-huangyujiayuan', '', '', '', '', '', '1', 3, 46431, 79, NULL, NULL, NULL, NULL, NULL, 5306, 5671, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
