INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (635, '710039', '大角湾景区', NULL, '广东 > 阳江 > 阳江景点 > 大角湾景区', '阳江', '阳江', 'https://img1.qunarzz.com/travel/d2/1606/da/b0d1d53f3602ee9a.jpg;https://img1.qunarzz.com/travel/d4/1606/5f/33c5eb1155a3a9a.jpg;https://img1.qunarzz.com/travel/d6/1606/a2/ca3a706b985f39a.jpg;https://img1.qunarzz.com/travel/d0/1606/ca/238fdbbd76c9079a.jpg;https://img1.qunarzz.com/travel/d3/1606/b0/734ea5c98975d29a.jpg;https://img1.qunarzz.com/travel/d0/1606/65/a3ed9362190bde9a.jpg', '4.4', '阳江景点排名第13', '12小时', '48', '21.578157', '111.880444', '46', '大角湾风景名胜区位于广东阳江市海陵岛西南端，三面群峰拱护，面向浩瀚南海，沙滩长2.5公里，宽100米，因状似牛角，故名“大角湾”。这里地处南亚热带，全年日照时间长，年平均气温22.8℃，水温23.5℃，降雨量1816毫米，年晴天数310天，四季气侯宜人，素以阳光灿烂明媚，沙质均匀松软，海水清澈纯净，空气清新纯洁而著称，各类质素均达国际一类（级）标准，又因沙滩宽阔平坦，海浪柔软适中，无鲨鱼出没而成为名扬海内外的天然海水浴场。', '广东省阳江市江城区闸陂镇海滨路38号', '0662-3887080、0662-3888240', 'http://www.djwtour.com', '7:00-18:00', 19655, NULL, NULL, 'TIPS：\n1、 在这里冲浪很爽快。\n2、大角湾附近有一个旧市场卖海鲜，买了海鲜可以到附近的小店加工，7元每斤。\n3、 大角湾里面没有免费换衣服的地方。', NULL, 'http://travel.qunar.com/p-oi710039-dajiaowanjingqu', '', '3', '3', '3', '4', '1', 5, 49140, 74, NULL, 32695, 36580, 59901, 78921, 16225, 5250, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (636, '704392', '坎儿井民俗园', NULL, '新疆 > 吐鲁番 > 吐鲁番景点 > 坎儿井民俗园', '吐鲁番', '吐鲁番', 'https://img1.qunarzz.com/travel/poi/201408/06/3cf8f9f0ff8d6d98c8d65eac.jpg;https://img1.qunarzz.com/travel/d1/1804/a6/6a3f2429770779b5.jpg;https://img1.qunarzz.com/travel/poi/1803/ae/07f49f4cdd436537.jpg;https://img1.qunarzz.com/travel/d4/1803/d6/36811e456ad03db5.jpg;https://img1.qunarzz.com/travel/d1/1803/14/3243607b2fbe40b5.jpg;https://img1.qunarzz.com/travel/d1/1803/83/43721e54b28833b5.jpg', '4', '吐鲁番景点排名第13', '3小时', '71', '42.9588138376', '89.1551274194', '52', '吐鲁番坎儿井民俗园是位于吐鲁番市牙儿乡，距市中心仅三公里。民俗园包括:：坎儿井,、坎儿井博物馆、民俗街、民居宾馆、葡萄园等。它将具有悠久历史的坎儿井和最具有民族特色的庭院式民居宾馆融为一体，既能让人们参观有400多年历史的坎儿井及其历史发展过程，又能了解维吾尔族民俗情况，在极具民族特色的维吾尔族式民居宾馆中住宿，是当今中国最具民族特色的集参观，观赏、购物、渡假为一体的旅游景点.。走进坎儿井民俗园，处处都是一副副如诗如歌的画面，碧绿的棚架，弥漫着葡萄的清香；翠绿的果园，结满了丰收的喜悦；具有新疆各地维吾尔建筑风格的民居宾馆，有50余间舒适整洁的标准客房，民族歌舞餐厅可同时接纳300余人，您在品尝淳正的民族风味小吃的同时，还能欣赏到浓郁风情的维吾尔歌舞表演；步入坎儿井博物馆,您可以通过最生动，最直观的方式，感受这凝聚着勤劳与智慧的人间奇迹，各种现代化服务设施一应俱全,使您忘记身处异域,充份满足您多方位的需求。', '中国新疆吐鲁番市新城路新城西门村888号', '0995-8557381', NULL, '旺季（4月21日-10月20日）8:00-21:00 ；淡季（10月21日-次年4月20日）10:00-18:30', 14488, NULL, NULL, NULL, '1小时', 'http://travel.qunar.com/p-oi704392-kanerjingminsuyuan', '2', '4', '5', '4', '3', '5', 2, 20328, 73, 48241, 171027, 263197, 60510, 59523, 102409, 105453, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
