INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (642, '715397', '红军会师楼', NULL, '甘肃 > 会宁 > 会宁景点 > 红军会师楼', '白银', '会宁', 'https://img1.qunarzz.com/travel/d2/1506/c2/d99f235a8a9eea.jpg;https://img1.qunarzz.com/travel/d8/1703/ee/abb2b8872af06cb5.jpg;https://img1.qunarzz.com/travel/d7/1509/d3/c9b6544b247687.jpg;https://img1.qunarzz.com/travel/d5/1509/7f/7a59118220d95.jpg;https://img1.qunarzz.com/travel/d8/1703/a9/46b125f4245d70b5.jpg;https://img1.qunarzz.com/travel/d5/1509/d3/8b7402594d02ca.jpg', '4.5', '会宁景点排名第1', '2小时 - 3小时', '5', '35.6964889092', '105.0587819907', '0', '红军会宁会师旧址又名会师园，位于会宁县南侧会师北路上，是纪念红军三大主力第一、二、四方面军长征会师的纪念园区。园内有多座纪念建筑，还有纪念馆和陈列馆展示众多的革命文物，是缅怀革命先烈、了解长征史诗故事的红色旅游胜地。另外园内的纪念建筑造型独特，还有一些古老的历史建筑，可以来此参观摄影。\n会师园面积不大，长宽大约都在200米左右，2-3小时可以游览完毕。园区有几个入口，从各个方向均可进入。景区的中轴线呈西北-东南走向，中心有一座广场，周围种植了很多树木和花朵，环境优美。\n景区的中心处是会师纪念塔，这座塔整个呈红色，一共11层。塔身的造型非常独特，下面九层是3座合抱的塔，到第十层时合并为一座，象征着三军在此会师的历史。这座塔也是会宁县的地标建筑，可以与之拍照，还可以登塔远望，视野开阔。\n位于会师塔东南方向的广场对面是红军长征胜利纪念馆，是我国最大的反映长征历史的纪念馆。纪念馆共有三层，馆内有很多大型的雕塑、图片、视频展示等，更为重要的是这里有近千件珍贵的革命文物，是缅怀红军长征英烈的胜地。\n园区内还有一些历史建筑可以参观拍照，在纪念塔西北一侧的则是红军会师联欢会会址，这里原来是县城的文庙，是一座明代的古建筑，现在保存完好。而纪念塔西南边的古建筑则是会师楼，也是一座明代建筑，原来是县城的西城楼，现在保留了两侧的城墙和门楼，非常壮观。\n除此之外，园内还有革命文物陈列馆、将帅碑林、纪念亭、牌楼等仿古建筑或陈列馆，可以一一参观，了解关于长征的故事，缅怀英烈们为信仰奋战的那一段历史。', '会宁县会师镇会师路7号　', '0943-3222565', NULL, '8:30-17:30', 17542, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi715397-hongjunhuishilou', '', '', '', '1', '', '1', 8, 24642, 83, NULL, NULL, NULL, 18914, NULL, 8645, 6085, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (643, '10003194', '文昌阁', NULL, '甘肃 > 嘉峪关 > 嘉峪关景点 > 文昌阁', '嘉峪关', '嘉峪关', 'https://img1.qunarzz.com/travel/poi/1712/e0/246505adf152dd37.jpg', '--', '嘉峪关景点排名第32', '0.2小时 - 0.5小时', '0', '39.8073806763', '98.2306828308', '0', '文昌阁始建于明代，重建于清道光二年（公元1822年）。楼阁为两层两檐歇山顶式建筑，底层两边为单间铺房，四周立红漆明柱18根，形成回廊。内为面宽三间、进深二间的官厅。四面装有花格门窗，上部绘制山水人物彩画80余幅。此阁在明清时为文人墨客会友、吟诗作画、读书的场所。到了清代末年成为文官办公的地方。', '嘉峪关市峪泉镇嘉峪关文物景区内', NULL, NULL, '8:30-18:30', 15617, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi10003194-wenchangge', '', '4', '4', '3', '', '1', 5, 35781, 158, NULL, 64359, 85337, 37684, NULL, 24809, 5984, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (644, '10095313', '孔雀谷', NULL, '云南 > 芒市 > 芒市景点 > 孔雀谷', '德宏', '芒市', 'https://img1.qunarzz.com/travel/poi/1712/7c/27904e768d67e737.jpg', '--', '芒市景点排名第13', NULL, '0', '24.5427100479', '98.6443424516', '0', '芒市孔雀谷森林公园是德宏钻石人间旅游投资开发有限公司按国家级森林公园标准投资打造的一个高端旅游景区，位于云南省德宏州芒市南天门放马桥。总占地面积15003亩，总投资5亿，分为五期建设。一期由两大主题和五个版块构成：第一个主题是孔雀，孔雀系列元素将申报世界吉尼斯纪录，重点突出德宏是美丽的孔雀之乡这张名片；第二个主题是原始森林生态观光游，景区植被类型丰富多样，树木造型奇特（孔雀树、迎客树、许愿树、长寿树等），可观赏性高，古树比比皆是，有的需五六人方可环抱，森林覆盖率高达到98.6％，负氧离子平均达到10000个/cm³，最高时达15000个/cm³ 以上，因负氧离子丰富，被誉为天然的大氧吧。\n五个版块分别是滇西抗战博物馆，由博物馆、抗战广场、观景台等构成；许愿树，一棵需五人合抱的古榕树，其形状因长得像孔雀开屏，故谓之名曰许愿树；三角梅主题花海，总占地158亩，花海最大亮点是一只长40米高10米的大孔雀，这也是景区内几大核心景点之一；孔雀园总占地40亩，环线观光游览孔雀表演、孔雀放飞以及孔雀喂养等，数千只孔雀全程为您呈现不一样的视觉美；540亩生态茶园：环线观光游览，集观景台、茶叶采摘、制作、购物等为一体，配套项目有房车露营和帐篷露营等。二三四五期分别是傣族文化园、龙型滑道、冰雪王国、阿昌文化园、景颇文化园和景颇山官府、傈僳文化园、德昂文化园。', '德宏州芒市南天门放马桥', NULL, NULL, '9:00-17:00', 4959, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi10095313-kongquegu', '', '', '2', '1', '5', '5', 2, 24981, 182, NULL, NULL, 10123, 15446, 157559, 373123, 93760, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
