INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (651, '10004883', '华蓥山石林', NULL, '四川 > 华蓥 > 华蓥景点 > 华蓥山石林', '广安', '华蓥', 'https://img1.qunarzz.com/travel/poi/1712/9e/23712231b1ea9f37.jpg', '--', '华蓥景点排名第7', '1小时', '0', '30.313783', '106.813499', '0', '华蓥山石林面积21平方公里，以秀丽的喀斯特石林、天坑、溶洞景观为代表，以两林（石林、杉林）、两海（云海、竹海）、两美景（积雪、日出）及石奇树怪而闻名。这里的喀斯特景观是1.5亿年前的地壳运动时，由于石灰石受新构造运动和华蓥山大断层的切割作用，地层大面积抬升，海水降退，风化剥蚀，岩层被溶化、冲刷而形成。\n景区既有秀峰怪石、天坑溶洞，又有佛教古刹、茂竹修林以及华蓥山游击队遗址，自然风光与人文景观交相辉映，其特点可概括为“峰奇、石怪、山绿、谷幽”，呈现出“春绿、夏荫、秋红、冬白”的四季变化。当地百姓称这片石林为“八百罗汉”，并辅以美丽的传说。石林之中，各种肖像天然成趣，“美妻审夫”、“千年一吻”、“骏马奔驰”、“秀才吟诗”、“石猴摘桃”、“金鸡报晓”、“大鹏展翅”、“拱手拜佛”、“雄兵八千”、“情侣谴绻”等，给人以美感和遐想。\n华蓥山石林，有别于云南石林的高大奇险和桂林石林的雄伟暴露，她将秀、雅、美的玲珑丰姿隐藏在丛林峻岭之中。石林由溶芽、溶柱、溶屏及各种不规则溶石组成，石在林中生、树在石中长、树缠石、石顾藤、石树相依等自然景观随处可见；石树顾盼、藤树生花、逶迤组团、形成了数百个大大小小的“天然盆景”，漫步其中，仿佛就像走进了“天然大盆景园”一般，难怪有“中国石林盆景王国”之美称，更增添其清秀、幽雅、隐迷、绮丽之美感。', '广安市华蓥市瓦店乡高顶村华蓥山', '0826-4330178', NULL, '8:00-17:00', 2043, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi10004883-huayingshanshilin', '4', '2', '', '2', '3', '2', 6, 34948, 91, 120750, 27695, NULL, 39046, 84928, 48561, 13367, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
