INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (658, '10024166', '李家龙宫', NULL, '甘肃 > 陇西 > 陇西景点 > 李家龙宫', '定西', '陇西', 'https://img1.qunarzz.com/travel/poi/1712/88/85ea2f67adbd5137.jpg', '--', '陇西景点排名第3', '1小时', '0', '35.018363', '104.65352', '0', '位于陇西县南安乡一心村庙儿巷的李家龙宫始是天下李氏族人敦宗睦族、祭祀先祖的宗祠，因唐太宗李世民御笔亲书“李家龙宫”而闻名海内外，是研究陇西李氏文化遗址遗迹的重要载体和标志性建筑之一，也是陇西唯一保存下来的一处古建筑群。\n清顺治五年李家龙宫毁于兵灾，仅留“北天第一门”牌坊。康熙元年重建，康熙三十七年吕培高任知县时，曾于任期内在“北天第一门”上悬有“仰弥高”竖匾额，同治五年陇西城陷，李家龙宫再遭劫难。清末及民国初年又陆续增建维修一新，规模虽远不如前，却也山水相依，楼殿参差，别有天地。\n2003年9月，李家龙宫正式向游人开放，先后接待了海内外80多个李氏宗亲组织和国内29个省、市、区的李氏组织及李氏族人和中外游客来陇西旅游观光、寻根祭祖。2006年，李家龙宫先后被定西市委、甘肃省委相继命名为“定西市爱国主义教育基地”、“甘肃省爱国主义教育基地”。 2011年，甘肃省人民政府宣布李家龙宫为省级文物保护单位。2013年，陇西李家龙宫成为甘肃省丝绸之路旅游观光带11个核心景区之一。', '定西市陇西县南安乡一心村庙儿巷', NULL, NULL, '8:30-17:30', 3192, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi10024166-lijialonggong', '2', '', '2', '', '', '4', 5, 27744, 179, 27745, NULL, 42089, NULL, NULL, 72627, 54932, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
