INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (661, '708559', '扎什伦布寺', 'Tashilhunpo Monastery', '西藏 > 日喀则 > 日喀则景点 > 扎什伦布寺', '日喀则', '日喀则', 'https://img1.qunarzz.com/travel/d2/1703/ca/eb9e3328b7b773b5.jpg;https://img1.qunarzz.com/travel/d6/1704/e2/582ac51c5e1c0bb5.jpg;https://img1.qunarzz.com/travel/d2/1509/35/714ce1565545ad.jpg;https://img1.qunarzz.com/travel/d9/1603/6d/799671feb1be4f7.jpg;https://img1.qunarzz.com/travel/d3/1701/42/dd44f2cd4a5dddb5.jpg;https://img1.qunarzz.com/travel/d5/1703/8d/691d77d5d270f5b5.jpg', '4.6', '日喀则景点排名第1', '4小时 - 6小时', '244', '29.2740298417', '88.874160526', '238', '扎什伦布寺分为宫殿(班禅拉丈)、勘布会议(后藏地方政府最高机关)、班祥灵塔殿、经学院4部分。大经堂可容纳2000喇嘛念经，弥勒佛大殿高近三十米，建筑面积近八百平方米，殿室分为莲花宝座殿、腰部殿、胸部殿、面部殿和冠部殿五大层。1446年，宗喀巴的第八代弟子、一世达赖根敦珠巴为纪念去世的经师希饶僧格，聘请西藏、尼泊尔工匠在日喀则精制了一尊2.7米高的释迦牟尼镀金铜像。为安放此像，根敦珠巴在帕竹政权的资助下，于公元1447年9月开始动工修建寺院，历时12年将所造之像置于该寺净室内。开始寺院定名为“岗坚典培”，意为雪域兴佛寺。后来根敦珠巴将其更名为“扎什伦布巴吉德经钦却唐皆南巴杰娃林”，意为吉祥宏固资丰福聚殊胜诸方州，简称“扎什伦布寺”，取“吉祥须弥”之意。', '西藏自治区日喀则市几吉朗卡路', '0892-8825220', 'http://www.eyub.com/news/315.html', '9：00-17：00\nTips\n1、中午12：00-14：00佛殿不开放。\n2、一般参观需要3小时左右。', 16321, '四季皆宜，夏季更好。', NULL, '1、扎寺的展佛台，西藏黄教各大寺院绝无仅有，其为扎寺一大特点。\n2、这里的壁画很有特色，是佛教艺术的杰作。\n3、在寺庙中可以看到许多放生狗、放生鸡等，与人们和平相处。\n4、注意高原反应的预防，建议服用携氧片或上高原前一周开始服用红景天。', NULL, 'http://travel.qunar.com/p-oi708559-zhashenlunbusi', '2', '3', '', '2', '3', '3', 6, 48752, 187, 28786, 72944, NULL, 43669, 71513, 58141, 22949, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
