INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (679, '706343', '天河山', NULL, '河北 > 邢台县 > 邢台县景点 > 天河山', '邢台', '邢台县', 'https://img1.qunarzz.com/travel/d6/1611/77/3cf7a7f20c9079b5.jpg;https://img1.qunarzz.com/travel/d0/1611/fc/f4ee1d8034b82cb5.jpg;https://img1.qunarzz.com/travel/d2/1611/ce/09b57b9b3466cbb5.jpg;https://img1.qunarzz.com/travel/d4/1504/74/bd5f2639676589.jpg;https://img1.qunarzz.com/travel/d4/1611/af/69b1e7dd3bc02db5.jpg;https://img1.qunarzz.com/travel/d8/1504/2e/7599b0ab3628e1.jpg', '4.9', '邢台县景点排名第2', '2小时 - 3天', '9', '37.085189', '113.814913', '1', '这里文化底蕴丰厚，早在新石器时代，天河山就有人类居住，春秋时期孔夫子曾到此游学，“夫子岩”因此得名，这里广泛流传牛郎织女的故事并拥有大量的文化遗存，经多位专家考证，这里就是牛郎织女故事的原生地。经国家工商局注册，成为中国唯一的爱情山。抗日战争时期，这里是八路军主要根据地之一，129师医院、冀南银行（中国人民银行前身）等革命遗址，至今保存完好。\n这里是千里太行一颗闪亮的明珠，是新被开发的处女地，主要景点有：天河湖、银河谷、摩诃池、蝶仙谷、碧莲池、情人谷、牛郎庄、仙女庙、鹊桥、山顶草原等数十处景点。天河山拥有华北最大的峡谷漂流基地，中国情侣第一漂。\n天河山——爱情之山、浪漫之山、欢乐之谷、人间仙境。', '邢台县白岸乡清泉村', '0319-2251188', 'http://www.tianheshan.com/', '7:00-19:00', 17934, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi706343-tianheshan', '4', '2', '4', '', '2', '1', 1, 37742, 183, 60900, 23337, 115245, NULL, 29764, 16477, 6626, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (680, '704489', '荣县大佛', NULL, '四川 > 荣县 > 荣县景点 > 荣县大佛', '自贡', '荣县', 'https://img1.qunarzz.com/travel/poi/1803/81/7e912d9c6da0f937.jpg;https://img1.qunarzz.com/travel/d0/1609/ff/6f0c0d9180fb25b5.jpg;https://img1.qunarzz.com/travel/d3/1609/a1/4b5da0020a61d8b5.jpg;https://img1.qunarzz.com/travel/d4/1609/d6/a6d2cada37dcb9b5.jpg;https://img1.qunarzz.com/travel/d9/1609/dc/5044cabdc99567b5.jpg;https://img1.qunarzz.com/travel/d4/1609/c4/6d2b0a645f5250b5.jpg', '4.7', '荣县景点排名第1', '1小时', '4', '29.4606626694', '104.4375329606', '1', '大佛寺坐落在四川省荣县城郊大佛山（亦称真如岩）山麓，山高海拔414米。半山之上，有一尊巍峨的石刻如来坐佛佛像，世称荣县大佛。拂身通高36.67米，头长8.76米，肩宽12.67米，膝高12米，脚宽3.5米，仅次于乐山弥勒大佛，为中国第二石刻大佛。\n大佛寺的啸台崖壁还有唐宋摩崖造像，西方极乐世界，十八罗汉，其中“罗汉群龛”引入注目龛高3.4米，宽4.6米，深0.46米，内刻六尊罗汉，其中三尊头部已毁。六劈罗汉中间浮雕一株桃树，枝上悬一仙桃。靠仙桃的罗汉，一尊双手交叉乎放在胸前，悠闲自在：另一尊面朝仙桃，似无动于衷。造像表现了他们超脱凡尘，不为利禄所动的神态。这种布局的雕刻为全国罕见，有一定研究价值，已为《中国美术全集四川石窟雕塑》收集。“啸台总一郡之胜”。唐宋时在此建有栋字轩槛，北宋建“一胜亭”，供人凭临怀古。若登台极目，东可望巍巍大佛，西可俯幽深龙洞。唐宋以来，这里就是人们乐于游览之地。历史上的楼台亭阁，栋宇轩槛早毁。1982年，根据古籍记载，揣摩宋代“一胜亭”旧址，在啸台一侧重建“一胜亭”，供人歇足眺望，凭吊古人。', '自贡市荣县旭阳镇大佛寺1号', '0813-6161156', NULL, '9:00-17:30', 2094, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi704489-rongxiandafo', '2', '1', '', '', '4', '5', 3, 49687, 196, 23490, 10385, NULL, NULL, 163226, 143229, 100282, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
