INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (684, '3864498', '沙湾县森林公园', NULL, '新疆 > 沙湾 > 沙湾景点 > 沙湾县森林公园', '塔城', '沙湾', NULL, '4', '沙湾景点排名第1', NULL, '2', '44.33434', '85.596661', NULL, '三道河子森林公园，位于沙湾县城西2公里处，占地面积800亩，公园周围被3800多亩水果基地环绕。优美的环境，旖旎的风光，使这里成为沙湾县各旅游景点中最具特色的生态型城市园林景区。景区内树木参天，绿荫簇拥，清流环绕，花草繁盛。沿着婉蜒的林间小道进入公园深处，只见路边白杨参天，桦林森森，各种树木参差相间，郁郁苍苍，满目苍翠，微风吹来，树影婆娑起舞，树声阵阵如涛。森林公园中遍生疣枝桦、银灰杨、山杨等高大乔木，树下灌木丛生，水渠、溪流穿梭其中，潺潺流水声不绝于耳。桃树、李树、苹果树、梨树、红枣树、枸杞、无花果树等300多亩果林，使公园变成果树世界。林间铺设了平坦的水泥路，建有各种造型的石桥，曲径通幽；各类亭台楼阁，掩映在绿荫深处，红墙楼阁点缀于翠绿之中，令人赏心悦目。近2公里长的葡萄长廊，犹如绿色隧道，但见葡萄藤枝蔓缠绕，上面挂满一串串翡翠般晶莹透亮的葡萄，浓荫蔽日，铺绿叠翠，泉流溪涌，令人陶醉。景区内专设的“民俗村”，内设别具风情和民俗的蒙古包，供游人留宿、就餐、小憩。在这里，游人不仅有机会品尝哈萨克人的奶茶、手抓肉，更能欣赏到地道的哈萨克族舞蹈和冬不拉弹唱。', '新疆塔城地区沙湾县城西2公里', '0901-2323', NULL, '全天', 3675, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi3864498-shawanxiansenlingongyuan', '4', '3', '', '3', '4', '3', 5, 25588, 86, 62247, 72075, NULL, 62216, 43625, 49608, 27907, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
