INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (686, '7585836', '小白鹭海滨度假村', NULL, '福建 > 福鼎 > 福鼎景点 > 小白鹭海滨度假村', '宁德', '福鼎', 'https://img1.qunarzz.com/travel/poi/1412/c4/c4f17cce660c822fcdb.jpg;https://img1.qunarzz.com/travel/poi/1803/f7/ac4b9792b84df237.jpg;https://img1.qunarzz.com/travel/poi/1605/11/d721952c241a09f7.jpg', '5', '福鼎景点排名第4', '2小时 - 4小时', '1', '27.14112', '120.389416', '0', '位于福建东北沿海沙埕港以南小白鹭海湾的小白鹭海滨度假村，是以“渔文化民俗游”及“海滨沙滩休闲度假”为主体的海滨休闲度假区，也是国家重点风景名胜区太姥山的“山、海、川、岛”四大旅游休闲基地之一。是所谓的“一片黄沙横碧海，几行白鹭上青天”。\n海湾内的沙滩，滩宽沙软，坡缓浪静，是优质海滩浴场，两岸草木葱郁、奇石峭壁。登山眺海，烟波浩荡，仙境飘渺；凭海观山，奇峰怪石，绮丽多姿；品尝鲜活海味，朵颐生香。度假村不仅有海滨浴场、旅游宾馆、渔村酒店、沙滩酒吧、山庄木屋、海滨别墅、独柱草亭、绿色广场、停车场，还有三柱蹦极、旋转太空气球、休闲渔船、摩托艇、游艇、水上三轮车、沙滩摩托车、水上蹦床等海上娱乐项目，涵盖了吃、住、行、游、购、娱的旅游要素，具备了海滨旅游的五大要点——海洋、沙滩、空气、阳光、绿色。', '宁德市福鼎市小白鹭海湾', '0593-7286111', NULL, '8:00-19:00', 18851, NULL, NULL, '为保证取票、入园顺利，预订时请务必填写真实姓名、手机号码等信息。注意天气情况', NULL, 'http://travel.qunar.com/p-oi7585836-xiaobailuhaibindujiacun', '', '1', '', '2', '2', '1', 4, 33279, 207, NULL, 22710, NULL, 22340, 17298, 11474, 6555, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
