INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (692, '10007399', '毗卢洞文物景区', NULL, '四川 > 安岳 > 安岳景点 > 毗卢洞文物景区', '资阳', '安岳', 'https://img1.qunarzz.com/travel/poi/1712/42/783805a56cdef937.jpg', '--', '安岳景点排名第3', NULL, '0', '29.929914', '105.631275', '0', '毗卢洞，位于安岳县城东南的石羊镇赤云片区油坪村塔子山上，距县城45公里。安岳至大足的公路横穿塔子山脚。这里，山峦叠翠，岩石峭立，毗卢洞就凿在这些磊磊秀石之中。毗卢洞其实是：毗卢洞、幽居洞、千佛洞和观音堂的总称，现存摩崖石刻造像465尊，碑刻题记32处。\n另外，毗卢洞、幽居洞雕刻的密宗第五代祖师柳本尊造像也别具特色。特别是在高6.6米，宽14米，深4.5米的毗卢洞中，雕刻的柳本尊的“十炼修行图”构图严谨，造像生动，通俗易懂，堪称精品。两侧还刻有神态威严的执斧、仗剑的护法金刚。来到洞里，大有阵阵寒气袭来之感，令你对佛法的神圣肃然起敬。\n毗卢洞石刻造像是宋代佛教艺术的结晶，具有很高的历史研究价值和游览观赏价值。2001年7月，国务院公布为第五批全国重点文物保护单位。', '资阳市安岳县毗卢洞石刻造像附近', NULL, NULL, NULL, 15095, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi10007399-piludongwenwujingqu', '', '', '', '', '4', '1', 8, 39157, 147, NULL, NULL, NULL, NULL, 168451, 12830, 5715, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
