INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (694, '702874', '姥山岛', NULL, '安徽 > 巢湖 > 巢湖景点 > 姥山岛', '巢湖', '巢湖', 'https://img1.qunarzz.com/travel/d2/1706/61/91bf6bf10ff8beb5.jpg;https://img1.qunarzz.com/travel/d7/1706/53/297278419b965db5.jpg;https://img1.qunarzz.com/travel/poi/1409/09/9e0227dc0f2860c1ffffffffc8d65eac.jpg;https://img1.qunarzz.com/travel/d4/1706/51/a918ee029614d8b5.jpg;https://img1.qunarzz.com/travel/d8/1706/94/847fd982265e96b5.jpg;https://img1.qunarzz.com/travel/d9/1501/a6/2384906da336a4.jpg', '4.3', '巢湖景点排名第2', '1天 - 3天', '3', '31.5698943511', '117.4518370348', '3', '姥山岛人文景观丰富。南麓，有一天然避风良港，旧称“南塘”，系元末抗元将领俞通海、廖永安等率水师屯寨姥山时修。山腰望湖而建的圣妃庙，祭祀主湖女神，始建于晋朝。如今圣妃庙依然香客如云，热闹非凡。岛麓还有一华藏净寺，始建于三国之后两晋之初，约有一千八百年历史，香火一度兴盛百里巢湖。\n山巅的文峰塔建于崇祯四年，登塔凭栏远眺，但见水天一色，群鸟飞翔，令人心旷神怡。离塔不远处，有座圣姥庙，春秋祭祀焦姥。红楹青瓦掩映在苍松翠柏之间。塔高七层，51米，133级，塔系条石青砖结构，层层飞檐走角，八角对着八方，角角装有铜铃，外观雄伟，结构精巧。塔身由外壁、回廊、塔心三部分组成。人入塔内，门梯交错，左拐右旋，乐趣无穷。人立塔上，只觉风声呼啸，铜铃叮当，如立云端之上。倚窗远望，烟波浩淼，渔帆点点；近观脚下，姑、鞋二礁如在雾中。塔刹上镶嵌有李鸿章全像。李鸿章发迹前，曾得益于恩师曾国藩的指点，集结淮军在湖上操练，并留下了一首气势磅礴的七言绝句：“巢湖好比砚中波，手把孤山当墨磨。姥山塔如羊毫笔，够写青天八行书。”', '安徽巢湖市居巢区中庙镇', '0551-88578318', 'http://www.chzmly.com/', '8:00-17:00', 8406, '四季皆宜', NULL, NULL, NULL, 'http://travel.qunar.com/p-oi702874-laoshandao', '', '4', '1', '1', '3', '1', 1, 34742, 170, NULL, 48288, 21352, 17821, 99247, 24726, 5912, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
