INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (702, '707738', '云龙山', NULL, '江苏 > 徐州 > 徐州景点 > 云龙山', '徐州', '徐州', 'https://img1.qunarzz.com/travel/d2/1608/3b/fe2de6e77ef94cb5.jpg;https://img1.qunarzz.com/travel/d6/1608/e8/24f91db1e5a83db5.jpg;https://img1.qunarzz.com/travel/d5/1705/8c/ac70bcc8d5c45cb5.jpg;https://img1.qunarzz.com/travel/d4/1702/d3/8cc66a58bc7092b5.jpg;https://img1.qunarzz.com/travel/d1/1506/79/dcea98541819e8.jpg;https://img1.qunarzz.com/travel/poi/201403/14/d0dc45a03e94a7b5ddb12cfb.jpg', '4.4', '徐州景点排名第8', '2小时 - 3小时', '39', '34.241519', '117.182793', '15', '云龙山山形蜿蜒如龙，因山上常有云雾缭绕而得名。云龙山虽然海拔只有142米，但文物古迹众多，历来是苏北的名胜。宋苏东坡在徐为太守时，常登山览胜，醉卧山石。他写并书《放鹤亭记》碑文现存山中。\n从北魏至民国期间，山上留有北魏时代的大石佛，唐宋摩崖石刻，宋代的放鹤亭、招鹤亭、饮鹤泉、张山人旧居；明代的兴化禅寺、清代的大士岩、山西会馆、船厅、御碑亭、碑廊等历史文物古迹。现在又修复和新建了幽邃轩、洞天小庐、跨云阁、云龙书院、杏花村、观景台、同心台等一大批历史景点。', '徐州市王陵路', '0516-83868109;0516-83865118', NULL, '6:00-21:00', 3431, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi707738-yunlongshan', '', '3', '', '5', '4', '3', 6, 22172, 72, NULL, 71149, NULL, 162723, 183135, 48045, 23399, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
