INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (707, '10019172', '福塔公园', NULL, '云南 > 楚雄州 > 楚雄州景点 > 福塔公园', '楚雄州', '楚雄州', 'https://img1.qunarzz.com/travel/poi/1712/b4/fd27893e1bd3a437.jpg', '--', '楚雄州景点排名第9', '1小时 - 2小时', '0', '25.0509738922', '101.57365386', '0', '福塔公园位于楚雄市鹿城东山，由楚雄州政协动员社会捐资。在明代所建（后被毁）古塔遗址建造。为八角九层楼阁式钢混结构塔，高层建筑59米，建筑面积1217平方米。是中国历史上第一座以多种艺术表现形式展示中华福文化深厚内涵的塔，也是楚雄最主要的标志性景观之一。\n楚雄福塔广集福之内涵，一层外壁是红岩雕刻的青龙、白虎、朱雀、玄武四灵图案和唐太宗、宋徽宗、清高宗、清宣宗手书福字。塔内每层展示福的一个主题，一层财源茂盛、二层事业兴旺、三层学绩长进、四层家庭和睦、五层子孙成才、六层平安吉祥、七层从善积德、八层健康永驻、九层延年益寿。楚雄福塔将中国古代自甲骨文、金文、隶书、楷书、行书、草书，以及彝文、东巴文、满文、蒙古文、朝鲜文、维吾尔文、藏文、水族文字、傣族文字等各少数民族文字的“福”文字中有关“福”字的精品荟萃于一塔之中。塔内塔外，塔上塔下，共有九千九百九十九个福字，古时向有九九归一之说，九九归于一个“福”字。', '楚雄彝族自治州楚雄市', NULL, NULL, '全天开放', 1114, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi10019172-futagongyuan', '3', '', '5', '1', '1', '4', 1, 38958, 119, 45037, NULL, 125973, 22034, 19926, 75012, 48050, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
