INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (709, '716455', '东胡休闲娱乐中心', NULL, '安徽 > 铜陵 > 铜陵景点 > 东胡休闲娱乐中心', '铜陵', '铜陵', NULL, '--', '铜陵景点排名第4', NULL, '0', '30.9420236561', '117.8080882642', NULL, '铜陵市地处长江三角洲经济开发区腹地，北倚长江，南连黄山、九华山，东接芜湖，西接古城安庆，自然地理条件得天独厚。铜陵市东湖休闲娱乐中心地处铜陵市沿江快车公路北侧，离市区12公里，交通十分便利。东湖休闲娱乐中心由铜陵有色金属（集团）公司投资。该“中心”整个区域占地120.8公顷，其中休闲娱乐为23.4公顷，花卉苗圃9.9公顷，养殖基地为43.5公顷，“新、特”产品种植面积为25.1公顷。对外营业的项目已有卡丁车娱乐场、跑马场、野外射箭场、碰碰船、水上餐厅、网球场、垂钓中心和拥有30间客房的“东湖宾馆”。尚有高尔夫球馆、健身娱乐中心、入口喷水塔，游泳池等已完成主体工程，待完善竣工。中心分为东区和北区，东区是水产养殖和“新、特”农产品种植基地，生产的产品有圣女红樱果、茶树菇、优质莲藕，水产品养殖年总产量可达12万公斤，养殖品种除四大家鱼外，还有大型热带鱼类淡水白鲳、罗非鱼等；北区主要是绿化苗圃和大型综合性现代化休闲娱乐项目区。', '铜陵市长江西路236号', NULL, NULL, NULL, 5816, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi716455-donghuxiuxianyulezhong', '', '', '5', '1', '2', '2', 4, 41924, 130, NULL, NULL, 251761, 20874, 10793, 16122, 12596, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (710, '710198', '大象山', NULL, '甘肃 > 天水 > 天水景点 > 大象山', '天水', '天水', 'https://img1.qunarzz.com/travel/d8/1508/8e/99951d2ecd50c8.jpg;https://img1.qunarzz.com/travel/d1/1508/51/b6c9ee67474bfb.jpg;https://img1.qunarzz.com/travel/d3/1508/93/fe5a804b0447df.jpg;https://img1.qunarzz.com/travel/d1/1508/fe/bf72606d44550f.jpg;https://img1.qunarzz.com/travel/d6/1508/3b/80918179bad57d.jpg;https://img1.qunarzz.com/travel/d4/1508/f5/fd5f02dd7329e6.jpg', '5', '天水景点排名第22', NULL, '4', '34.749559074', '105.2666046234', '5', '位于天水市甘谷县城郊五华里处。山恋正看如旗，横看如龙，因山中悬崖间峭壁上有大洞窟一个，洞内有一石胎泥塑大佛而闻名。造象可远溯至北魏，具体年代无从稽考。现存释迦牟尼大佛像始凿于北魏，一直到盛唐才趋完美。大佛身高23.3米，腰阔10.4米，和颜善目，仪容庄重，脸型丰颐，双耳垂肩，螺发肉髻，体魄雄伟，斜披袈裟，唇旁短须呈蝌蚪状，结跏跌坐于莲座之上，安详而宁静迎接来往游客。窟顶呈圆拱形，左右两侧有残存悬塑，塑有菩萨、达摩、飞天和卷云、莲叶。大佛洞窟两旁，依山附势有长长的走廊，廊上窟龛相连，现存22个窟龛，大部分平面近方形，正壁开大圆拱龛，设高坛基，并有僧人修行的禅窟，这是大象山窟龛特殊之处，在国内罕见。', '天水市甘谷县城', NULL, NULL, '8:00-17:00', 14786, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi710198-daxiangshan', '', '2', '2', '', '', '2', 4, 34340, 144, NULL, 31197, 27739, NULL, NULL, 21078, 11029, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (711, '10014363', '双湖无人区', NULL, '西藏 > 那曲 > 那曲景点 > 双湖无人区', '那曲', '那曲', 'https://img1.qunarzz.com/travel/poi/1712/ef/9c75b731168f0737.jpg', '--', '那曲景点排名第15', '1小时', '0', '33.1944122314', '88.8441695426', '0', '又称“可可西里藏北无人区”。在西藏的北部，昆仑山、可可西里山以南，冈底斯山和念青唐古拉山以北，有一片幅员辽阔、人迹罕至的地域，这就是藏北无人区。无人区面积60多万平方公里，平均海拔5000米以上，气候干燥，寒冷，空气稀薄，被外界称为“生命的禁区”。该区地处羌塘高原湖盆地带。境内山势平缓，草原开阔。地势北高南低，多为干寒和半荒漠草场。境内主要有昆仑山、唐古拉山、可可西里山、冬布勒山等山脉。这里湖泊众多，较为著名的有多格错仁其香错、多尔索洞错、多格错仁强错、美日切错、雅根错等。湖面海拔均在4500米以上。“藏北无人区”至今人迹罕至，仍然保留着最原始的自然风貌，成为珍稀野生动物赖以生存的唯一乐土，是越野爱好者们魂牵梦绕的探险天堂。', '那曲藏北高原西北部，东邻班戈县，南接文部、申扎，西与阿里地区改则县接壤', NULL, NULL, NULL, 15981, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi10014363-shuanghuwurenqu', '5', '5', '5', '1', '5', '3', 9, 25931, 119, 353880, 235724, 304999, 13266, 358742, 42103, 20724, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (712, '707580', '安岳石刻', NULL, '四川 > 安岳 > 安岳景点 > 安岳石刻', '资阳', '安岳', 'https://img1.qunarzz.com/travel/poi/1803/61/1adcc9b4f2f67837.jpg;https://img1.qunarzz.com/travel/d8/1803/4f/66969854cc3258b5.jpg;https://img1.qunarzz.com/travel/d8/1803/2e/15b7931875b0bcb5.jpg;https://img1.qunarzz.com/travel/d8/1803/27/87efa57756f3efb5.jpg;https://img1.qunarzz.com/travel/d2/1803/80/3861e021bb928fb5.jpg;https://img1.qunarzz.com/travel/d7/1803/b5/e8d9bf2e2ab419b5.jpg', '4', '安岳景点排名第1', '0.5小时 - 1小时', '4', '29.929907877', '105.631251088', '0', '安岳石刻，始于南北朝（公元521年前后），盛于唐、五代和宋。距今已有1300年历史。\n安岳县是目前中国已知的中国古代佛教造像遗址最集中的县，目前已发现历代石窟造像200处，造像10万余尊，其中尤以唐代造像的宏伟和宋代造像的精美著称于世，在中国石刻艺术史上具有上承云冈、龙门，下启大足石刻的特殊地位。\n安岳石刻是中华古代石刻艺术一宝库。它多是我国石刻艺术成熟和鼎盛时期的作品，具有很高的雕刻艺术价值，造像风格除少数敦朴、粗犷的魏晋风骨外，大多是体态丰满，雍容华贵的唐代风格，也有一些精细华美、璎珞盖身的宋代特征。其中，也有安岳人的独创，他们凭自己特有的匠心，高超的技法和对生活的观察与理解，对宗教仪式有许多大胆的突破。', '四川省安岳县境内', '028-24536626', 'http://www.scaysk.com/', '8:00-17:00', 15050, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi707580-anyueshike', '', '5', '5', '1', '4', '1', 4, 36633, 102, NULL, 217068, 369709, 18710, 65029, 22213, 6458, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (713, '7469235', '固关长城', NULL, '山西 > 平定 > 平定景点 > 固关长城', '阳泉', '平定', 'https://img1.qunarzz.com/travel/d4/1703/a6/8426e3a3faee2cb5.jpg;https://img1.qunarzz.com/travel/d5/1508/f0/be5a9130c656bf.jpg;https://img1.qunarzz.com/travel/d2/1703/7a/f1c24348b1b77ab5.jpg;https://img1.qunarzz.com/travel/d6/1703/ab/52eff710fb0eab5.jpg;https://img1.qunarzz.com/travel/d1/1703/17/77ca7f819d7cbbb5.jpg;https://img1.qunarzz.com/travel/d4/1703/25/a5ebc4b90834fcb5.jpg', '4', '平定景点排名第1', '2小时 - 3小时', '4', '37.899568', '113.918129', '0', '固关长城有东西两门，其防御目标主要在西边，故西门楼筑有坚固的城门及瓮城。城门上建重檐歇山顶关楼一座，城额“固关”石刻完好。因其战略要塞位置，故明朝在城内设3座衙门，中为“大衙门”，由三品武官把守，称参将；“二衙门”由掌管勤务的把总负责，居城内后街；“小衙门”由分管过往商贾行人税收的守备负责，在西城门之内。现主要遗址有：长城南段，固关经将军峪至白灰口长约7000米；北段经岑后底至娘子关嘉峪沟约13000米；城墙宽2米，高3—4米，全部依山势而建，用石头砌筑。在墙体的重要至高点上分别建有：炮台，烽火台，哨台等。现有炮台、墩台11座，烽火台2座，哨台1座，药楼1座，敌楼3座。固关长城建有东西城门两座，西门建有坚固的瓮城，城门上有重檐歇山顶关楼。固关长城地势险要，历史悠久，古驿道深深。清康熙帝西巡路经此地，赞叹此关的雄伟，赋《过固关》诗一首：“鸟道入云中，风光塞漠同，人依险地立，城自越山丛，俗朴观民舍，才多壮士雄，芹泉连冀北，回首指青聪。”', '山西省阳泉市平定县娘子关镇新关村', '0353-6142258;0353-6142158', NULL, '8:00-19:00', 6808, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi7469235-guguanzhangcheng', '', '', '2', '', '2', '4', 4, 25372, 93, NULL, NULL, 41991, NULL, 48354, 153874, 55122, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (714, '712532', '董永公园', NULL, '湖北 > 孝感 > 孝感景点 > 董永公园', '孝感', '孝感', 'https://img1.qunarzz.com/travel/poi/201211/15/5a73d9a722b59716ddb12cfb.jpg', '3.9', '孝感景点排名第1', NULL, '8', '30.9261094568', '113.9243077144', '0', '董永公园位于孝感市槐荫大道东段，一九八四年建成，占地75亩，分为三个区域。园内有孝子祠、仙女池、槐荫树、鸳鸯楼、理丝桥、涤丝亭、白步梯和升仙台等景点12处。景点按董永卖身葬父、孝行感天、仙女下凡、百日姻缘等情节为线索建造，歌颂了孝感人民尊老爱幼的传统美德。位于公园中部的“孝子祠”，屋顶青色古瓦，门院粉壁花墙，院内正厅坐北向南，多开的雕花门屏，四周花卉盆景，古色古香协调匀称，祠正堂立着记载董永生平的横匾，两旁陈列着有关的文物、碑石、族谱和名人字画。祠院正中耸立着董永和七姐满工回家的汉白玉塑像，祠院围墙的十六面窗花，以浮雕形式，展示了民间传说中的16个孝子故事图画。“瑶池仙境”，再现了张七姐下凡前，在瑶池沐浴梳妆的情景，七姐妹白玉似的仙姿塑像，在池中岛上神态各异，含情脉脉，个个栩栩如生。公园内有“槐荫古树”、“理丝桥畔”、“鸳鸯楼台”一组景点。相传槐荫为媒，七姐与董永成婚，理丝桥下借滚子河水，浣绢洗丝，用天梭一夜织成十匹锦绢，赎回卖身契；“鸳鸯楼台”是一个建筑在池水边的楼榭建筑群。傍水楼台上有一对紧依的双亭，象征董永七姐，相互依偎相亲相爱；另有一亭，沿廊伸入池中，象征他们爱子在池里戏水。池中小岛上，有一对白色的鸳鸯鸟，与楼台形成犄势。在此观水中之鱼踪、楼台之倒影，思鸳鸯之依依，爱意之甜蜜，不禁使人深深地感受到仙女、董永故乡的亲切，也更增添了无比的乐趣。 　　园中左侧有一米粒形土山，周围十三个土丘。相传董永的儿子董天宝，经常受到十三个财主子弟的欺负，有一天，天宝正在吃用七姐留下的米煮的饭，自称十三太保的家伙又来侮辱他，一气之下，天宝将碗砸去，顿时风沙满天，旋即风停沙尽，地上出现了一座米粒形的山压着十三个土丘。后称此山名“饭山”。 　　公园后门处，有一马鞍形的山丘，上山台阶谓“百步梯”，直到“升仙台”山顶，有一镂花香炉，相传七姐在满工回家途中，被迫上“百步梯”到“升仙台”，凭借袅袅青烟，含恨离别人间。这里四周环水，山洞构筑曲径通幽，崎岖小路和桥、函、亭、景相间。在“升仙台”上可观公园全景。', '湖北省孝感市槐荫大道257号', NULL, NULL, '09:00-18:00', 8388, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi712532-dongyonggongyuan', '3', '1', '2', '2', '', '3', 6, 26964, 99, 47460, 17395, 36740, 44687, NULL, 46382, 21422, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (715, '706268', '驼铃梦坡沙漠公园', NULL, '新疆 > 玛纳斯 > 玛纳斯景点 > 驼铃梦坡沙漠公园', '昌吉', '玛纳斯', 'https://img1.qunarzz.com/travel/poi/1411/2e/2ecaa76d81414df3213a9cdb.jpg;https://img1.qunarzz.com/travel/d6/1410/10/75a305fdf9cff00affffffffc8d65eac.jpg;https://img1.qunarzz.com/travel/d4/1410/10/775724de051ae5d0ffffffffc8d65eac.jpg', '3', '玛纳斯景点排名第1', NULL, '5', '45.104368', '86.012568', '0', '准噶尔盆地古尔班通古特沙漠南缘，是一片原始、粗犷、一望无垠的沙漠世界，又是一座天然的荒漠植物园，葱绿的梭梭、茂密的胡杨、沁人心脾的沙枣、飘逸羽叶的三芒草，以丑为美的猪毛菜、富有药用价值的大黄、大芸黄芪、“叮铛”作声的铃铛刺、形似鹿角的苍劲梧桐，还有血红的红柳，组成了一幅色、味、声、相并茂的大自然景观。沙漠公园还是一座天然动物园，这里有着国家一类保护动物野驴及野猪、黄羊、狼、狐狸、跳鼠、娃娃头蛇，沙枣鸟等百余种动物。来到这野趣十足的“驼铃梦坡”，可以爬沙丘，涉沙海，进行徒步探险，也可以借助“沙漠之舟”，一边听着悦耳的驼铃声，一边欣赏大漠风光。这里开辟了滑沙、狩猎和观看沙漠日出、日落等等，让沙漠之旅丰富多彩，趣味无穷。若还有余兴，还可以就地住宿，来个“天当被，沙做床”式的浪漫。 校进行科普教育实习的基地，同时更是进行沙疗的理想场所。', '玛纳斯县驼铃梦坡沙漠生态景区', NULL, NULL, '8：00——17：00', 1016, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi706268-tuolingmengposhamogong', '3', '', '4', '5', '5', '2', 1, 38704, 159, 82190, NULL, 142601, 364586, 163240, 47026, 11357, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (716, '706601', '龙山', NULL, '辽宁 > 铁岭 > 铁岭景点 > 龙山', '铁岭', '铁岭', 'https://img1.qunarzz.com/travel/d2/1605/52/1bc8582eca9fc5f7.jpg;https://img1.qunarzz.com/travel/d8/1605/2d/66ef537ca4ca20f7.jpg;https://img1.qunarzz.com/travel/d5/1605/ad/ae5a7eb9809480f7.jpg;https://img1.qunarzz.com/travel/d5/1605/7f/567c65accebca3f7.jpg;https://img1.qunarzz.com/travel/d5/1605/98/8cd98bb99fc645f7.jpg;https://img1.qunarzz.com/travel/d2/1605/27/19cbbe04939560f7.jpg', '4.5', '铁岭景点排名第2', NULL, '2', '42.2991440249', '123.8710386227', '0', '龙山风景名胜区位于铁岭市区中心，是辽宁省省级风景名胜区。风景区分为龙首山、柴河、帽山三大景区，总面积9.47平方公里，其中龙首山景区2.22平方公里，柴河景区2.32平方公里，帽山景区4.93平方公里。1991年龙山风景名胜区已被省政府命名为省级风景名胜区。铁岭有龙首山、帽峰山、柴河水库、清河水库四个较大风景区。其中，遐迩闻名的龙首山风景区，西窥城廓，东枕柴河，蜿蜒起伏十华里，纵贯银州南北，呈现一幅巨龙欲飞的自然影观。这里春季满山樱花、桃花、杏花和梨花，竞相开放，郁香醉人；盛夏绿草如茵，百鸟啼鸣；金秋松柏苍劲，红叶满山；隆冬白雪皑皑，银装素裹。四季秀美的自然景色与山中的亭、台、楼、阁相映成趣，使整个风景区到处充满诗情画意。清康熙皇帝东巡时路经此地，曾留下\"雨余塞草自绿，日出山花更红\"的赞美诗句。著名的慈清寺位于龙首山的北部山顶，相传始建于唐代，原名秀峰寺、水潮寺，清初改称为慈清寺。整个寺由正殿、东西配殿和醉翁楼组成一个四合院。正殿内有如来、观单、托塔李天王、韦驮和十八罗汉等塑像，西侧一间正房是三清观，里面有玉清无始天尊、上清灵宝天尊，太清太上老君塑像。醉翁楼是古时当地官绅们饮酒、赏景的地方。三十年代，慈清寺住持曾在日本取回大藏经一套藏于此楼，故也称为藏经阁。', '中国辽宁省铁岭市', NULL, NULL, NULL, 19417, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi706601-longshan', '', '1', '1', '4', '1', '1', 8, 32627, 139, NULL, 13844, 17013, 189494, 23769, 21486, 6267, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (717, '10009512', '延安', NULL, '陕西 > 延安 > 延安景点 > 延安', '延安', '延安', 'https://img1.qunarzz.com/travel/d7/1804/c6/ad53fa506bde3cb5.jpg;https://img1.qunarzz.com/travel/d1/1804/b4/90a3c60286ebcab5.jpg;https://img1.qunarzz.com/travel/d2/1804/73/fc1d46cad4949fb5.jpg;https://img1.qunarzz.com/travel/d8/1804/19/9332c7626f3f18b5.jpg;https://img1.qunarzz.com/travel/poi/1803/ce/323daae9da35b137.jpg;https://img1.qunarzz.com/travel/poi/1712/5b/b764d51c3699c737.jpg', '--', '延安景点排名第22', NULL, '2', '36.590994', '109.496364', '4', '延安位于陕西省北部黄土高原中部，是中国革命的圣地，也是国家级历史文化名城。尽管没有太多宜人的风景，但对于每一个中国人而言，它是那么的有吸引力。\n延安是一个革命的课堂，六十多年前中国历史选择了延安，这里作为当时中国“光明前途的象征”吸引了无数热血精英奔赴宝塔山下投身革命，于是才在中国贫瘠的西北聚集了无数中华民族的精英。也许你对党史教材已经熟悉得有些厌倦了，也许你在各种媒体的轰炸下已经对这段历史有些麻木了，可是，当你走进这个革命圣地，你仍然会对宝塔山作最崇敬的凝视，仍然愿意在许多旧址和故居中聆听战火中的故事。\n延安有非常丰富的旅游资源，在历史古迹方面有国务院公布的全国重点保护单位黄帝陵、有国家级重点文物保护单位钟山石窟等；在自然景观方面有黄河壶口瀑布、全国最大的野生牡丹群和花木兰故里万花山等。', '延安市', NULL, NULL, '全天开放', 13538, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi10009512-yanan', '', '', '5', '1', '', '4', 6, 27026, 158, NULL, NULL, 213551, 8753, NULL, 62027, 47249, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
