INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (727, '9599181', '仙凤三宝农业休闲观光园', NULL, '江西 > 萍乡 > 萍乡景点 > 仙凤三宝农业休闲观光园', '萍乡', '萍乡', 'https://img1.qunarzz.com/travel/poi/1703/b9/6d08f0619c857737.jpg;https://img1.qunarzz.com/travel/d3/1804/e5/0b255a5118e27db5.jpg;https://img1.qunarzz.com/travel/d8/1804/72/850cd51a1cde1bb5.jpg;https://img1.qunarzz.com/travel/d4/1804/93/7cfd2d92f5d121b5.jpg;https://img1.qunarzz.com/travel/d6/1804/31/d516195d77cc35b5.jpg;https://img1.qunarzz.com/travel/d3/1804/ed/cc534a50ecbcabb5.jpg', '5', '萍乡景点排名第7', '1小时 - 2小时', '1', '27.722726', '114.179158', '0', '观光园内建造包括康养体验区、农业观光区、休闲娱乐区、湿地生态区、“三宝”文化体验长廊等项目。并将在观光园内打造马场、森林木屋、休闲沙滩、四季花海、暮光森林小火车、生态鸭池、采摘区等景观或游乐项目。观光园区内不光有大量的苗木花树，里面的住所更是别具一格——帐篷酒店、集装箱酒店等新型住处，想必许多人都还没有体验过。其中特别的是木屋酒店，是将房间安在树上，游客只需爬梯而上，便可体会一把做“鸟人”的感觉。', '江西萍乡市芦溪县宣风镇竹垣村', NULL, NULL, '09:00～17:00开放；', 1732, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi9599181-xianfengsanbaonongyexiu', '1', '5', '4', '2', '3', '4', 2, 40287, 79, 12966, 298168, 51477, 33700, 24614, 128510, 46879, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (728, '10017894', '乾安泥林', NULL, '吉林 > 松原 > 松原景点 > 乾安泥林', '松原', '松原', 'https://img1.qunarzz.com/travel/poi/1712/10/a3981fa8ffbf8337.jpg', '--', '松原景点排名第2', '2小时 - 3小时', '1', '44.812551', '123.719053', '1', '狼牙坝，又称泥林，位于吉林松原市乾安县西部，距县城35公里，西临大布苏湖，北靠大布苏镇。东观“狼牙坝”，锯齿狼牙，奇峰林立，气势磅薄，像一座森严古城堡。垛口连绵，影若将士扶望守城，严阵以待。\n狼牙坝”高出湖面50米，南北长15公里，面积为58平方公里，沟壑纵横，叠峦起伏，数以万计泥柱如林，连峰接岭，土壁陡峭，形状各异，阵阵寒气逼人，大有幽谷深渊之感，犹如置身于原始公园之中。土柱泥林，其形似锯齿，状如狼牙，脉脉相连，横卧南北，故当地人称为“狼牙坝”。来此观光的人说：“南有桂林，北有泥林。”\n“狼牙坝”历经数年风雨沧桑，但是，高大泥林景观在漫长的历史变迁中仍完好地保存下来，不失其故有的风采。中外考古学家发掘的13种脊椎动物化石证明，这里几万年前生存着大批猛犸象披毛犀动物群。\n去“狼牙坝”观光、旅游的人们随之必到大布苏镇，那里通过改革开放是经济发展特区，大小工厂不下十几个，是乾安西部经济开发中心，可观赏一番北国小镇的风采和那里的工厂。\n今日的“狼牙坝”，奇花异草共生，湖中芦苇荡漾，乃是候乌、野兔、狐狸、野狼、蛇等鸟、兽常常出没和繁衍栖息的地方。', '松原市乾安县所字镇119县道', NULL, 'http://www.qianannilin.com/', NULL, 9850, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi10017894-qianannilin', '1', '1', '1', '2', '3', '1', 4, 36105, 119, 14470, 7188, 15270, 11845, 42258, 6025, 6921, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (729, '710286', '碛口', NULL, '山西 > 吕梁 > 吕梁景点 > 碛口', '吕梁', '吕梁', 'https://img1.qunarzz.com/travel/d0/1712/13/8dbb7aa561bf38b5.jpg;https://img1.qunarzz.com/travel/d3/1712/4e/2d76fee4096ea2b5.jpg;https://img1.qunarzz.com/travel/d0/1710/5f/f31c801f6c8077b5.jpg;https://img1.qunarzz.com/travel/d6/1712/13/49182a8f068cd2b5.jpg;https://img1.qunarzz.com/travel/d4/1712/d9/8a5b10e81cd99eb5.jpg;https://img1.qunarzz.com/travel/d3/1512/e5/4c260d333dddf0f7.jpg', '4', '吕梁景点排名第1', NULL, '31', '37.6491890328', '110.8048770124', '20', '碛（音qi）口位于黄河晋陕峡谷中部，临县城南48公里处，因黄河大同碛而得名。黄河由北而来，湫水从东而至，卧虎山横亘镇北，黑龙庙雄峙河东，山环水抱，阴阳交会，山的气势，河的雄浑，凝成了“虎啸黄河，龙吟碛口”的壮丽图景。碛口凭借黄河水运，从清乾隆年间到抗战爆发，200多年间，一直是中国北方著名的商阜重镇。西接陕、甘、宁、内蒙古，东连太原、京、津，差不多吸引了大半个中国的商人，成为东西经济文化交流的枢纽。据镇上黑龙庙清道光年间碑文记载，零星商贩不计，仅店铺字号就有20余家。其间，船筏在黄河里穿梭，驼铃在山谷间回荡。商贾云集，店铺林立，一街灯火，昼夜辉煌，接不完，填不满，正所谓“水旱码头小都会，九曲黄河第一镇”。', '临县城南48公里处', NULL, NULL, NULL, 3553, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi710286-qikou', '2', '5', '5', '', '2', '2', 4, 39663, 147, 12335, 119639, 272494, NULL, 41903, 41896, 13701, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (730, '706365', '天山庙', NULL, '新疆 > 哈密 > 哈密景点 > 天山庙', '哈密', '哈密', 'https://img1.qunarzz.com/travel/poi/1803/fa/63b7fb87b4afab37.jpg', '3', '哈密景点排名第9', '1小时', '1', '43.315994', '93.673703', '1', '天山庙位于天山之巅，全称“天山关帝庙”，始建于李唐时期，海拔2700米左右。天山庙扼丝路北新道的咽喉要冲，与汉、唐、清三代经营西域的历史都有重大关系，原庙中存\"联额颇多\"。因清乾隆五十一年(公元1786年)在天山顶部修建关帝庙而得名。\n现在的天山庙，仅存有三间砖木结构的空殿和一块断碑。遂使今人有“破庙残碑映雪寒”之感叹。天山路蜿蜒曲折凡三十六盘，道旁山高林密，鹰啭鹿鸣。顺盘山弯道，登上天山庙山顶，豁然天开，俯瞰山下，只见林海莽莽，雪光莹莹，一会儿朗月当空，一会儿云遮雾罩，一会儿大雪纷飞。游人旅客多驻足于此，领略一番大自然的奇妙景色。\n天山公路由此分道，急转直下，南去是南山口，北去是口门子。庙旁原有小屋一间，内藏汉、唐和清代碑刻，而唐朝姜行本碑就是其中之一。站在山顶远眺盘山公路，鸣沙山、巴里坤草原尽收眼底。', '新疆哈密北部天山顶', NULL, NULL, '全天开放', 7718, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi706365-tianshanmiao', '', '', '2', '3', '3', '3', 8, 40002, 170, NULL, NULL, 41840, 25919, 37449, 28866, 24428, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (731, '705277', '黄河石林', NULL, '甘肃 > 景泰 > 景泰景点 > 黄河石林', '白银', '景泰', 'https://img1.qunarzz.com/travel/d2/1712/8a/5be27ee3f44edeb5.jpg;https://img1.qunarzz.com/travel/d9/1502/aa/e69a394adf2199.jpg;https://img1.qunarzz.com/travel/d2/1604/b2/224b4c7b66d734f7.jpg;https://img1.qunarzz.com/travel/d1/1502/e4/ead8d8a64cdba3.jpg;https://img1.qunarzz.com/travel/d1/1604/7a/9960feeda7f8eaf7.jpg;https://img1.qunarzz.com/travel/d3/1604/60/df920bf481a6c3f7.jpg', '4.1', '景泰景点排名第2', '3小时 - 4小时', '28', '36.900678', '104.310225', '5', '黄河石林生成于距今四百万年前的第三纪末和第四纪初的地质时代。由于燕山运动、地壳上升、河床下切、加之风化、雨蚀、重力坍塌形成了以黄褐色河湖相砂砾岩为主的石林地貌奇观。景区内陡崖凌空，造型千姿百态，峰回路转，景象步移万千。专家考察评估认定：黄河石林堪称中华自然奇观，极具旅游开发价值。已开发的饮马沟大峡谷内有雄狮当关、猎鹰回首、大象吸水、千帆竞发、西天取经、月下情侣、屈原问天等众多景点，形神兼备，栩栩如生。', '黄河石林龙湾村', '0943-5523080', 'http://www.chinahhsl.cn/', '0:00-24:00(景区全天开放，部分项目16点结束）', 7428, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi705277-huangheshilin', '5', '2', '3', '3', '3', '2', 8, 31021, 200, 149534, 39262, 80545, 51931, 40472, 18403, 12431, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
