INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (739, '715875', '隐水洞地质公园', NULL, '湖北 > 通山 > 通山景点 > 隐水洞地质公园', '咸宁', '通山', 'https://img1.qunarzz.com/wugc/p50/201204/20/8d3fce483a73960493835fbb.jpg;https://img1.qunarzz.com/wugc/p131/201204/20/9d3d3abb34e5cdbd93835fbb.jpg;https://img1.qunarzz.com/wugc/p231/201204/20/40ed7b37b47730c193835fbb.jpg;https://img1.qunarzz.com/wugc/p71/201204/20/fbbf641215f9ada093835fbb.jpg;https://img1.qunarzz.com/wugc/p162/201204/20/fbbf641215f9ada093835fbb.jpg;https://img1.qunarzz.com/wugc/p61/201204/20/8d3fce483a73960493835fbb.jpg', '3.4', '通山景点排名第4', NULL, '8', '29.6681919299', '114.6438047057', '0', '隐水洞位于鄂南山区、享有湖北核电之都的通山县境内，在著名的避暑山庄九宫山下，最美的乡村湖泊富水湖畔，这里青山绿水环抱，风景秀美可餐。距通山县城12公里，咸宁市温泉40公里，武汉市128公里。交通便利，核电专用公路从洞口经过，即将建成通车的杭（州）瑞（丽）高速公路在进洞口100米处建有隐水洞专用匝道，目前属国内距离高速公路最近景区，形成四通八达的交通网络。2009年隐水洞景区已通过国家AAAA级旅游景区的验收挂牌；被湖北省人民政府授予“湖北省优秀旅游景区”、湖北省旅游局授予“湖北省先进旅游景区”、湖北省爱卫会授予“湖北省卫生先进单位”、咸宁市人民政府授予“咸宁市安全生产红旗单位”、“咸宁市文明旅游景区”、通山县人民政府授予“通山县旅游行业先进单位”和“消防安全先进单位”等荣誉称号。隐水洞全长5180米，分上下两层，历经百万年演变，洞内景观丰富多彩，独特的容颜地貌奇特壮丽，十分罕见。洞中游览分为：上游1/3乘船观光、中游1/3乘火车游览、下游1/3徒步零距离感受科学探密的快感，其独特的洞中多样化的游览方式属亚洲第一。“神奇隐水洞，十里水画廊”。隐水洞将向游客展示鬼斧神工的神奇精彩和动人心弦的故事，让您领略触摸大自然的奇特的地下世界。景物如玉琢精雕，洞中仙境惟妙惟肖；潺潺流水，碧潭清澈，轻舟荡漾，飘飘欲仙；宏大的钟乳群体造型别致，在荧光闪烁下流光溢彩，华丽胜似天宫；当您乘坐洞内火车在迷宫中穿行，好似神仙下凡，梦幻无穷；罕见的洞穴生物，犹如外星来客，与人共舞，独特景观让您大饱眼福。百闻不如一见，洞中一游，将使您终身眷念，获益匪浅。是湖北省命名的首家地质公园，被专家喻为“洞天华中—中华天洞”和“神秘的地下明珠宫殿”。', '湖北省咸宁市通山县大畈镇板桥街道', '0715-2750888', NULL, NULL, 13359, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi715875-yinshuidongdizhigongyuan', '', '2', '1', '', '', '3', 1, 33852, 114, NULL, 12719, 21351, NULL, NULL, 90459, 25523, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
