INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (742, '10004403', '红楼', NULL, '新疆 > 塔城市 > 塔城市景点 > 红楼', '塔城', '塔城市', 'https://img1.qunarzz.com/travel/poi/1712/ee/5b9cdcb9534a3937.jpg', '--', '塔城市景点排名第2', '1小时', '0', '46.7533531189', '82.990928658', '0', '红楼作为塔城市较悠久、也是仅剩的较为完整的俄式建筑之一，距今已有近百年历史。目前，它是塔城报社的办公用地，是自治区级文物保护单位。\n红楼是在清朝宣统二年，即1910年由原籍俄国的塔塔尔族商人热玛赞·坎尼雪夫所建。它占地885平方米，分上下两层，共有大小房屋16间，天棚地板，铁皮房顶，门框窗棂，均砌有精美图案。临街的墙全部呈浅红色，故而人们称之为“红楼”。\n红楼建成交付使用后，热玛赞·坎尼雪夫在这里经营大宗土特产和进出口贸易，成为当时塔城进出口贸易的中心，连迪化（乌鲁木齐）的许多商号也慕名前来在此开设代办机构。\n1933年，盛世才以莫须有的罪名将热玛赞·坎尼雪夫逮捕，红楼被没收，并被改作医院；三区革命时，红楼被交还给原主。热玛赞·坎尼雪夫之子接管红楼后不久，便将其捐给了政府，政府仍作医院用。后来，红楼先后被改作地区行政专署办公楼、招待所、行署农口部分处局的办公用房。1985年，红楼被划为塔城报社办公用房。\n近几年来，国内有许多影视导演专程前来塔城，把它搬上了荧屏，让亿万观众一睹它的风采，而许多外地游客来到塔城市，也都会慕名来参观。', '新疆维吾尔自治区塔城市文化路', NULL, NULL, NULL, 1497, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi10004403-honglou', '', '2', '5', '', '1', '4', 0, 27066, 201, NULL, 15809, 295437, NULL, 22923, 56829, 50607, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (743, '715114', '仙人崖', NULL, '甘肃 > 天水 > 天水景点 > 仙人崖', '天水', '天水', 'https://img1.qunarzz.com/travel/d8/1709/ee/f138e0097bbab3b5.jpg;https://img1.qunarzz.com/travel/d4/1708/79/b603221278ef2cb5.jpg;https://img1.qunarzz.com/travel/d1/1410/12/b32f9afa74b9171effffffffc8d65eac.jpg;https://img1.qunarzz.com/travel/d3/1709/b3/e5afc7c18520c0b5.jpg;https://img1.qunarzz.com/travel/d3/1803/7f/1eff476ebe1d4fb5.jpg;https://img1.qunarzz.com/travel/d4/1708/71/ed126c29f4d7ecb5.jpg', '4.3', '天水景点排名第12', '1小时 - 2小时', '22', '34.3856694365', '106.0540441097', '18', '位于天水市北道镇东南45公里的朱家后川，距麦积山景区15公里。相传此处过去常有高人隐居于此修行，故名仙人崖。仙人崖景区由三崖、五莲山、六大寺组成，翠峰林立，岩石万仞，松桧成林，鸟语花香。寺观修建于峰顶或飞崖之下，融汇了儒释道三教合一的宗教文化，现为一佛、道合一的石窟寺庙，有明清殿宇27座，房屋54间，及北魏、宋、明清各代塑像197尊，壁画83平方米，永乐年间的珍贵铜佛像5尊。仙人崖三崖中，以西崖面积和佛殿数量为最，14座殿宇内有唐、宋、明、清各代佛像100多尊，艺术价值极高。“仙人送灯”、“净土松涛”、“仙水湖光”、“十八罗汉朝玉帝”，仙境意趣，景景相连。其中位于朱家后川的净土寺，为天水有名的佛教净土宗寺院。仙人崖景区在麦积山景区的正北方，处于秦岭的西？？。因其地处南北方交界，所以在北方的雄山旁常伴南方的秀水淙淙。南北方景色相比相衬，浑然一体。景色极为诱人。仙人崖地势险要，以石岩洞、献珠峰、南天门、宝盖山、千佛崖最为典型，奇山、秀水、绿树、野花相映，涉足其间，确有身临仙境之感。', '天水市北道镇', '0938-2731407', NULL, '8:30-16:30', 17204, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi715114-xianrenya', '5', '', '', '3', '3', '4', 7, 40333, 203, 216229, NULL, NULL, 81898, 48661, 114731, 55087, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (744, '10013187', '中华黄河坛', NULL, '宁夏 > 青铜峡 > 青铜峡景点 > 中华黄河坛', '吴忠', '青铜峡', 'https://img1.qunarzz.com/travel/poi/1712/43/2385e32669b7f837.jpg', '--', '青铜峡景点排名第3', NULL, '0', '37.8366584778', '105.9319930829', '0', '黄河世纪坛全称是“中华黄河坛”，也称为“中华黄河圣坛”，是黄河金岸的标志性建筑，位于宁夏回族自治区吴忠青铜峡市，为了礼敬黄河、祭拜黄河、感恩黄河。黄河坛长999米，宽200米，建筑面积是6.5万平方米，背靠贺兰山山脉，隔河屹立牛首山，左傍万里黄河臂弯，右依青铜峡峡口。站在黄河圣坛的位置向下望去，眼前的黄河及滩地正好成一个太极八卦图。2011年5月8日，在第三届中国（宁夏）国际文化艺术旅游博览会·感恩母亲河活动中，中华黄河坛首次亮相。\n中华黄河坛创造性地采用青铜铸造，以表达华夏民族饮水思源、感恩戴德、崇敬吟颂黄河母亲。在规划布局上采用六条线：第一条是中国祭祀文化一条线，第二条线是黄河五千年文化一条线，第三条线是青铜艺术展示一条线，第四条线是皇家祭祀园林一条线，第五条线是中国传统文化数数一条线，第六条线是中国传统文化符号一条线；在建筑布局上按‘三区、五牌楼、三大道、三大殿、一广场、一坛、一院’：三区为‘思恩区、礼恩区、感恩区’，五牌楼为‘中华黄河坛大牌楼、思恩牌楼、礼恩牌楼、感恩牌楼、文渊牌楼’，三大道为‘碑林大道、农耕大道、文华大道’，三大殿为‘中华人文始祖殿、慈孝懿范殿、百家姓祠堂’，一广场为黄河广场，一坛为中华黄河坛，一院为黄河文化研究院”。', '吴忠青铜峡市109国道广武收费站北400米', NULL, NULL, '9:00-18:00', 1033, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi10013187-zhonghuahuanghetan', '', '1', '', '1', '1', '2', 6, 40464, 200, NULL, 23234, NULL, 8631, 7543, 36430, 11983, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
