INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (746, '710032', '五大连池风景区', NULL, '黑龙江 > 五大连池 > 五大连池景点 > 五大连池风景区', '黑河', '五大连池', 'https://img1.qunarzz.com/travel/d2/1705/29/28c3937530e9f5b5.jpg;https://img1.qunarzz.com/travel/d1/1606/81/f22dfdd3709daaf7.jpg;https://img1.qunarzz.com/travel/d4/1705/8d/8b6d76a7a910b1b5.jpg;https://img1.qunarzz.com/travel/d0/1705/dc/9e91435e5174a0b5.jpg;https://img1.qunarzz.com/travel/d2/1703/d4/11fd2bfb62db5.jpg;https://img1.qunarzz.com/travel/d1/1505/3e/8de3d6e3063162.jpg', '4.8', '五大连池景点排名第1', '3天 - 6天', '65', '48.659875', '126.160213', '19', '黑龙江省西北部的五大连池市，是我国著名的火山游览胜地。公元1719-1721年，火山爆发堵塞了当年的河道，形成了五个互相连通的熔岩堰塞湖。这里有景色奇特的火山风光、丰富完整的火山地貌和疗效显著的矿泉“圣水”，是一个集游览观光、疗养休息、科学考察多种功能的综合性天然风景名胜区。\n五大连地除五个堰塞湖外，还有许多古代和近代的火山。中、近期形成的火山共14座，其中老黑山和火烧山年龄最小，但体态庞大，景色尤佳，是五大连池中最佳景区。老黑山山势高耸，植被葱郁，相对高差达166米，是14座火山中最高的一座。', '黑龙江省黑河市五大连池风景区', '0456-7296999', 'http://fuwu.wdlc.com.cn/', '五大连池全年开放，开放时间按照淡、旺季会有所改变。\n1、旺季（06月01日-10月31日）：6:00-18:30\n2、淡季）11月01日-05月03日）：8:00-17:00\n博物馆：夏季08：00-17：30，冬季08：00-16：30', 1663, '夏天和冬天最佳，可以领略到不同的特色景观。\n入夏，这里气候宜人，树木葱郁。成群的矿泉涌出地表，昼夜不息。且多为冷矿泉，特点是水温低、辛辣、多气泡，饮之如冰镇汽水，洗之似千万根银针穿透肌肤，饮浴结合，对一些病症有特殊疗效。\n到了冬天，这里还有温泊云雾和三池冰裂奇观。在零下几十度的环境里，这里的泉水温度亦高达14℃，台地上会袅袅升起一层云雾，煞是壮观。三池冰裂是一年一度冬季“冰裂”奇景，只有在严冬时节出现。', NULL, NULL, '1天', 'http://travel.qunar.com/p-oi710032-wudalianchifengjingqu', '5', '', '3', '1', '2', '1', 3, 45226, 111, 106246, NULL, 89258, 19910, 14177, 7061, 5533, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
