INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (748, '10023824', '寿鹿山森林公园', NULL, '甘肃 > 景泰 > 景泰景点 > 寿鹿山森林公园', '白银', '景泰', 'https://img1.qunarzz.com/travel/poi/1712/4b/4ee9316e43bac37.jpg', '--', '景泰景点排名第3', NULL, '0', '37.1422348022', '103.7504066118', '0', '寿鹿山森林公园位于甘、蒙、宁三省（区）交界处，西距景泰县城39KM。总面积1086公顷。公园地处腾格里沙漠与黄土高原的过渡地带，周围被荒漠戈壁所包围，享有“戈壁绿岛”之美誉。公园的开发建设与经营管理已初具吃、住、行、游、购为一体的规模。旅游餐饮网点可提供寿鹿山羊羔肉、烤全羊、蘑菇面片等具有地方风味的餐饮服务。有蒙古包、藏包，可享受独具民族特色的敬酒献哈达和民族歌舞。成为人们休闲度假，观景避暑，调节精神、消除疲劳，沐浴森林温馨的优美环境。人们在这里充分体验满目醉人的绿色，在这天然氧吧进行森林浴，呼吸新鲜纯净的空气，陶冶情操，净化心灵，尽情享受大自然的乐趣。这里又是白银市爱国主义教育基地，生动的中小学生德育课常在这里进行。', '白银市景泰县位于甘蒙宁三省区交界处', NULL, NULL, '8:00-19:00', 17804, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi10023824-shoulushansenlingongyuan', '5', '2', '', '5', '2', '5', 2, 45126, 65, 122547, 30890, NULL, 99786, 31694, 222634, 80012, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
