INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (755, '7588645', '达瓦昆沙漠', NULL, '新疆 > 喀什 > 喀什景点 > 达瓦昆沙漠', '喀什', '喀什', 'https://img1.qunarzz.com/travel/poi/1501/79/799026ded150077ecdb.jpg;https://img1.qunarzz.com/travel/d7/1610/49/eaad60c5d2979b5.jpg;https://img1.qunarzz.com/travel/poi/1501/67/679c0abbbfa1a2c9cdb.jpg;https://img1.qunarzz.com/travel/poi/1501/20/20c346242008b92dcdb.jpg;https://img1.qunarzz.com/travel/d1/1610/81/df5cde2ae97aecb5.jpg;https://img1.qunarzz.com/travel/d3/1610/46/9f41e996dec7d2b5.jpg', '4', '喀什景点排名第45', '1天', '5', '39.056731', '77.046532', '6', '据说三世纪末，有一个叫铁力木的国王，他率领女儿达瓦昆和女婿沙迪克为老百姓找水，并在沙漠边缘发现了一个冒沫的深坑，于是命千人挖了百天，结果什么也没有，是达瓦昆偷偷瞒着父亲和丈夫，在一天夜里终于挖出了水，而公主达瓦昆也化作了一泓美丽的湖水。我始终相信传说是人们的一种美好愿望，新疆有许多湖泊，每个湖泊都会被人们赋予许多神秘的色彩，达瓦昆也是如此。\n达瓦昆旅游风景区位于岳普湖县铁力木乡，交通便捷，被称为“中国沙漠风景旅游之乡”。达瓦昆沙漠风光旅游风景区有2000亩的水面、4万亩的沙漠,并且沙水相连，碧波荡漾、游艇往来。以达瓦昆为中心,辐射13个景点,有1800年的千年胡杨王，古墓群,700年历史的圣池和古战场遗址，可以“临境知天寰，怀古吊钩沉”1500年的千年柳树王，原始古朴的大漠风光有“大漠孤烟直，长河落日圆”的恢弘壮美组成了诱人的风景线，令人心驰神往，无不让中外游客叹为观止。\n同时开发了沙漠探险、沙疗、特色餐饮、滑沙、沙滩排球及民族歌舞观赏、民族式摔跤、赛马、叼羊等反映少数民族传统生活的特色旅游娱乐和特色文化体育活动项目。', '喀什岳普湖县铁力木乡', NULL, NULL, '10:00-20:00', 12949, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi7588645-dawakunshamo', '5', '', '5', '1', '3', '4', 6, 26491, 74, 213995, NULL, 297434, 8659, 93258, 151644, 52825, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (756, '10004565', '桌子山岩画', NULL, '内蒙古 > 乌海 > 乌海景点 > 桌子山岩画', '乌海', '乌海', 'https://img1.qunarzz.com/travel/poi/1712/41/f0ca5526a9b86337.jpg', '--', '乌海景点排名第7', '1小时', '0', '39.6415977478', '106.9947015636', '0', '桌子山岩画位于乌海市城东，主峰海拔高度2149.4米。山脉主体呈南北走向，长约75公里，东与内蒙古鄂尔多斯高原接壤，西距穿越市区的黄河约2公里。桌子山山势雄伟，峰峦迭起，巍峨壮观，因其主峰山顶较平坦，远眺貌似桌子状，故得此名。在桌子山脉诸多山沟的悬崖峭壁和沟畔石灰岩磐石上，残存着无数古代岩画的遗迹，称为\"桌子山岩画\"。桌子山岩画总面积有16000多平方米，1973年由乌海市牧民在放牧时发现的，岩画内容多为太阳神等人面像、动物图形、狩猎图、符号等，每幅岩画单独成画，神态各异，是国内罕见的出土岩画。桌子山岩画创作于新石器时代，每幅画都是刻磨而成，是我国北方游牧民族的历史文化遗迹。特别是人面像岩画是中国和世界人面像岩画之精华，反映人类童年时代丰富的想像力和对生活的美好愿望。岩画被称为是人类最早的画布，世界上人面形岩画最集中的地区就是乌海市的桌子山岩画，乌海市正在逐步加大力度保护这一珍贵遗产。这里已经先后发现了多处岩画：召烧沟岩画、苦菜沟岩画、后摩尔沟岩画、苏白沟岩画等。从岩画的内容看，多为人面像、动物、狩猎类图形，其中已经被建成博物馆加以保护的召烧沟岩画还是一处神奇的祭祀岩画组。除召烧沟岩画外，目前其它岩画皆为自然状态，不收门票，文物局偶尔予以勘查保护。从乌海市前往各处岩画点皆不远，可以打车前往，但其他岩画目前未开放旅游参观，也很难找到，游客请勿贸然前往。', '乌海市城东', NULL, NULL, NULL, 13650, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi10004565-zhuozishanyanhua', '3', '2', '5', '5', '4', '5', 8, 40086, 116, 29539, 22450, 200859, 395433, 43561, 385513, 80207, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
