INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (764, '713634', '高黎贡山国家级自然保护区', NULL, '云南 > 怒江 > 怒江景点 > 高黎贡山国家级自然保护区', '怒江', '怒江', 'https://img1.qunarzz.com/travel/poi/201211/15/5c80dfbfc4a3eb61ddb12cfb.jpg;https://img1.qunarzz.com/travel/poi/201211/15/f02ffad1a9974819ddb12cfb.jpg;https://img1.qunarzz.com/travel/poi/1803/c2/7b18713c5ebf7537.jpg', '4.6', '怒江景点排名第4', '3天', '10', '25.3423783514', '98.2746095259', '1', '这是横断山脉中的一颗明珠，森林覆盖率达85%，高山峡谷复杂的地形和悬殊的生态环境，为各种动植物提供了有利的自下而上条件。高黎贡山巨大的山体挡住了西北寒流的侵袭，又留住了印度洋的暖湿气流，使地处低纬度高海拔的保护区，形成了典型的亚热带气候。在东西坡海拔1600-2800米地区，是自然保护区的主体，它连接东喜马拉雅区，组成了我国最引人瞩目的原始阔叶林区。在这里，森林资源极期丰富，珍稀植物随处可见。有当今世界上是大的杜鹃树种―500多年树龄的大树杜鹃；有被称为“绿色寿星”的古老孑遗植物――国家一级保护珍稀树种秃杉；有云南樱花的原始种、云南山茶的原生种，以及其它珍稀树种，是我国珍稀树种汇集的森林。', '云南怒江贡山县怒江西岸', NULL, NULL, '全天开放', 12553, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi713634-gaoligongshanguojiaji', '1', '', '', '2', '2', '3', 0, 30688, 150, 18865, NULL, NULL, 45170, 19221, 33067, 26910, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
