INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (781, '709972', '三兴康乐村', NULL, '河南 > 鹤壁 > 鹤壁景点 > 三兴康乐村', '鹤壁', '鹤壁', NULL, '5', '鹤壁景点排名第3', NULL, '1', '35.791532532', '114.239948161', NULL, '三兴集团淇滨康乐村地处豫北明珠鹤壁市。它南依悠悠淇水。西靠巍巍太行。四季气候宜人。周围名胜星罗密布（中华第一古军校――云梦山、大丕山名胜风景区、天然太极图等）。它交通便利，毗邻京广铁路、京珠高速公路、107国道。距首都北京仅需六个小时车程。距省会郑州仅需二个小时。三兴集团温泉度假村实用总面积近200亩，依鹤壁市特有的地貌――丘陵地带而建，整体西高东低，呈阶梯形式。2平方公里内无工厂噪声或其他污染排放源，周围阡陌交错，一派田园自然景象。是喧嚣都市中难得的一方清静之地。目前康乐村是我市乃至周边地区唯一的一所大型娱乐场所，三兴集团淇滨康乐村内绿树成荫、鸟语花香，是一所集体休闲、娱乐、餐饮、服务、旅游为一体的大型娱乐场所，下辖大型温泉洗浴中心、室内游泳馆、大型健康房、餐饮部（以蒙古包为特色，共计13个）水上乐园（标准池、冲浪池、儿童池、环流池、高空滑道）客房部、三兴旅行社，形成了吃、住、行、游、娱一条龙的服务体系。', '河南省鹤壁市', NULL, NULL, NULL, 14231, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi709972-sanxingkanglecun', '2', '1', '5', '3', '1', '4', 4, 48874, 147, 19937, 11748, 80076, 75903, 12011, 167205, 53895, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
