INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (793, '10004633', '咪依噜风情谷', NULL, '云南 > 楚雄州 > 楚雄州景点 > 咪依噜风情谷', '楚雄州', '楚雄州', 'https://img1.qunarzz.com/travel/poi/1712/6c/f3bd0f1bfd8f8a37.jpg', '--', '楚雄州景点排名第19', '1小时', '0', '25.3029785156', '101.2794135487', '0', '咪依噜风情谷位于楚雄州南华县城以北10公里处的龙川镇岔河村，地处南华与姚安、牟定三县交界处，是一个风景秀丽、民族风情浓郁、彝族文化底蕴深厚、民俗保持完好的少数民族聚居区。\n进入咪依噜风情谷，相聚姑娘房，体验彝家情。可以在这里可品尝纯正的黑山羊、乌骨鸡和地道的山茅野菜，还可以参与激情奔放的民族歌舞。另外，爬青山、穿莽林、找菌子、采野花，摘林果等娱乐活动，能让人体验到山里彝人的古朴豁达、宽容平和、闲适快乐的“世外桃源”生活。\n电影《通天长老》、《大峡谷》，电视剧《新娘出嫁》、《火的民族》在这里拍摄过外景。日本广播协会NHK电视台《彩云之南》摄制组曾对这里的彝族农耕稻作文化进行过卫星现场直播。', '楚雄彝族自治州南华县217省道东北', NULL, NULL, NULL, 1623, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi10004633-miyilufengqinggu', '3', '3', '4', '3', '3', '4', 0, 27638, 141, 63761, 98646, 72188, 20954, 27006, 62615, 46443, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (794, '9802235', '金龙山文化旅游园', NULL, '甘肃 > 天水 > 天水景点 > 金龙山文化旅游园', '天水', '天水', 'https://img1.qunarzz.com/sight/p0/1704/79/7986256e46c21847a3.water.jpg', '--', '天水景点排名第51', NULL, '0', '34.564939', '105.697053', '1', '天水金龙山文化旅游园是天水天乐文化旅游开发有限责任公司投资开发的项目。该项目占地面积约300亩，总投资3.95亿元。分两期实施，在建的一期工程占地面积约100亩，总投资2.29亿元，已完成投资7000多万元。由接待服务区、滨水景观区、大型综合游乐园区、儿童游乐园区、田园生态区等五大功能区组成，预计2017年完成一期工程。接待服务区建有餐饮大厅1500平方米，包厢房30多间，能同时接待1600多人就餐。滨水景观区有喷泉景观，人工湖可容纳约10多条船游玩，适宜成人和儿童群体，并配有十多个木凉亭和500平方米的木廊架凉亭，能同时容纳300多人乘凉、喝茶、休闲娱乐。大型综合游乐区有自旋滑车、大摆锤、海洋欢乐岛、立环跑车、跳楼机、海盗船、旋风骑士、青蛙跳、碰碰车、旋转木马、转转杯、5D影院、军事射击（配有各种机关枪10把、大炮2门），真人CS（配有装备20套，可供20人同时体验）。儿童游乐区为室内配备，分为淘气堡类和迷你设备类，占地700平方米，配有18种类型，同时可容纳120名儿童玩耍。田园生态区占地面积约43亩，园内种植樱桃树（已挂果）二千多棵，其中有5亩土地种植纯天然的各种蔬菜（如西红柿、辣椒、黄瓜、豆角、菜瓜等，可供游人采摘大樱桃和各类蔬菜，体验田园乐趣）。该项目的建设将填补我市大型游乐园的空白，对改善我市生态环境，带动文化旅游业的发展，完善我市文化产业链条有较大的助推作用。', '天水市秦州区天水郡村长仪路上山一公里', NULL, NULL, '10:00～17:30开放；', 10043, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi9802235-jinlongshanwenhualu:you', '', '', '', '2', '3', '3', 4, 27931, 103, NULL, NULL, NULL, 44963, 32641, 54606, 21284, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (795, '701903', '查干湖自然保护区', NULL, '吉林 > 松原 > 松原景点 > 查干湖自然保护区', '松原', '松原', 'https://img1.qunarzz.com/wugc/p80/201208/12/6aa0548edf74867293835fbb.jpg;https://img1.qunarzz.com/travel/d5/1504/7e/f62d3925e14755.jpg;https://img1.qunarzz.com/travel/d7/1508/6e/697b972017ef05.jpg;https://img1.qunarzz.com/travel/d7/1602/44/bfaa0fb799429ff7.jpg;https://img1.qunarzz.com/travel/d6/1701/ab/9eedadb45aa61cb5.jpg;https://img1.qunarzz.com/travel/d5/1701/12/97eacbd8812c83b5.jpg', '4.3', '松原景点排名第4', '2小时 - 4小时', '18', '45.2637668967', '124.3913915132', '8', '查干湖自然保护区是吉林省人民政府1986年批准成立的省级自然保护区。保护区面积为480平方公里，大部分在开发区境内。\n保护区核心区位于辛甸泡，泡内生满芦苇，是鸟类主要栖息场所；缓冲区位于查干湖夹芯子岛以北至辛甸泡水域；生产实验区位于新庙泡、库里泡、菱角泡和查干湖夹芯子岛以南水域。\n保护区内现有鸟类有116种，其中属国家一级保护的有白枕鹤、白头鹤、丹顶鹤等5种；属国家二级保护的有白天鹅、雀鹰、灰鹤等12种。另，对两栖类、爬行类、兽类、鱼类都开展相应的保护。\n随着环境保护工作的不断开展，全区植树造林面积逐年增加，植被得到恢复，湖泊塌岸得到治理，鸟类数量也明显增加。中华秋沙鸭、白天鹅等国家级重点保护鸟类年年来保护区栖息和繁殖。\n现在的查干湖旅游度假区夏季可以观赏大湖湿地、草原风光体验蒙古族风情，冬季以冬网捕鱼为乐，已然成为集观光、娱乐、休闲、度假、餐饮、购物等功能为一体的综合性旅游区。', '长白高速公路沿查干湖景区下路口', '0438-2605077', NULL, '8：00-18：00', 4844, '查干湖的旅游旺季是冬季和夏秋两季。查干湖的冬捕是世界独有的，许多国内外人士都慕名而来。夏季水草丰美，烟波浩淼。秋天雁鸭栖息、蒲苇轻拂鱼戏浪，又是不一样的景色。', NULL, NULL, NULL, 'http://travel.qunar.com/p-oi701903-chaganhuziranbaohuqu', '', '4', '3', '2', '2', '2', 2, 36744, 182, NULL, 53565, 68356, 21451, 36058, 32088, 12223, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (796, '722637', '孝庄祖陵风景区', NULL, '吉林 > 松原 > 松原景点 > 孝庄祖陵风景区', '松原', '松原', 'https://img1.qunarzz.com/travel/poi/1803/60/186112577db8e537.jpg', '--', '松原景点排名第3', NULL, '1', '45.3230571187', '124.4913786365', '1', '长山明珠园是由长山热电厂出资始建于1988年，原名水上公园，为了充分发挥地理环境优势，加快开发旅游业，2001年长山热电厂出资再次对其进行了全面改造，并更名为明珠园，由长山热电厂明珠实业公司进行管理，现在的明珠园占地面积达2.2平方公里，其中水面1.4平方公里，绿化面积超过1万平方米。\n公园内设置了16个功能区，涵盖了历史教育，野外生存，餐饮服务，园林景观，现代游艺，荷花观赏，植物观赏，游船垂钓，冬季运动等。整个明珠园既富现代文明气息，又具备苏州园林的典雅，突出了休闲、娱乐、教育、健身、实践的宗旨。', '松原市前郭县长山镇境内长山热电厂东侧', NULL, NULL, '8:00-18:00', 13594, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi722637-xiaozhuangzulingfengjing', '', '3', '5', '5', '3', '5', 7, 49928, 91, NULL, 38842, 302931, 226142, 62186, 376890, 108341, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (797, '10005576', '商丘古城景区', NULL, '河南 > 商丘 > 商丘景点 > 商丘古城景区', '商丘', '商丘', 'https://img1.qunarzz.com/travel/poi/1803/b7/dab6cc43cce36937.jpg;https://img1.qunarzz.com/travel/poi/1712/13/748a94972e88cb37.jpg', '--', '商丘景点排名第3', '4小时 - 5小时', '0', '34.374118', '115.620226', '0', '商丘古城，是明清时期的归德府城，古城始建于明朝正德六年（公元1511年）。商丘古城由砖城、城湖、城廓三部分构成，外圆内方，呈现一个古钱币造型，建筑十分独特。目前商丘古城下，同时还叠压着春秋时期的宋国都城、秦汉和隋唐时期的睢阳城、宋代的应天府（南京城）等6座都城、古城。\n商丘古城旅游区内的景点比较多，主要包括古城景区、南湖景区和火文化景区三大部分。重要景点有：古城区的壮悔堂、南湖区的张巡祠、八关斋及文化景区的阏伯台（火星台）、燧皇陵、开元寺等。游客在这里可以看到保存完好的古城，还有一块块斑驳的墙砖，古城边上还有优美的南湖，以及生活悠闲的本地人，这里的节奏是比较慢的，没有大城市那么喧嚣，更多了一份平和。\n商丘古城非常有特色，具有八卦城、水上城、城撂城三大特点。全城外圆内方、形如古铜钱，九十三条街道形如棋盘，内城地势呈龟背状，中间高，四周低，略向南倾斜。内城四周城墙耸立，巍巍壮观，古城四面环水，仅有四门与外界相通，从空中看古城像建在水中一样，这种内城、城湖、城廓三位一体，保存完好的古城，在国内也是非常少见的。\n古城中被称为活文物、活化石的千年皂角树，至今依然枝繁叶茂，这里还是花木兰的故里，被誉为明末四大才子之首的侯方域就出生在古城内，他与江淮名伎李香君缠绵悱恻的爱情故事也早已为人所熟知。', '商丘市睢阳区华商大道与平原路交叉口东北角', '0370-3310695;0370-3313605', NULL, '夏季：8:00-18:00，冬季：8:00-17:00', 12937, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi10005576-shangqiuguchengjingqu', '', '', '', '', '5', '1', 9, 29409, 152, NULL, NULL, NULL, NULL, 330089, 19464, 5333, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (798, '703507', '洞山游园', NULL, '安徽 > 淮南 > 淮南景点 > 洞山游园', '淮南', '淮南', NULL, '4', '淮南景点排名第2', NULL, '2', '32.6324417093', '117.0098542182', NULL, '洞山游园是舜耕山脉的重要组成部分。舜耕山因上古贤君大舜在此耕耘而得名。洞山游园现分为洞山山顶游园和洞山北坡游园。洞山山顶游园已铺设高档花岗岩便道150米，新修了观景平台一处，新添置了休闲设施石凳、石椅等，重新修缮了舜耕亭，山顶还进行了大面积绿化和亮化。洞山北坡游园是浙江美院根据《淮南子》历史记载而设计修建的。随着城市“东进南扩”战略的实施，舜耕山将成为新城区中一道靓丽的风景线，洞山游园已成为淮南市民的后花园。', '安徽省淮南市田家庵区', NULL, NULL, NULL, 3404, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi703507-dongshanyouyuan', '', '', '2', '', '4', '3', 5, 37259, 127, NULL, NULL, 29142, NULL, 174101, 30556, 22670, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (799, '7645381', '罗驿村', NULL, '海南 > 澄迈 > 澄迈景点 > 罗驿村', '澄迈', '澄迈', 'https://img1.qunarzz.com/travel/poi/1504/b5/41d137f8156761.jpg;https://img1.qunarzz.com/travel/d0/1502/1e/9c4ba7d0f0256d.jpg;https://img1.qunarzz.com/travel/d3/1502/52/9ef88ab5244696.jpg', '3.5', '澄迈景点排名第3', '2小时', '2', '19.901629', '110.125753', '3', '在罗驿小学的旁边，寻着一道小木栅栏，穿过一片树木，便看到了有283年历史的李氏宗祠。这是一幢独庭独院，拥有三进三间的高大建筑，斑驳的墙壁、破落的屋檐、疯长的野草……岁月已经在此留下太多的痕迹，让人走在空荡荡的宅子里有种恍如隔世的感觉，只是从庭前威武的石狮、屋宇四周翘起的飞檐及墙上有关教化的彩绘等处还依稀能勾勒昔日的辉煌。罗驿李氏宗祠始建于1723年，位于罗驿村东南面，坐北朝南，木石结构，三进院落四合院式布局，是目前省内少见的大型古建筑文物之一，在目前省内家族宗祠中，这么大的规模也很少见。李氏宗祠虽然已经历经近300年的风雨，但是保存较为完整，具有鲜明的清代建筑特色。在第三进主体建筑中，还有岛内不多见的月梁，造型古朴美观大方。目前，已经被列入县级历史文化保护文物的罗驿李氏祠堂正在申报第二批海南省重点文物保护单位。', '海南省直辖县级行政单位澄迈县', NULL, NULL, '全天', 17710, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi7645381-luoyicun', '1', '1', '4', '5', '1', '5', 8, 48067, 118, 17075, 10981, 42214, 211800, 8807, 251264, 107010, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (800, '713702', '白暨豚养护场', NULL, '安徽 > 铜陵 > 铜陵景点 > 白暨豚养护场', '铜陵', '铜陵', 'https://img1.qunarzz.com/travel/poi/1803/51/e56ecb1bd2ad5537.jpg', '4', '铜陵景点排名第6', '1小时 - 2小时', '1', '30.8113139531', '117.7462376225', '0', '铜陵白鳍豚养护场是世界上首座利用半自然条件对白鳍豚进行易地养护的场所。它位于安徽铜陵市大通镇和悦洲与铁板洲之间的夹江上。夹江长1620米，宽220米，洲上杨柳依依，云淡风高；江上水鸟逐波戏浪，江岸树影婆娑起舞，水色秀丽，风景宜人，是养殖白鳍豚的理想水域。\n铜陵白暨豚养护场始建于1985年9月，占地面积409亩，保护区自枞阳县三江口至繁昌县荻港镇，全长110公里江域。该保护区主要保护长江流域国家一、二类重点保护动物，如白暨豚、中华鲟、江豚、胭脂鱼等。2003年6月24日，国家环保总局批准该保护区为国家环境保护长江重点永生野生动物保护中心，保护水域涵盖八百里皖江。她还是安徽大学生命科学教学研究基地、青少年科普教育基地和国家AA级旅游区。由于其科技含量高，知识与趣味性强，特别是“水簇馆”生动有趣，“标本馆”种类繁多，“白暨豚”驰名中外，“江豚”憨态可鞠，为广大游客提供了旅游、观光、休闲的好去处。', '安徽铜陵市大通镇和悦洲与铁板洲之间的夹江上', NULL, NULL, '全天', 17838, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi713702-baijitunyanghuchang', '', '', '', '2', '4', '3', 4, 48562, 152, NULL, NULL, NULL, 42367, 51309, 48963, 21498, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
