INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (803, '712479', '瑞安堡', NULL, '甘肃 > 民勤 > 民勤景点 > 瑞安堡', '武威', '民勤', NULL, '--', '民勤景点排名第1', NULL, '0', '38.5985914629', '103.0769052828', NULL, '河西走廊东北部有一块被腾格里沙漠和巴丹吉林沙漠包围着的绿洲，这就是甘肃省武威市(中国旅游标志的故乡)的民勤县，距省城兰州300多公里.长年沙暴肆虐，地广人稀。近年来，这里因盛产黄河密瓜和大板瓜子而闻名遐迩，小城商客渐多。这里有一座年代并不久远、却地处偏乡僻壤的独特景观―――瑞安堡。从县城出发，沿途满目防沙林带，高高的参天杨树像道道屏障，挡住了风沙。沿着水渠旁的土路，10分钟车程便到了瑞安堡，当地人把“堡”念作“PU”。瑞安堡本是民国初期一位暴富乡绅的私家庄园，占地5000多平方米，大小院落8个，高脊瓦房140多间，亭台楼阁7座，墙高10米，院内暗道机关无数，与其说是私家豪宅，不如说是防御堡寨。宅院的主人王庆云(字瑞庭，“瑞安”二字乃取祥瑞平安之意)当年是保安团长，据说是搜刮民财为自己建造了这座庄园，又怕招来乡党嫉恨，处心积虑在院内外修筑了许多防御设施。看得出来，此人虽家财万贯，妻妾成群，日子却过得提心吊胆，战战兢兢。', '甘肃省武威市', NULL, NULL, NULL, 11790, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi712479-ruianbao', '1', '', '3', '1', '1', '4', 1, 40970, 156, 13763, NULL, 38656, 11119, 21131, 58046, 42531, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
