INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (819, '709320', '刘开渠纪念馆', NULL, '安徽 > 淮北 > 淮北景点 > 刘开渠纪念馆', '淮北', '淮北', NULL, '5', '淮北景点排名第5', NULL, '1', '33.9853488953', '116.8031402706', NULL, '人民艺术家、我国著名雕塑大师刘开渠纪念馆，坐落在安徽淮北市相山公园内。这里依山傍水，环境幽雅。 开渠先生是淮北市刘窑村人。家乡人民和政府怀着对大师的敬仰之情，在海内外各界朋友的支持下，兴建了我国首座雕塑家纪念馆。刘开渠纪念馆座北朝南，建筑在南低北高的山的怀抱中既有现代建 筑的新颖特色。纪念馆占地4977平方米，是由三部分组成的长方形庭院。前面左右对称的两栋两层小楼构成了纪念馆懂得石门，中间四栋两层楼房首尾相接是展览厅，后院为大师的陵园。青松翠柏环抱着开渠大师的墓石。古朴的半圆形墓碑石上，镌刻着全国政府副主席赵朴初题就的“人民艺术家雕塑宗师刘开渠之墓”的金字。刘开渠纪念馆收藏陈列着大师大量的珍贵作品和信件，向人们展示着开渠先生作为杰出的艺术家、美术教育家的辉煌人生。纪念馆自1995年4月5日正式对外开放以来，幕名而来参观的海内外人士络绎不绝。1995年5月，安徽省委、省政府确定刘开渠纪念馆为爱国主义教育基地。', '安徽省淮北市相山南麓相山公园内', NULL, NULL, '全天', 8931, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi709320-liukaiqujinianguan', '5', '4', '', '3', '5', '4', 9, 25743, 150, 149685, 78406, NULL, 66570, 160162, 172634, 49509, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
