INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (824, '10008816', '淖毛湖', NULL, '新疆 > 哈密 > 哈密景点 > 淖毛湖', '哈密', '哈密', 'https://img1.qunarzz.com/travel/poi/1712/bb/92324ee986fe8237.jpg', '--', '哈密景点排名第24', NULL, '0', '43.7625846863', '94.994323345', '1', '淖毛湖位于新疆哈密地区伊吾县北部73公里处，距中蒙边境50公里，2001年9月，撤乡建镇。根据“金农网”资料，区域面积8598平方公里，人口1万人，有宜农可耕土地18万亩，耕地面积4万多亩。该镇是全疆小城镇建设示范乡镇，是全县重点发展的经济区域。随着西部大开发的实施，淖毛湖晚熟哈密瓜产业化经营，生态工程建设、小城镇基础设施建设以及土地矿产资源的开发利用等多个领域都迅速发展。\n镇东约10公里，有一片面积超过40万亩的原始天然胡杨林。在如此干旱且碱性极大的戈壁滩上，竟然有这么广阔茂密的胡杨林，经千百年风雨、沙尘、烈日的锤炼，粗壮的躯体扭曲成各种姿态，犹如耸立于林间的一个个雕塑，令人叹为观止。\n在淖毛湖镇西20公里以外的一片戈壁滩上，可以看到大大小小的硅化木。硅化木又叫树化石，是树木在特殊的地址条件下，经过上亿年沧海桑田的演变，最终变成石头。虽然变成了石头，但树的形状依然可辨。由于硅化的时间和地质条件的差别，硅化木的质地也存在差异。硅化木有较高的科研和观赏价值，成为许多奇石爱好者收藏的对象。', '伊吾县北部73公里处', NULL, NULL, NULL, 16015, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi10008816-naomaohu', '', '1', '', '', '4', '3', 4, 33834, 61, NULL, 14790, NULL, NULL, 46613, 52088, 25941, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
