INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (831, '715704', '水帘洞', NULL, '甘肃 > 天水 > 天水景点 > 水帘洞', '天水', '天水', 'https://img1.qunarzz.com/travel/poi/201408/11/da9e68ac797a2ac6c8d65eac.jpg;https://img1.qunarzz.com/travel/poi/201408/11/f494d456e7842eb5c8d65eac.jpg;https://img1.qunarzz.com/travel/poi/201211/15/72617d263921fbd3ddb12cfb.jpg;https://img1.qunarzz.com/travel/poi/1803/8d/bc853a7c8b055237.jpg;https://img1.qunarzz.com/travel/d3/1803/bc/51057030bca305b5.jpg;https://img1.qunarzz.com/travel/d8/1803/18/7fa474e9e93110b5.jpg', '4.9', '天水景点排名第21', '1小时 - 3小时', '6', '34.8382197102', '104.9599710366', '5', '坐落在武山县城东北约25公里处的钟楼山鲁班峡谷中。这里山势挺拔，突兀壮观；绿树成荫，山花烂漫；洞中泉水奔涌，山间溪流潺潺，景色幽静宜人。\n“群峰叠嶂觅无路，乱石开径别有天”，为理想的游览胜地。该石窟开凿于十六国时期的后秦，经北魏、北周、隋、唐、五代、宋、元历代修建，主要有水帘洞、拉稍寺、千佛洞等名胜古迹，为国家重点文物保护单位。\n水帘洞在形似斧劈的试斧山东侧之峭壁上，是一个约50米长，30米高，20米深的拱形自然洞穴。每当雨季，洞顶、洞壁泉水、雨水一齐涌出，洞檐流水如注，恰似珠帘掩门，故得名水帘洞。洞内有四圣宫、观音寺、南殿、菩萨殿等多座建筑，各殿、台、亭、阁依自然岩洞有开有合，错落有致，工艺精巧。其中尤以菩萨殿最为高大华丽。该殿上下两层，下层石壁洞中有一汪清池，清澈见底；上层横匾大书“西山暮雨”四字，内塑当地民间传说中的麻线娘娘；水帘洞的崖面上保存着北魏、隋、唐、元各代的佛教巨幅壁画。整个洞内楼台、泉石、雕塑、画像相辉相映，大有天然布景之趣。', '武山县城东北钟楼山鲁班峡谷中', '0938-3423808', NULL, '8:00-18:00', 1246, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi715704-shuiliandong', '', '', '3', '2', '1', '3', 1, 40793, 99, NULL, NULL, 28848, 46192, 22494, 52158, 24755, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (832, '10001520', '赤壁古战场', NULL, '湖北 > 赤壁 > 赤壁景点 > 赤壁古战场', '咸宁', '赤壁', 'https://img1.qunarzz.com/travel/poi/1803/c2/baa1c2a004a7a437.jpg;https://img1.qunarzz.com/travel/poi/1712/e5/e2784248b88a7637.jpg', '3', '赤壁景点排名第11', '2小时 - 3小时', '1', '29.884192', '113.637904', '0', '赤壁古战场（俗称武赤壁），是我国古代著名战役中惟一尚存原貌的古战场，是东汉末年“赤壁之战”发生的地点，位于赤壁市赤壁镇。赤壁大战意义重大，赤壁之战后，出现魏、蜀、吴鼎峙局面，即历史上的三国时期。赤壁古战场风景区主要景观有：赤壁摩崖石刻、武财神庙、周瑜塑像、凤雏庵、翼江亭、赤壁大战陈列馆、赤壁碑廊等，几乎都是当代的人工景点。景区内部交通并不复杂，游客根据指示牌步行游览即可。\n在赤壁山临江悬崖上，刻有“赤壁”二字，这就是景区内的“赤壁摩崖石刻”。传说当年赤壁大战，孙刘联军大胜后把酒庆功，周瑜提剑在崖壁上刻下赤壁二字，字迹竟透过石崖到了山后。虽然是传说，但这两个字，也成为了古战场的标志性景点。\n武财神庙中供奉着关羽关二爷。关羽从古至今被各地商人供奉，关公像除了治病除灾，驱耶辟恶，诛罚叛逆，巡察冥司，还有司命禄，庇护商贾，招财进宝，又因其忠义，故被奉之为武财神。翼江亭在赤壁山临江矶头，建于1936年，相传赤壁大战时此处为周瑜的指挥哨所，站在此亭中，可凭栏远眺隔江乌林，为当时曹军驻扎的大本营。在赤壁矶头，有一处开阔平地上，矗立着三国东吴都督周瑜的石雕像，这座塑像是1991年元月竖立起来的，您整个雕像把周瑜“欲与曹公试比高”的英雄气概表现得淋漓尽致。', '咸宁市赤壁市赤壁镇武侯巷6号', '0715-5788888', 'http://www.chinachibi.com/', '8:30-17:30。最晚入园时间17:30。', 13038, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi10001520-chibiguzhanchang', '3', '4', '3', '5', '3', '2', 3, 34268, 153, 25622, 167631, 81767, 175942, 58892, 36664, 13000, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
