INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (840, '7469055', '淮安府署', NULL, '江苏 > 淮安 > 淮安景点 > 淮安府署', '淮安', '淮安', 'https://img1.qunarzz.com/travel/poi/1408/26/d67700eae3b6c9a2ffffffffc8d65eac.jpg;https://img1.qunarzz.com/travel/poi/1803/36/7a785bff06033e37.jpg;https://img1.qunarzz.com/travel/d9/1704/89/a9dc43806b7bb4b5.jpg;https://img1.qunarzz.com/travel/d3/1704/39/8216e269d22ae0b5.jpg;https://img1.qunarzz.com/travel/d6/1704/31/5a2c0ec58146e4b5.jpg;https://img1.qunarzz.com/travel/d4/1704/83/4938ad4a6d7349b5.jpg', '4.5', '淮安景点排名第5', '1小时 - 2小时', '12', '33.516746', '119.152085', '10', '世界文化遗产、全国重点文物保护单位、国家AAAA级旅游景区 ——淮安府署始建于明朝洪武三年（1370年）一直沿用至清末，至今已有640余年历史。淮安府署在明朝时管辖区域为赣榆、海州（今连云港）、邳州、沭\n阳、宿迁、睢宁、桃源（今泗阳）、清河、安（今涟水）、盐城、山阳（今淮安区）等九县两州，是江苏辖区面积最大的府署。 淮安府署是全国仅存的两座府署之一，占地五万余平米，署内原有房屋50余幢，600余间。淮安府署正堂高10米，面积500余平方，气势宏大，威严壮观，为全国衙署正堂之最。清咸丰十年（1860年）正堂毁于大火，署理淮安府事的陶金冶于当年修复，至今在淮安府署正堂中脊梁上还可见到陶金冶修复正堂时留下的墨宝。\n淮安府中路前段办公建筑，中路后段主要是知府及知府家人生活居住场所。从二堂过宅门便是知府居住的上房院落。这里花木丛生，一派休闲园林格调。正面为知府及夫人居住的上房；东厢房为知府官亲、长随等居住的场所；西厢房为知府两位重要幕僚“刑名师爷”、“钱谷师爷”居住场所。上房后面是青玉堂，青玉堂为二层楼房，是知府小妾及子女居住场所。 东路为迎宾、宴客场所。首先是祭祀、招待来宾娱乐之所——古戏台、酂侯祠；后为宝翰堂，是知府用来和来宾交流诗、书、画的专门场所； 西路为军捕厅署，设有仪门、待质所、熬审房、法鉴堂、二堂、三堂、东西厢房、腰斩台、皮场庙、库房、水龙局等建筑。（二堂、三堂及东西厢房现已为中国历代酷刑刑具展示地）。', '江苏省淮安市淮安区东门大街38号淮安府署', '(0517)85131181', 'http://www.hafs-china.cn/', '8:30-18:00。', 16664, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi7469055-huaianfushu', '4', '2', '1', '', '4', '1', 9, 20516, 174, 74207, 26409, 11272, NULL, 190093, 16973, 5453, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
